#define A 3

mat2 rot(float a)
{
    float s = sin(a),c = cos(a);
    return mat2(c,s,-s,c);
}

vec3 random33(vec3 st)
{
    st = vec3(dot(st,vec3(127.1, 311.7,811.5)),
                dot(st, vec3(269.5, 183.3,211.91)),
                dot(st, vec3(511.3, 631.19,431.81))
                );
    return -1.0 + 2.0 * fract(sin(st) * 43758.5453123);
}

float norm(vec3 p, float n)
{
    vec3 t=pow(abs(p),vec3(n));
    return pow(t.x+t.y,1./n);
}

vec4 celler3D(vec3 i,vec3 sepc)
{
    float stime = iTime / 1.5;
    vec3 sep = i * sepc;
    vec3 fp = floor(sep);
    vec3 sp = fract(sep);
    float dist = 5.;
    vec3 mp = vec3(0.);

    for (int z = -1; z <= 1; z++)
    {
        for (int y = -1; y <= 1; y++)
        {
            for (int x = -1; x <= 1; x++)
            {
                vec3 neighbor = vec3(x, y ,z);
                vec3 pos = vec3(random33(fp+neighbor));
                pos = sin( (pos*50. +stime/(230. + 100.*cos(stime/130.))) )* 0.5 + 0.5;

                float shape = 0.5 + clamp(sin(stime),0.,1.) *30.;
                float rate = clamp( (sin(stime/2.)),0.,1.);
                float divs = norm(neighbor + pos - sp,shape);
                divs = mix(length(neighbor + pos - sp),divs,rate);
                mp = (dist >divs)?pos:mp;
                dist = (dist > divs)?divs:dist;
            }
        }
    }
    return vec4(mp,dist);
}

void scene(out vec3 color,in vec2 F)
{
    vec2 p = (F.xy * 2.0 - iResolution.xy) / min(iResolution.x,iResolution.y);
    //vec3 color = vec3(0.);

    vec3 cp = vec3(0.,0.,-5.);
    vec3 cd = vec3(0.,0.,1.);
    vec3 cu = vec3(0.,1.,0.);
    vec3 cs = normalize(cross(cd,cu));
    cu = normalize(cross(cd,cs));

    float target = 5.0;

    vec3 rd = normalize(vec3(p.x * cs + p.y * cu + cd * target));

    float depth = 0.;

    float stime = iTime/ 150.;

    for(int i = 0 ; i < 3 ; i++)
    {
        vec3 rp = cp + rd * depth;
        vec4 celler = celler3D(rp,vec3(50.)+1.1*sin(stime) );
        float w = celler.w;
        w = max(celler.w,0.001);

        if (celler.w - 0.3 < 0.0001 && depth > 0.0001)
        {
            color += celler.xyz - celler.w;
            break;
        }
        if(depth > 1.){break;}
        depth += w;
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //supersampling | oversampling
	//https://youtu.be/Pcph14WlLsk   

    vec3 color = vec3(0.);

    for(int i = 0;i < A;i++ )
    {
        for(int j = 0; j < A;j++)
        {
            vec2 d = vec2(float(i),float(j)) - vec2(float(i),float(j))/2.;
            d /= float(A);
            scene(color,fragCoord + d);
        }
    }
    color /= float(A * A);

    fragColor = vec4(color ,1.0);
}

#define EPSILON 0.01
#define MIN_DISTANCE 0.0
#define MAX_DISTANCE 2000.0
#define MAX_MARCHING_STEP 256

struct Ray {
    vec3 o; // origin
    vec3 d; // direction, always normalized
};

struct Camera {
	vec3 pos;
    vec3 lookAt;
    float focal;
    vec2 sensorSize;
};

struct Light {
    vec3 pos;
    vec3 color;
};

vec3 xe = vec3(EPSILON, 0., 0.);
vec3 ye = vec3(0., EPSILON, 0.);
vec3 ze = vec3(0., 0., EPSILON);

// get corresponding ray from uv (0.0 -> 1.0)
Ray getRay( Camera cam, vec2 uv ) {
    vec3 camDir = normalize(cam.lookAt - cam.pos); // the camera direction
    vec3 center = cam.pos + cam.focal * camDir; // get the coordinate of the screen center
    vec2 coord = (uv - 0.5) * cam.sensorSize; // convert from 0.0 -> 1.0 to -sensorSize / 2. -> sensorSize / 2.

    // axis relative to camera
    vec3 zpos = -camDir; // k hat, outward the screen
    vec3 xpos = cross(vec3(0., 1., 0.), zpos); // i hat, cross from ypos global and zpos local
    vec3 ypos = cross(zpos, xpos); // j hat, cross from zpos local and xpos local

    // target in screen
    vec3 target = center + coord.x * xpos + coord.y * ypos; // get pixel target coordinate from the screen

    Ray ray;
    ray.o = cam.pos; // the origin of the ray is from the camera duh
    ray.d = normalize(target - cam.pos); // the direction is from the camera to the target

    return ray;
}

// project point to ray
vec3 projPoint2Ray( vec3 point, Ray ray ) {
    vec3 rel = point - ray.o; // get relative vector from the point to the ray origin
    float len = dot(rel, ray.d); // project that vector to vector direction of the ray, we get the length
    return len * ray.d + ray.o; // multiply the length with unit vector direction and add to ray origin
}

// get the distance from point to ray (or line)
float distPoint2Ray( vec3 point, Ray ray ) {
    vec3 proj = projPoint2Ray(point, ray); // project it first to the ray
    return distance(proj, point); // get the distance from the projected position to the original position
}

// just linear interpolation, map value v between fromA and fromB to between toA and toB
float lerp( float v, float fromA, float fromB, float toA, float toB ) {
    return (v - fromA) / (fromB - fromA) * (toB - toA) + toA;
}

// sphere function
float sdSphere( vec3 p, float radius ) {
    return length(p) - radius;
}

// Octahedron function
float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  return (p.x+p.y+p.z-s)*0.57735027;
}

// smooth union function
float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }



// box function
float sdBox( vec3 p, float width, float height, float depth ) {
    vec3 d = abs(p) - vec3(width, height, depth);
    return max(d.x, max(d.y, d.z));
}

// cylinder function
float sdCylinder( vec3 p, float radius, float height ) {
    return max(length(p.xz) - radius, abs(p.y) - height);
}

float sdUnion( float sd1, float sd2 ) {
