vec4 taylorInvSqrt(vec4 r)
{
  return 1.79284291400159 - 0.85373472095314 * r;
}

float snoise(vec3 v, out vec3 gradient)
{
  const vec2  C = vec2(1.0/6.0, 1.0/3.0) ;
  const vec4  D = vec4(0.0, 0.5, 1.0, 2.0);

// First corner
  vec3 i  = floor(v + dot(v, C.yyy) );
  vec3 x0 =   v - i + dot(i, C.xxx) ;

// Other corners
  vec3 g = step(x0.yzx, x0.xyz);
  vec3 l = 1.0 - g;
  vec3 i1 = min( g.xyz, l.zxy );
  vec3 i2 = max( g.xyz, l.zxy );

  //   x0 = x0 - 0.0 + 0.0 * C.xxx;
  //   x1 = x0 - i1  + 1.0 * C.xxx;
  //   x2 = x0 - i2  + 2.0 * C.xxx;
  //   x3 = x0 - 1.0 + 3.0 * C.xxx;
  vec3 x1 = x0 - i1 + C.xxx;
  vec3 x2 = x0 - i2 + C.yyy; // 2.0*C.x = 1/3 = C.y
  vec3 x3 = x0 - D.yyy;      // -1.0+3.0*C.x = -0.5 = -D.y

// Permutations
  i = mod289(i); 
  vec4 p = permute( permute( permute( 
             i.z + vec4(0.0, i1.z, i2.z, 1.0 ))
           + i.y + vec4(0.0, i1.y, i2.y, 1.0 )) 
           + i.x + vec4(0.0, i1.x, i2.x, 1.0 ));

// Gradients: 7x7 points over a square, mapped onto an octahedron.
// The ring size 17*17 = 289 is close to a multiple of 49 (49*6 = 294)
  float n_ = 0.142857142857; // 1.0/7.0
  vec3  ns = n_ * D.wyz - D.xzx;

  vec4 j = p - 49.0 * floor(p * ns.z * ns.z);  //  mod(p,7*7)

  vec4 x_ = floor(j * ns.z);
  vec4 y_ = floor(j - 7.0 * x_ );    // mod(j,N)

  vec4 x = x_ *ns.x + ns.yyyy;
  vec4 y = y_ *ns.x + ns.yyyy;
  vec4 h = 1.0 - abs(x) - abs(y);

  vec4 b0 = vec4( x.xy, y.xy );
  vec4 b1 = vec4( x.zw, y.zw );

  //vec4 s0 = vec4(lessThan(b0,0.0))*2.0 - 1.0;
  //vec4 s1 = vec4(lessThan(b1,0.0))*2.0 - 1.0;
  vec4 s0 = floor(b0)*2.0 + 1.0;
  vec4 s1 = floor(b1)*2.0 + 1.0;
  vec4 sh = -step(h, vec4(0.0));

  vec4 a0 = b0.xzyw + s0.xzyw*sh.xxyy ;
  vec4 a1 = b1.xzyw + s1.xzyw*sh.zzww ;

  vec3 p0 = vec3(a0.xy,h.x);
  vec3 p1 = vec3(a0.zw,h.y);
  vec3 p2 = vec3(a1.xy,h.z);
  vec3 p3 = vec3(a1.zw,h.w);

//Normalise gradients
  vec4 norm = taylorInvSqrt(vec4(dot(p0,p0), dot(p1,p1), dot(p2, p2), dot(p3,p3)));
  p0 *= norm.x;
  p1 *= norm.y;
  p2 *= norm.z;
  p3 *= norm.w;

// Mix final noise value
  vec4 m = max(0.6 - vec4(dot(x0,x0), dot(x1,x1), dot(x2,x2), dot(x3,x3)), 0.0);
  vec4 m2 = m * m;
  vec4 m4 = m2 * m2;
  vec4 pdotx = vec4(dot(p0,x0), dot(p1,x1), dot(p2,x2), dot(p3,x3));

// Determine noise gradient
  vec4 temp = m2 * m * pdotx;
  gradient = -8.0 * (temp.x * x0 + temp.y * x1 + temp.z * x2 + temp.w * x3);
  gradient += m4.x * p0 + m4.y * p1 + m4.z * p2 + m4.w * p3;
  gradient *= 42.0;

  return 42.0 * dot(m4, pdotx);
}
// END OF COPY PASTE SIMPLEX NOISE

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 t = vec2(.5 * iTime, .5);
    // sample moving noise in 4 directions, with slower morph
    vec3 grad0, grad1, grad2, grad3;
    snoise(vec3(2.*fragCoord.xy/iResolution.y + t.xy, .125*t.x), grad0);
    snoise(vec3(2.*fragCoord.yx/iResolution.y - t.xy, .125*t.x), grad1);
    snoise(vec3(2.*fragCoord.xy/iResolution.y + t.yx, .125*t.x), grad2);
    snoise(vec3(2.*fragCoord.yx/iResolution.y - t.yx, .125*t.x), grad3);

    // take xy-gradient as surface partial derivative, add together
    vec2 grad = grad0.xy + grad1.xy + grad2.xy + grad3.xy;

    // light dir
    vec3 L = normalize(vec3(-1,1,3));
    // camera dir
    vec3 C = vec3(0,0,1);
    // normal
    vec3 N = normalize(vec3(grad.xy, 4.));

    float lit = pow(max(0.,dot(normalize(L+C), N)), 20.);
    float frac = 1.-max(0., dot(N, C));
    frac *= frac;

    vec3 b = texture(iChannel0, uv + (4./iResolution.xy)*N.xy).xyz;

    b = (1.-frac) * b + frac*(10.*lit);

    // Output to screen
    fragColor = vec4(vec3(b),1.0);
}

#define S(a, b, t) smoothstep(a,b,t)
#define LAYER_COUNT 10.
#define MOON_SIZE .15
#define TREE_COL vec3(.8, .8, 1.)
#define ORBIT_SPEED .025
#define SCROLL_SPEED .3
#define ROT -0.785398
#define ZOOM .4
#define STAR_SPEED .25

float N11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

float N21(vec2 p)
{
	vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

float DistLine(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p-a;
    vec2 ba = b-a;
    float t = clamp(dot(pa, ba)/ dot(ba, ba), 0., 1.);
    return length(pa - ba*t);
}


float DrawLine(vec2 p, vec2 a, vec2 b) {
    float d = DistLine(p, a, b);
    float m = S(.00125, .000001, d);
    float d2 = length(a-b);
    m *= S(1., .5, d2) + S(.04, .03, abs(d2-.75));
    return m;
}

float ShootingStar(vec2 uv) {    
    vec2 gv = fract(uv)-.5;
    vec2 id = floor(uv);

    float h = N21(id);

    float line = DrawLine(gv, vec2(0., h), vec2(.1, h));
    float trail = S(.12, .0, gv.x);

    return line * trail;
}

float LayerShootingStars(vec2 uv) {
    float t = iTime * STAR_SPEED;
    vec2 rv1 = vec2(uv.x - t, uv.y + t);
    rv1.x *= 1.05;

    float r = 3. * ROT;
    float s = sin(r);
    float c = cos(r);
    mat2 rot = mat2(c, -s, s, c);
    rv1 *= rot;
    rv1 *= ZOOM * .9;

    vec2 rv2 = uv + t * 1.2;
    rv2.x *= 1.05;

    r = ROT;
    s = sin(r);
    c = cos(r);
    rot = mat2(c, -s, s, c);
    rv2 *= rot;
    rv2 *= ZOOM * 1.1;

    float star1 = ShootingStar(rv1);
    float star2 = ShootingStar(rv2);
    return clamp(0., 1., star1 + star2);
}

float GetHeight(float x) {
    return sin(x*.412213)+sin(x)*.512347;
}

float TaperBox(vec2 p, float wb, float wt, float yb, float yt, float blur) {
    //bottom edge
    float m = S(-blur, blur, p.y - yb);
    //top edge
    m *= S(blur, -blur, p.y - yt);
    //mirror x to get both edges
    p.x = abs(p.x);
    //side edges
    // 0 p.y = yb 1 p.y = yt
    float w = mix(wb, wt, (p.y - yb) / (yt - yb));
    m *= S(blur, -blur, p.x - w);
    return m;
}

vec4 Tree(vec2 uv, vec3 col, float blur) {
    float m = TaperBox(uv, .03, .03, -.05, .25, blur); //trunk
    m += TaperBox(uv, .2, .1, .25, .5, blur); //canopy 1
    m += TaperBox(uv, .15, .05, .5, .75, blur); //canopy 2
    m += TaperBox(uv, .1, .0, .75, 1., blur); //top

    blur *= 3.;
    float shadow = TaperBox(uv-vec2(.2,0.), .1, .5, .15, .253, blur);
    shadow += TaperBox(uv+vec2(.25,0.), .1, .5, .45, .503, blur);
    shadow += TaperBox(uv-vec2(.25,0.), .1, .5, .7, .753, blur);
    col -= shadow*.8;

    return vec4(col, m);
}

vec4 Layer(vec2 uv, float blur) {
    vec4 col = vec4(0);
    float id = floor(uv.x);

    //random [-1, 1]
    float n = N11(id)*2.-1.;
    float x = n*.3;
    float y = GetHeight(uv.x);

    //ground
    float ground = S(blur, -blur, uv.y-y);
    col += ground;

    y = GetHeight(id + .5 - x);

    //vertical grid
    uv.x = fract(uv.x)-.5;
    //					 offset		scale tree size		color
    vec4 tree = Tree((uv+vec2(x,-y))*vec2(1, 1.+n*.2), vec3(1), blur);

    col = mix(col, tree, tree.a);
    col.a = max(ground, tree.a);
    return col;
}

vec2 MoonPos() {
    float t = iTime * ORBIT_SPEED;
    return vec2(sin(t), cos(t))/2.;
}

vec4 MoonGlow(vec2 uv) {
    vec2 moonPos = MoonPos();
    float md = length(uv-(moonPos - vec2(.07, 0.01)));
    float moon = S(.1, -.01, md-MOON_SIZE*8.);
    moon = mix(0., moon, clamp((moonPos.y + .2)*3., 0., 1.));

    vec4 col = vec4(moon);
    md = clamp(1.-md, 0., 1.);
    md *= md;
    col.rgb *= md;
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy)/iResolution.y;
    vec2 M = (iMouse.xy/iResolution.xy)*2.-1.;
    float t = iTime * SCROLL_SPEED;
    float blur = .005;

    float twinkle = dot(length(sin(uv+t)), length(cos(uv*vec2(22., 6.7)-t*3.)));
    twinkle = sin(twinkle*5.)*.5+.5;
    float stars = pow(N21(uv*1000.), 1024.)*twinkle;
    vec4 col = vec4(stars);

    vec2 moonPos = MoonPos();
    float moon = S(.01, -.01, length(uv-moonPos)-MOON_SIZE);
    col *= 1.-moon;
    moon *= S(-.01, .05, length(uv-(moonPos+vec2(.1,.05)))-MOON_SIZE);
    col += moon;

    col += LayerShootingStars(uv);

    vec4 layer;
    for (float i=0.; i <1.; i+=1./LAYER_COUNT) {
        float scale = mix(30., 1., i);
        blur = mix(.05, .005, i);
        layer = Layer(uv*scale+vec2(t+i*100.,i)-M, blur);
        layer.rgb *= (1.-i) * TREE_COL;
        col = mix(col, layer, layer.a);
    }
    col += MoonGlow(uv);

    layer = Layer(uv+vec2(t,1.)-M, .07);
    col = mix(col, layer*.05, layer.a);

    fragColor = col;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec3 opRep( in vec3 p, in vec3 c, in vec3 l)
{
    return p-c*clamp(round(p/c),-l,l);
}

float map(vec3 p)
{
    p.z += 5.0;
    p = opRep(p, vec3(4.0), vec3(1.0));
    return sdBox(p, vec3(1.0));
}

vec3 calcNormal( in vec3 p )
{
    const float h = 1e-5; // or some other value
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*map( p + k.xyy*h ) + 
                      k.yyx*map( p + k.yyx*h ) + 
                      k.yxy*map( p + k.yxy*h ) + 
                      k.xxx*map( p + k.xxx*h ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    // use different camera setting for CineShader
    #ifdef IS_CINESHADER

    // use the relative position of the camera to the center of the screen as ray origin
	vec3 rayOri = iCamPos;

    // screen size is 6m x 6m, or you can use iScreenSize.xy(CineShader only) to get the screen size
	vec3 rayDir = normalize(vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 6.0, 0.0) - iCamPos);

    // make the maxDepth further
    float maxDepth = 30.0;
    #else
	vec3 rayOri = vec3((uv - 0.5) * vec2(iResolution.x/iResolution.y, 1.0) * 6.0, 3.0);
	vec3 rayDir = vec3(0.0, 0.0, -1.0);
    float maxDepth = 6.0;
    #endif

	float depth = 0.0;
	vec3 p;

	for(int i = 0; i < 64; i++) {
		p = rayOri + rayDir * depth;
		float dist = map(p);
        depth += dist;
		if (dist < 1e-6) {
			break;
		}
	}

    depth = min(maxDepth, depth);
	vec3 n = calcNormal(p);
    float b = max(0.0, dot(n, vec3(0.577)));
    vec3 col = (0.5 + 0.5 * cos((b + iTime * 3.0) + uv.xyx * 2.0 + vec3(0,2,4))) * (0.85 + b * 0.35);
    col *= exp( -depth / maxDepth );

    #ifdef IS_CINESHADER
    // set the screen thickness to zero in CineShader
    fragColor = vec4(col, 0.0);
    #else
    // maximum thickness is 2m in alpha channel
    fragColor = vec4(col, 1.0 - (depth - 0.5) / 2.0);
    #endif
}

