	f = f*f*(3.0-2.0*f);

#if 1
	vec2 uv = (p.xy+vec2(37.0,239.0)*p.z) + f.xy;
    vec2 rg = textureLod(iChannel2,(uv+0.5)/256.0,0.0).yx;
#else
