float noiseB (vec3 p) {
	vec3 f = fract (p);
	p = floor (p);
	f = f * f * (3.0 - 2.0 * f);
	f.xy += p.xy + p.z * vec2 (37.0, 17.0);
	f.xy = texture (iChannel2, (f.xy + 0.5) / 256.0, -256.0).yx;
	return mix (f.x, f.y, f.z);
}

float opSmoothUnion( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdRoundCone( vec3 p, float r1, float r2, float h )
{
  vec2 q = vec2( length(p.xz), p.y );

  float b = (r1-r2)/h;
  float a = sqrt(1.0-b*b);
  float k = dot(q,vec2(-b,a));

  if( k < 0.0 ) return length(q) - r1;
  if( k > a*h ) return length(q-vec2(0.0,h)) - r2;

  return dot(q, vec2(a,b) ) - r1;
}
float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

vec3 tex3D( sampler2D tex, in vec3 p, in vec3 n ){

    n = max((abs(n) - .2)*7., .001);
    n /= (n.x + n.y + n.z );  

	p = (texture(tex, p.yz)*n.x + texture(tex, p.zx)*n.y + texture(tex, p.xy)*n.z).xyz;

    return p*p;
}

vec3 guide (vec3 p){
	return vec3(
    	sin(p.z*0.5) + sin(p.z*0.3) + cos(p.z*0.3),
    	cos(p.z*0.8) + sin(p.z*1.3)*0.5,
        0.
    )*1.2;
}
#define W 1.
vec2 map(vec3 p){
	vec2 d = vec2(10e4);
    /*
    for (int i = 0; i < 3; i++){
    	p = abs(p);
        p.y -= 0.25;
        p.xy *= rot(0.125*3.14);
    }*/
    p.xy -= 4.;
    vec2 id = floor(p.xy/8.);

    p.xy = pmod(p.xy, 8.);
    //p.xy *= 1. - pow(fract(sin(iTime + id.x + p.z)), 1.)*0.1;
    p.xy += normalize(p.xy)*0.1*(sin(iTime + id.x + p.z));
    p += guide(p + id.x)*0.4;
    vec3 t = tex3D(iChannel0,p*0.1, normalize(vec3(p.x,p.y, 0.)));
    vec3 tB = tex3D(iChannel0,p*0.2+ 1., normalize(vec3(p.x,p.y, 0.)));
    //t.x = 1. - t.x; 
    //t.x *= 0.4;
    //t.x = pow(smoothstep(0.,1.5,t.x*3.), 1.)*0.4;

    t.x *= 0.6;
    float dPlant = length(p.xy) - W - t.x - tB.x*0.4;


    //dPlant = max(dPlant, sin(p.z)*0.4 + 0.2);

    p.z = pmod(p.z, 4.);
    for (float i = 0.; i < 3.; i++){
        vec3 q = p;
        q.z += 4.*i/3.;
        pmod(q.z, 4.);
        q.xy *= rot(0.7 + i);
        q.y -= W;
        dPlant = opSmoothUnion( dPlant, sdTorus( q, vec2(0.25,0.12) ), 0.2 );
        //q.z -= exp(-clamp(length(q.xy)*20.,0., 20.)*1.) ;
        q.z -= clamp(pow(length(q.xy)*0.8,3.),0., 1.) ;
        d = dmin(d, vec2( (sdRoundCone( q, 0.2, 0.007, 1. )- tB.x*0.15)*0.6, 1.) );
    }
    d= dmin(d, vec2(dPlant*0.6, 2.));

    d.x *= 0.5;
    return d;
}



vec2 march(vec3 ro, vec3 rd,inout vec3 p ,inout float t, inout bool hit){

	hit = false;
    t = 0.;
    vec2 d;
    p = ro + rd*1.;
    for(int i = 0; i < 150; i++){
    	d = map(p);
        glow += exp(-d.x*5.);
        if(d.x < 0.001){
        	hit = true;
        	break;
        }
        if(t > 30.){
        	break;
        }
        t += d.x;
    	p = ro + rd*t;
    }
	return d;
}

vec3 getNormal(vec3 p){
	vec2 t = vec2(0.001, 0.);
	return normalize(map(p).x - vec3(
    	map(p - t.xyy).x,
    	map(p - t.yxy).x,
    	map(p - t.yyx).x
    ));
}

vec3 getRd(vec3 ro, vec3 lookAt, vec2 uv){
	vec3 dir = normalize(lookAt - ro);
    vec3 right = normalize(cross(vec3(0,1,0), dir));
    vec3 up = normalize(cross( dir, right));
	return normalize(dir + right*uv.x + up*uv.y);
}

