    float y = 0.0;

#ifdef MOTION_BLUR
    for (float i = 0.0; i < 8.0; i++)
#endif
#ifdef ANTI_ALIASING
    for (float y = -1.0; y <= 1.0; y++)
    for (float x = -1.0; x <= 1.0; x++)
#endif
    {
        // Normalized pixel coordinates (from 0 to 1)
        vec2 uv = ((fragCoord + vec2(x, y) / 3.0)/ iResolution.xy - vec2(0.5)) * 3.0;
        uv.x /= iResolution.y / iResolution.x;

        float t = (iTime - iTimeDelta / 8.0 * i) * 0.3;
        float s = sin(t);
        float c = cos(t);

        uv.xy = mat2(c, -s, s, c) * uv.xy;
        float r = sqrt(dot(uv.xy, uv.xy));
        float phi = atan(uv.y, uv.x);

        r = pow(r, (sin(t * 5.0) + 2.0));
        uv.x = r * cos(phi);
        uv.y = r * sin(phi);

        bool flag = false;
        float mx = mod(uv.x, 1.0);
        float my = mod(uv.y, 1.0);

        if (mx > 0.47 && mx < 0.53)
          flag = true;
        if (my > 0.47 && my < 0.53)
          flag = true;
        float col = flag ? 1.0 : 0.0;
        sum += col;

    }
#ifdef ANTI_ALIASING
    sum /= 9.0;
#endif
#ifdef MOTION_BLUR
    sum /= 8.0;
#endif
    // Output to screen
    fragColor = vec4(vec3(sum), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
