// from https://www.shadertoy.com/view/4djSRW
vec2 hash21(float p)
{
    vec3 p3 = fract(vec3(p) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

// for each 8 rows, randomly select one of the two greyscales
// the values for odd rows are equal and guaranteed to not be
// the same as whichever colour is chosen for the even row,
// therefore there is never a repeated colour
const float greyscales[16] = float[16]
    (1./3., 2./3.,
     0./3., 0./3.,
     2./3., 3./3.,
     1./3., 1./3.,
     3./3., 0./3.,
     2./3., 2./3.,
     0./3., 1./3.,
     3./3., 3./3.);

const float scale = 4.0;
const float red_chance = 0.3;

vec4 get_row_colour(float row)
{
    // assign a random value to the row
    vec2 rand = hash21(row);
    // select a greyscale for the row
    row = mod(row, 8.0);
    float grey = greyscales[int((row * 2.0) + step(rand.x, 0.5))];
    // randomly replace odd rows with red
    float is_red = mod(row, 2.0) * step(rand.y, red_chance);
    // return row colour
    return vec4(max(is_red, grey), vec2(min(1.0 - is_red, grey)), 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // normalise coordinates
    vec2 R = iResolution.xy, U = ((scale * fragCoord.xy) - R) / min(R.x, R.y) - 0.5;
    // get the pixel width
    float pix = scale / min(R.x, R.y);

    // scroll vertically
    U.y += iTime;

    // get local coordinates [-0.5, 0.5] and select the top left quadrent
    vec2 UF = abs(fract(U) - 0.5);

    // add 1.0 if outside the circle
    float c = smoothstep(-pix, pix, length(UF) - 0.5);
    // reflect the quadrent along the diagonal from top left to bottom right
    UF = 0.5 - UF;
    // add 1.0 if inside the circle
    c += 1.0 - smoothstep(-pix, pix, length(UF) - 0.5);
    // negate c in the bottom half
    c *= sign(fract(U.y) - 0.5);
    // offset c by 4 for each neighbouring circle
    c += floor(U.y) * 4.0;
    // c now indicates which row of 'stars' or 'lenses' we are in

    fragColor = mix(get_row_colour(floor(c)), get_row_colour(ceil(c)), fract(c));
}

#define PI 3.14159265357989


vec2 rotate(vec2 pos, float angle)
{
    float s = sin(angle), c = cos(angle);
    return mat2(c, -s, s, c) * pos;
}

float hash(vec2 n)
{
    return fract(sin(dot(n, vec2(123.0, 458.0))) * 43758.5453);
}

float cubicInOut(float time) {
  return (time < 0.5) ? (4.0 * time * time * time) : (1.0 - 4.0 * pow(1.0 - time, 3.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / min(iResolution.x, iResolution.y);
    uv += vec2(1,-1) * iTime * 0.05;

    float uvScale = 2.5;
    vec2 uvID = floor(uv * uvScale);
    vec2 uvID2 = floor(uv * uvScale + vec2(0.5));
    vec2 uvLocal = fract(uv * uvScale);

    float time = iTime * 1.0;
    float timef = cubicInOut(fract(time));
    float timei = floor(time);
    time = (timef + timei) * 0.5;

    // animation 1
    float rotDir = (0.0 == mod(uvID.y, 2.0) ? 1.0 : -1.0);
    vec2 rotCenter = vec2(0.5, 0.5);
    vec2 uvAnim1 = uvLocal;
    uvAnim1 = rotate(uvAnim1 - rotCenter, time * PI * rotDir);
    uvAnim1 += rotCenter;

    // animation 2
    vec2 uvAnim2 = uvLocal;
    float uvAnim2Corner = floor(uvLocal.x);
    uvAnim2 += vec2((0.5 < uvAnim2.x ? -0.5 : 0.5), (0.5 < uvAnim2.y ? -0.5 : 0.5));
    uvAnim2 = rotate(uvAnim2 - rotCenter, time * PI);
    uvAnim2 += rotCenter;

    // animation
    uvLocal = (fract(time) < 0.5) ? uvAnim1 : uvAnim2;

    // distance
    float neighborDist = 1e+4;
    for(float x=-1.0; x<=1.0; x+=2.0)
        for(float y=-1.0; y<=1.0; y+=2.0)
            neighborDist = min(neighborDist, distance(uvLocal, vec2(x,y) * 0.5 + vec2(0.5)));

    float dist = 1e+4;
    dist = distance(uvLocal, vec2(0.5));
    dist = max(dist, neighborDist);

    // color
    float smoothness = 0.05;
    float thickness = 0.03;
    float center = 0.45;
    float density = smoothstep(center - thickness, center + thickness, dist);
    density = smoothstep(1.0, 1.0 - smoothness, density) * (smoothstep(0.0, 0.0 + smoothness, density));

    float colorID = (fract(time) < 0.5) ? hash(uvID) : hash(uvID2);
    float colorVariation = 0.3;
    float colorOffset = -0.7;
    vec3 color = vec3(0,1,2) * PI * 0.5 + colorID * colorVariation + colorOffset;
    color = max(sin(color), cos(color)) + 0.4;

    float colorBgWave = (sin((uv.x - uv.y) * 2.0 + iTime * 3.0) + sin((uv.x - uv.y * 0.5) * 1.5 + iTime)) * 0.5;
    vec3 colorBg = vec3(1.0, 0.8, 0.5) * mix(0.65, 1.0, colorBgWave);
    fragColor.rgb = vec3(density) * color * (colorBgWave * 0.75 + 0.25 + (1.0 - colorBgWave) * vec3(0.1, 0.0, 0.0));
    fragColor.rgb = mix(colorBg, fragColor.rgb, density);
    fragColor.rgb = clamp(fragColor.rgb * (1.2 + sin(iTime) * 0.1), 0.0, 1.0);
}

const float r = 0.5;
const float pi = 3.141592;

vec2 cycloid(float t) {
	return vec2( r*(t - sin(t)), r*(1.0 - cos(t)) );    
}

float disttoline(vec2 a, vec2 b, vec2 p) {
 	return abs( (b.y-a.y)*p.x - (b.x-a.x)*p.y + b.x*a.y-a.x*b.y ) / sqrt( dot(b-a, b-a) ) 
        * (length(a-p) + length(b-p) - length(a-b) < 0.001 ? 1.0 : 3000.0);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2.0*pi*r * (2.0 * fragCoord - iResolution.xy)/iResolution.x + vec2(pi*r, 0.7);

    float dist = 10.0;
    float x = iTime * 3.0;
    float tpos = 4.0*pi*r*sin(x*0.25) + pi;// = mod( (x - 2.0*mod(x, 4.0*pi) * step(4.0*pi, mod(x, 8.0*pi))), 4.0*pi ) - pi;
    for(float t = -pi; t < tpos; t += 0.01) {
    	vec2 point = cycloid(t);
        dist = min(dist, length(point - uv));
    }

    vec2 circle = vec2(r*tpos, r);
    vec2 finalpoint = cycloid(tpos);
    float linedist = disttoline( circle, finalpoint, uv );
    vec3 color = mix(vec3(0.0), mix( vec3(1.0), vec3(0.95, 0.96, 0.73), smoothstep(0.0, -0.01, length(uv-circle)-r) ), smoothstep(0.0, 0.01, abs(length(uv-circle)-r)));
    color = mix(color, vec3(1.0, 0.0, 0.0), smoothstep(0.01, 0.0, dist));
    color = mix(color, vec3(0.0), step(length(uv - finalpoint), 0.02));
    color = mix( color, mix( vec3(0.0), vec3(.8, .5, .51) + pow(0.5, 1.2+7.0*abs(circle.x-uv.x))*pow(0.5, 2.0*(-uv.y)), smoothstep(0.0, 0.01, abs(uv.y + 0.005)) ), step(uv.y, 0.00) );
    color = mix(color, vec3(0.0), smoothstep(0.01, 0.0, linedist));


    fragColor = vec4(color, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float STEP_SIZE = 20.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    float dist = length(uv);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    // Cut into a circle
    col *= step(dist, 0.5);

    // Cut out horizontal lines
    float invY = 1.0 - uv.y;
    col *= step(mod(invY * invY * STEP_SIZE, 2.0), 0.9);

    // Output to screen
    fragColor = vec4(col, 1.0);
}

