#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST 0.01
#define PI 3.1415

mat2 GetRot2d(float alpha)
{
    float c = cos(alpha);
    float s = sin(alpha);

    return mat2(c, -s, s, c);
}

float GetCylinder(vec3 p, vec3 top, vec3 bottom, float radius)
{
    vec3 ab = bottom - top;
    vec3 ap = p - top;

    float t = dot(ab, ap) / dot(ab, ab);

    vec3 c = top + t * ab;

    float x = length(p - c) - radius;
    float y = (abs(t - .5) - .5) * length(ab);
    float exterior = length(max(vec2(x, y), 0.));
    float interior = min(max(x, y), 0.);

    return exterior + interior;
}

float GetMoon(vec3 p)
{
    float cyl1 = GetCylinder(p, vec3(0,0,0), vec3(0,.3,0), 2.);
    float cyl2 = GetCylinder(p, vec3(0,-1,0.4), vec3(0,2,0.4), 1.7);

    return max(cyl1, -cyl2);
}

float GetMainShape(vec3 p)
{
    vec3 p1 = p;
    p1.z -= 0.5;
    float m1 = GetMoon(p1);

    vec3 p2 = p;
    p2.xz *= GetRot2d(2. * PI / 3.);
    p2.xz -= vec2(1.4, 2.76);
    float m2 = GetMoon(p2);

    vec3 p3 = p;
    p3.xz *= GetRot2d(-2. * PI / 3.);
    p3.xz -= vec2(-1.4, 2.76);
    float m3 = GetMoon(p3);

    return min(m3, min(m1, m2));
}

float GetDecor(vec3 p)
{
    float outer = GetCylinder(p, vec3(0,0,0), vec3(0,.3,0), 4.2);
    float inner = GetCylinder(p, vec3(0,-1.,0), vec3(0,1,0), 4.05);
    float circle = max(outer, -inner);

    float middleTip = GetCylinder(p, vec3(0,0,0), vec3(0,.3,0), 0.5);

    return min(circle, middleTip);
}

float GetDist(vec3 p)
{
    p.yx *= GetRot2d(iTime);

    vec3 p1 = p;
    p1.z -= -1.5;
    float middleHole = GetCylinder(p1, vec3(0,-1,0), vec3(0,1,0), 1.0);
    float decor = GetDecor(p1);
	float result = max(-middleHole, GetMainShape(p));

    return min(decor, result);
}

vec3 GetNormal(vec3 p)
{   
    float d = GetDist(p);
    vec2 e = vec2(0.01, .0);

    vec3 n = d - vec3(
    	GetDist(p - e.xyy),
    	GetDist(p - e.yxy),
    	GetDist(p - e.yyx));

    return normalize(n);
}

float RayMarch(vec3 ro, vec3 rd, float surfDist)
{
    float d0 = 0.;
        for (int i = 0; i < MAX_STEPS; i++)
        {
            vec3 p = ro + rd * d0;
            float ds = abs(GetDist(p));
            d0 += ds;
            if (abs(d0) > MAX_DIST || ds < surfDist)
            {
                break;
            }
        }

    return min(d0, MAX_DIST);
}

float GetGlow(vec3 ro, vec3 rd, vec3 hitPoint)
{   
    float glowValue = 0.;
    float initialDist = length(ro - hitPoint);

    if (initialDist < 90.)
    {
        return 0.0;
    }

    for (int i = 0; i < 10; i++)
    {
        glowValue += 1. - (RayMarch(ro, rd, 1.2 * float(i) * SURF_DIST) - initialDist);
    }

    return glowValue * 0.002;
}

float GetLight(vec3 p)
{
    vec3 lightPos = vec3(-13., 15, 0);

    vec3 l = normalize(lightPos - p);
    vec3 n = GetNormal(p);

    float diff = clamp(dot(n, l)*.5+.5, 0., 1.);

    return diff;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy) / iResolution.y;

    vec3 ro = vec3(0, 10, -1.5);
    vec3 rd = normalize (vec3(uv.x, uv.y, 1));
    rd.yz *= GetRot2d(PI / 2.);

    float d = RayMarch(ro, rd, SURF_DIST);
    vec3 p = ro + rd * d;
    float dif = GetLight(p);
    vec3 col = vec3(.5, 1, .5);
    vec3 glow = GetGlow(ro, rd, p)* vec3(.4, 1, .65);

    fragColor = vec4(vec3(dif) * col + glow, 1.0);
}

