	// subtract  : max( d1, -d0 )
	//return max( d1, -d0 );
}

// phong shading
vec3 shading( vec3 v, vec3 n, vec3 eye ) {
	// ...add lights here...

	float shininess = 16.0;

	vec3 final = vec3( 0.0 );

	vec3 ev = normalize( v - eye );
	vec3 ref_ev = reflect( ev, n );

	// light 0
	{
		vec3 light_pos   = vec3( 20.0, 20.0, 20.0 );
		vec3 light_color = vec3( 1.0, 0.7, 0.7 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color * ( diffuse + specular ); 
	}

	// light 1
	{
		vec3 light_pos   = vec3( -20.0, -20.0, -20.0 );
		vec3 light_color = vec3( 0.3, 0.7, 1.0 );

		vec3 vl = normalize( light_pos - v );

		float diffuse  = max( 0.0, dot( vl, n ) );
		float specular = max( 0.0, dot( vl, ref_ev ) );
		specular = pow( specular, shininess );

		final += light_color * ( diffuse + specular ); 
	}

	return final;
}

// get gradient in the world
vec3 gradient( vec3 pos ) {
	const vec3 dx = vec3( grad_step, 0.0, 0.0 );
	const vec3 dy = vec3( 0.0, grad_step, 0.0 );
	const vec3 dz = vec3( 0.0, 0.0, grad_step );
	return normalize (
		vec3(
			dist_field( pos + dx ) - dist_field( pos - dx ),
			dist_field( pos + dy ) - dist_field( pos - dy ),
			dist_field( pos + dz ) - dist_field( pos - dz )			
		)
	);
}

// ray marching
float ray_marching( vec3 origin, vec3 dir, float start, float end ) {
	float depth = start;
	for ( int i = 0; i < max_iterations; i++ ) {
		float dist = dist_field( origin + dir * depth );
		if ( dist < stop_threshold ) {
			return depth;
		}
		depth += dist;
		if ( depth >= end) {
			return end;
		}
	}
	return end;
}

// get ray direction
vec3 ray_dir( float fov, vec2 size, vec2 pos ) {
	vec2 xy = pos - size * 0.5;

	float cot_half_fov = tan( ( 90.0 - fov * 0.5 ) * DEG_TO_RAD );	
	float z = size.y * 0.5 * cot_half_fov;

	return normalize( vec3( xy, -z ) );
}

// camera rotation : pitch, yaw
mat3 rotationXY( vec2 angle ) {
	vec2 c = cos( angle );
	vec2 s = sin( angle );

	return mat3(
		c.y      ,  0.0, -s.y,
		s.y * s.x,  c.x,  c.y * s.x,
		s.y * c.x, -s.x,  c.y * c.x
	);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// default ray dir
	vec3 dir = ray_dir( 45.0, iResolution.xy, fragCoord.xy );

	// default ray origin
	vec3 eye = vec3( 0.0, 0.0, 5.0 );

	// rotate camera
	mat3 rot = rotationXY( vec2( iTime ) );
	dir = rot * dir;
	eye = rot * eye;

	// ray marching
	float depth = ray_marching( eye, dir, 0.0, clip_far );
	if ( depth >= clip_far ) {
		fragColor = vec4( 0.0, 0.0, 0.0, 1.0 );
        return;
	}

	// shading
	vec3 pos = eye + dir * depth;
	vec3 n = gradient( pos );
	fragColor = vec4( shading( pos, n, eye ), 1.0 );
}

float n11(float p) {
	return fract(sin(p*154.101)*313.019);
} 
float n21(vec2 p) {
    float n = sin(dot(p, vec2(7, 157)));    
    return n;
}
vec3 hash33(vec3 p){ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768)*n); 
}

float voronoi(vec3 p){
	vec3 b, r, g = floor(p);
	p = fract(p);
	float d = 1.; 
	for(int j = -1; j <= 1; j++) {
	    for(int i = -1; i <= 1; i++) {
		    b = vec3(i, j, -1);
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 0.0;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
		    b.z = 1.;
		    r = b - p + hash33(g+b);
		    d = min(d, dot(r,r));
	    }
	}

	return d; // Range: [0, 1]
}

vec3 hsl2rgb( in vec3 c ){
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0,1.0);
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

float nebula(in vec3 p) {
    float amp = 1., sum=0., z= 1., m = 0.;
    for(int i=1; i<=10;i++){
    	vec3 t = vec3(0., 0., p.z+iTime*.1);
        z *= 2.;
        m += voronoi(vec3(p.xy*z, 1.)+t) *amp;
        sum += amp;
        amp *= .5;
    }
    m /= sum;
    return pow(m, 1.5);
}

float star(vec3 p) {
    float z = 16.;
    float t = p.z;
    vec2 gv = fract(p.xy*z)-.5;
    vec2 id = floor(p.xy*z);
    gv.x += sin(n21(id)*354.23)*.3;
    gv.y += sin(n11(n21(id))*914.19)*.3;
    float r = n11(n21(id));
    float m =  .1*n11(r)*abs(sin(p.z+r*133.12))*.4/length(gv)*.1;
    return m;
}

float stars(in vec3 p) {
    float z= 1., m = 0.;
    for(int i=1; i<=10;i++){
    	vec3 t = vec3(0., 0., p.z+iTime*.2);
        z *= 2.;
         m += star(vec3(p.xy*z, 1.)+t);
    }
    return m;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(.0);

    vec3 col1 = normalize(vec3(159., 38., 106.))*nebula(vec3(uv,1.));
    vec3 col2 = normalize(vec3(106., 110., 185.))*nebula(vec3(uv,1298.));
    col += mix(col1,col2,5.);
    float m = stars(vec3(uv, iTime*2.));
    col += vec3(m);

    // Output to screen
    fragColor = vec4(col,1.0);
}

