mat3 rMatX(float x)
{
     float r = deg2rad(x);
     float c = cos(r), s = sin(r);
     return mat3(1,0,0,0,c,-s,0,s,c);
}

mat3 rMatY(float y)
{
     float r = deg2rad(y);
     float c = cos(r), s = sin(r);
     return mat3(c,0,s,0,1,0,-s,0,c);
}

mat3 rMatZ(float z)
{
     float r = deg2rad(z);
     float c = cos(r), s = sin(r);
     return mat3(c,-s,0,s,c,0,0,0,1);
}

