float arm(vec3 pos, float side, vec3 elbow, vec3 ud, vec3 x)
{
    float d = sdCapsule(s.pos, pos-side*x*0.15, elbow, 0.45);
    return opUnion(d, sdCapsule(s.pos, elbow-0.2*ud, elbow-1.0*ud, 0.45), 0.2);
}

