}

// 1. Create grid cell 3x3
// 2. Create a random pixel inside each grid
// 3. Connect the pixels with lines
// 4. Add effects

// Procedural programming


#define S(a, b, t) smoothstep (a, b, t)


float DistLine(vec2 p, vec2 startPoint, vec2 endPoint) {

    vec2 pointA = p - startPoint;
    vec2 pointB = endPoint - startPoint;

    float t = clamp(dot(pointA, pointB) / dot (pointB, pointB), 0., 1.);
    return length(pointA - pointB*t);
}


float N21(vec2 p) {
    // Random float between 0 and 1
    p = fract(p*vec2(233.42, 472.21));

    // make more random 
    p += dot(p, p+59.31);
    return fract(p.x*p.y);

}


vec2 N22(vec2 p) {

    // Random vec2
    // for Random X and random Y

   float n = N21(p);
   return vec2(n, N21(p+n));
}

vec2 GetPos(vec2 id, vec2 offset) {

    vec2 noise = N22(id + offset)*iTime;

    return offset + sin(noise) *.4;
}

float Line(vec2 p, vec2 a, vec2 b) {

    float d = DistLine(p, a, b);
	float m = S(.03, .01, d);
    float dist = length(a-b);

    m *= S(1.2, .08, dist)*3. + S(.05, .01, abs(dist-.5));
    return m;
}

float GridDotConnectLayer(vec2 uv) {

    vec2 gridUv = fract(uv)-.5;
    vec2 id = floor(uv);

    vec2 p[9];

    float m;

    int i = 0;
    for (float y=-1.; y <= 1.; y++) {
    	for (float x=-1.; x <= 1.; x++) {
    		 p[i++] = GetPos(id, vec2(x, y));
    	}
    }

    // Draw lines
    float t = iTime*10.;

    for(int i=0; i < 9; i++) {
        m += Line(gridUv, p[4], p[i]);

        // Current point
        vec2 j = (p[i]-gridUv)*25.;
        float sparkle =  1. / dot(j, j);
        m += sparkle; //* (sin(t+p[i].*x10.)*.5+.5);
    }
    // add additional lines to hidden lines
    m += Line(gridUv, p[1], p[3]);
    m += Line(gridUv, p[1], p[5]);
    m += Line(gridUv, p[5], p[7]);
    m += Line(gridUv, p[7], p[3]);

    return m;
}

