    float moon3 = S(.01, -.01, length(uv-vec2(.2*sin(iTime)*2./3.+.2,.3*cos(iTime)/3.+.3))-.025);


    col *= 1.-moon;
    col *= 1.-moon2;
    col *= 1.-moon3;

    moon *= S(-.01, .1, length(uv-vec2(.22,.33))-.02);
    moon2 *= S(-.01, .1, length(uv-vec2(-.63,.24))-.12);
    moon3 *= S(-.01, .1, length(uv-vec2(.22*sin(iTime)*2./3.+.22,.33*cos(iTime)/3.+.33))-.0015);



    col += moon;
    col += moon2;
    col += moon3;

    vec4 layer;
