    {
    	p.y=mod(p.y,20.0) -6.0;
     }       
    p.z=mod(p.z,12.0) -6.0;
    p.x=mod(p.x,12.0) -5.0;


    float bx1 = sdBox (p-vec3(0.,6.5,5.0),vec3(0.8,7.0,1.0));   //vetical
    //float bx1a = sdBox (p-vec3(0.,0.0,5.4),vec3(0.5,6.0,1.8));   //vertical cch1

    float bx2 = sdBox (p-vec3(0.,10.0,4.6),vec3(4.3,0.5,1.0));  //horizontal
    float bx2a = sdBox (p-vec3(0.,10.0,4.6),vec3(3.0,0.4,1.0));  //horizontal

    float bx3 = sdBox (p-vec3(0.,1.0,5.0),vec3(3.0,0.5,2.0));  //escalon2
    float bx4 = sdBox (p-vec3(0.,0.5,5.0),vec3(4.0,0.5,3.0)); //escalon1

    float bx5 = sdBox (p-vec3(0.,0.0,5.0),vec3(4.5,0.5,4.0)); //escalon3 


    float sp1 = sdSphere (p-vec3(0.0,1.0,-4.0), 0.8);



    float dif1, dif2, dif3;




    dif1=unionSDF(bx1,bx2);
    dif1=unionSDF(dif1,bx3);
    dif1=unionSDF(dif1,bx4);
    dif1=unionSDF(dif1,bx2a);
    dif1=unionSDF(dif1,bx5);


    d=min(d, dif1);


    return d;
}

vec3 GetNormal(vec3 p) {
	vec2 e=vec2(.001,0);
	return normalize(vec3(
		h(p+e.xyy)-h(p-e.xyy),
		h(p+e.yxy)-h(p-e.yxy),
		h(p+e.yyx)-h(p-e.yyx)
	));
}

float AO(vec3 p,vec3 q){			// AO at point p with normal q
	float o=0.,s=1.,r,d;
    	for(float i=0.;i<5.;i++){
		r=.01+.12*i/4.;
		vec3 a=q*r+p;
		d=h(a);
		o+=-(d-r)*s;
		s*=.95;
    	}
    	return clamp(1.-3.*o,0.,1.);   
}

float SH(vec3 p, vec3 q){			// Calculate shadow amount: p=intersection point; q=light direction
	vec3 r = p + q*.01;
	float d;
	for(int i=0; i<16; i++) {
		d = h(r);
		if (d < .001)
			break;
		r += q * d;				// March along!
	}
	if (d < 0.001)
		return 0.1;
	else
		return 1.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
