#define PI 3.14159265

float f(vec2 u)
{
    return mod(abs(u.y*sin(iTime*0.21) + 
                   u.x*u.y*sin(iTime*0.13+2.) + 
                   u.y*u.y*sin(iTime*0.14+3.) + 
                   u.x*u.x*sin(iTime*0.22+4.)), 0.3*length(u*u*u));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy)*2. - 1.;
    uv.y *= iResolution.y / iResolution.x;
    uv *= 2.5;

    vec3 ca = (iTime +uv.x+vec3(0,2.*PI/3.,4.*PI/3.));
    ca.g = 0.;

    vec3 col = 0.525 + 0.625*cos(ca);
    col /= exp(length(uv*uv));
    col = col * 10.;

    float p = 2.0 / iResolution.x;

    float ssx = smoothstep(uv.x-p, uv.x+p, uv.x);
    float ssy = smoothstep(uv.y-p, uv.y+p, uv.y);

    vec3 col1 = col * mix(vec3(f(vec2(uv.x-p, uv.y))), vec3(f(vec2(uv.x+p, uv.y))), ssx);
    vec3 col2 = col * mix(vec3(f(vec2(uv.x, uv.y-p))), vec3(f(vec2(uv.x, uv.y+p))), ssy);
    vec3 col3 = col * mix(vec3(f(vec2(uv.x-p, uv.y-p))), vec3(f(vec2(uv.x+p, uv.y+p))), sqrt(ssx*ssx + ssy*ssy));
    vec3 col4 = col * mix(vec3(f(vec2(uv.x-p, uv.y+p))), vec3(f(vec2(uv.x+p, uv.y-p))), sqrt(ssx*ssx + (1.-ssy)*(1.-ssy)));

    col = (col1 + col2 + col3 + col4) * 0.25;

    fragColor = vec4(col, 1.0);
}

const int MAXD = 400;
const float EPSI  =0.0001;
int matId = 0;
mat2 rotate(float a){
    return mat2(cos(a),-sin(a),
                sin(a),cos(a));
}

float sm( float d1, float d2, float k ) {
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); }

float sdSphere(vec3 p,float size){
    return length(p)-size;
}

float map(vec3 p,bool id){
    float smo = 2.;
    float sphere = sdSphere(p,1.);
    float sphere2 = sdSphere(p+vec3(sin(iTime)*2.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere3 = sdSphere(p+vec3(sin(iTime)*2.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere4 = sdSphere(p+vec3(sin(iTime)*2.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere5 = sdSphere(p+vec3(sin(iTime)*2.,cos(iTime)*4.,cos(iTime)*4.),0.4);
    float sphere6 = sdSphere(p+vec3(cos(iTime)*4.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere7 = sdSphere(p+vec3(cos(iTime)*4.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere8 = sdSphere(p+vec3(cos(iTime)*4.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere9 = sdSphere(p+vec3(sin(iTime)*4.,sin(iTime)*4.,sin(iTime)*4.),0.4);
    float sphere10 = sdSphere(p-vec3(sin(iTime)*2.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere11 = sdSphere(p-vec3(sin(iTime)*2.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere12 = sdSphere(p-vec3(sin(iTime)*2.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere13 = sdSphere(p-vec3(sin(iTime)*2.,cos(iTime)*4.,cos(iTime)*4.),0.4);
    float sphere14 = sdSphere(p-vec3(cos(iTime)*4.,sin(iTime)*2.,sin(iTime)*2.),0.4);
    float sphere15 = sdSphere(p-vec3(cos(iTime)*4.,sin(iTime)*2.,cos(iTime)*4.),0.4);
    float sphere16 = sdSphere(p-vec3(cos(iTime)*4.,cos(iTime)*4.,sin(iTime)*2.),0.4);
    float sphere17 = sdSphere(p-vec3(sin(iTime)*4.,sin(iTime)*4.,sin(iTime)*4.),0.4);


    float plane =1.-(length(p)-550.);
    float sp = sm(sphere9,sm(sphere8,sm(sphere7,sm(sphere6,sm(sphere5,sm(sphere4,sm(sphere3,sm(sphere2,min(sphere,plane),smo),smo),smo),smo),smo),smo),smo),smo);
    float sp2 = sm(sphere17,sm(sphere16,sm(sphere15,sm(sphere14,sm(sphere13,sm(sphere12,sm(sphere11,sm(sphere10,min(sphere,plane),smo),smo),smo),smo),smo),smo),smo),smo);
    float mn = min(sp,sp2);
    if(id){

        if(mn == plane){
            matId = 3;
        }
        else{
            matId = 2;
        }
    }
    return  mn;
}

vec3 normal(vec3 p){
    vec2 e = vec2(EPSI,0);
    return normalize(vec3(map(p+e.xyy,false)-map(p-e.xyy,false),
                            map(p+e.yxy,false)-map(p-e.yxy,false),
                            map(p+e.yyx,false)-map(p-e.yyx,false)));
}
vec3 rayMarch(vec3 ro,vec3 rd, bool id){

    float tot = 0.;
    float dst = 0.;
    vec3 p;
    for(int i = 0; i<MAXD; i++){
        p = ro+rd*tot;
        dst = map(p,id);
        tot+=dst;
        if(dst<EPSI || tot > float(MAXD)){
            break;
        }
    }
    if(dst > EPSI){
       // matId = 1;
    }
    return p;
}
vec3 light(vec3 p,vec3 ro){
    vec3 lightCol = vec3(1);
    vec3 objCol = vec3(0);
    vec3 lightPos = vec3(10,10,12);
    vec3 viewDir = normalize(ro-p);
    vec3 l = normalize(lightPos - p);
    vec3 n = normal(p);
    vec3 reflectDir = reflect(-l,n);
    float diff = max(dot(l,n),0.);
    float spec = pow(max(dot(viewDir,reflectDir),0.),25.);
    float spcStr = 3.;    
    float ambi = 0.45;



    bool rm = length(rayMarch(p+n*EPSI*2.,l,false))<length(lightPos-p);
    if(rm){
        diff *= 0.1;
    }
    if(matId == 2){
        objCol = vec3(1,0.9,0.);
    }

    else if(matId == 3){

        objCol = vec3(0,0,0);
    }
    vec3 diffuse = lightCol*diff;
    vec3 ambient = ambi*lightCol;    
    vec3 specular = spcStr*spec*lightCol;
    return (ambient+diffuse+specular)*objCol;
}



void mainImage(out vec4 fragColor, in vec2 fragCoord){
    vec2 uv = (fragCoord.xy-0.5 * iResolution.xy)/iResolution.x;
    vec3 ro = vec3(0,0,-20);
    vec3 rd = normalize(vec3(uv,1.));
    ro.zy*=rotate(iTime*2.);
    rd.zy*=rotate(iTime*2.);
    ro.xz*=rotate(iTime);
    rd.xz*=rotate(iTime);

    vec3 p = rayMarch(ro,rd,true);
    vec3 color = (light(p,ro));
    if(matId == 1){
        color = vec3(0);
    }
    fragColor = vec4(color,1.0);
}

