float dis(vec3 p){ 
return min(length(mod(p,8.0)-4.0)-1.3,
          box(r(vec3(0.0, -4.0, 0.0)-p, vec3(20.0,20.0,3.0)), vec3(40.0,0.5,0.5))
          );
}

bool trac(out vec3 p, vec3 d, out float dd){
    for(int i = 0; i < 40; i++){
        dd = dis(p);
        if(dd < 0.01){return true;}
        p+=d*dd;
    }
    return false;
}

