#define PI 3.14159265359
#define ITERATIONS 2.0
#define AA_SCALE 2.0

//SETTINGS:
#define VIEW_DIST 25.0
#define C vec4(0.2,0.5,0.3,0.3)
//#define C vec4(0.8,0.-0.1,0.9,0.9)

//RESOURCES:
//https://www.cs.cmu.edu/~kmcrane/Projects/QuaternionJulia/paper.pdf

struct Object
{
 	float dist;
    float difVal;
    float specVal;
    float specKs;
    float id;

    vec3 color;
    vec3 normal;
};

struct MarchRes
{
 	float totalDist;
    float minDist;

    vec3 curRay;
    Object obj;
};

//http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/
vec4 multQuat(vec4 q1, vec4 q2)
{
    vec4 r;
    r.x   = q1.x*q2.x - dot( q1.yzw, q2.yzw );
    r.yzw = q1.x*q2.yzw + q2.x*q1.yzw + cross( q1.yzw, q2.yzw );
    return r;
}

//http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/
vec4 squareQuat(vec4 q)
{
 	vec4 r;
    r.x   = q.x*q.x - dot( q.yzw, q.yzw );
    r.yzw = 2.0*q.x*q.yzw;
    return r;  
}

//http://blog.hvidtfeldts.net/index.php/2011/09/distance-estimated-3d-fractals-v-the-mandelbulb-different-de-approximations/
Object map(vec3 pos)
{
    Object o;
    o.difVal = 1.0;
    o.specVal = 50.0;
    o.specKs = 0.01;    
    o.dist = 1000.0;

    float ang = iTime/8.0;
    float r;

    pos.xz *= mat2(cos(ang), -sin(ang), sin(ang), cos(ang));

    vec4 z = vec4(pos, 0.1);
    vec4 dz = vec4(1, 0, 0, 0);

    float it;
    float minDist = 1000.0;
    float mdX, mdY, mdZ;
    mdX = minDist; mdY = minDist; mdZ = minDist;

    vec4 c = C;
    c.w += 0.5*sin(iTime*2.0);


    for(float x=0.0; x < 20.0; x++)
    {
        dz = 2.0*multQuat(z,dz);
    	z = squareQuat(z) + c;

        minDist = min(minDist, length(z - c)); //store minimum distance from starting point
        mdX = min(mdX, abs(z.x));
		mdY = min(mdY, abs(z.y));
		mdZ = min(mdZ, abs(z.z));

        if(length(z) > 2.0)
        {
            it = x;
            break;
        }
    }

    //Coloring based off orbits: https://www.iquilezles.org/www/articles/ftrapsgeometric/ftrapsgeometric.htm
    o.color = vec3(0) + vec3(1.0, 0.3, 0.55)*sqrt(minDist);
    o.color += vec3(0.0,0.3,0.3)*sqrt(mdX);
    o.color += vec3(0.0,0.4,0)*sqrt(mdY);
    o.color += vec3(0.0,0.1,0.5)*sqrt(mdZ);

    //o.color = vec3(1)-vec3(it/20.0);
    o.dist = 0.5*length(z)*log(length(z)) / length(dz);

    return o;
}

MarchRes marchRay(vec3 pos, vec3 dir, float speed)
{
 	MarchRes res;
    Object o;

    res.totalDist = 0.001;
    res.minDist = 1000.0;

    for(int x=0; x<250; x++)
    {
 		res.curRay = pos + (dir*res.totalDist);

        o = map(res.curRay);

        if(abs(o.dist) < 0.001)
        {
            res.obj = o;
            break;
        }
        else if(res.totalDist >= VIEW_DIST) break;


        if(o.dist < res.minDist) res.minDist = o.dist;
        res.totalDist += o.dist*speed; 
    }

    if(res.totalDist < VIEW_DIST)
    {   
        res.obj = o;
    }


    return res;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float done;
    vec3 col;

    //AA based off IQ's implementation in many different shaders
    for(float x=0.0; x<AA_SCALE; x++){
        for(float y=0.0; y<AA_SCALE; y++){

            vec2 aaOffset = vec2(x,y);
            vec2 uv = (2.0*(fragCoord+aaOffset/AA_SCALE) - iResolution.xy)/iResolution.y;
            vec3 camEye = vec3(0,0.0,2.2);
            vec3 dir = normalize(vec3(uv, -1));


            float rate = 8.0;
            float camAng = PI/3.5;
            float camAngPos = camAng;

            mat2 rotCam = mat2( vec2(cos(camAng), -sin(camAng)), vec2(sin(camAng), cos(camAng)) );
            mat2 rotCamPos = mat2( vec2(cos(camAngPos), -sin(camAngPos)), vec2(sin(camAngPos), cos(camAngPos)) );

            camEye.xz = rotCamPos * camEye.xz;
            dir.xz = rotCam * dir.xz;

            MarchRes res = marchRay(camEye, dir, 1.0);
            vec3 pos = res.curRay;  

            vec3 tempCol = vec3(0.25);
            if(res.totalDist < VIEW_DIST)
            {
                tempCol = res.obj.color;
            }

            col += tempCol;

        }
    }

    fragColor = vec4(col/((AA_SCALE*AA_SCALE)),1.0);
}

#define PI 3.14159265359
#define E 2.7182818284
#define GR 1.61803398875
#define EPS .001

#define MAX_DIM (max(iResolution.x,iResolution.y))
#define time ((saw(float(__LINE__)/GR)+1.0)*(iTime/E+1234.4321)/E)
#define flux(x) (vec3(cos(x),cos(4.0*PI/3.0+x),cos(2.0*PI/3.0+x))*.5+.5)

float cross2d( in vec2 a, in vec2 b ) { return a.x*b.y - a.y*b.x; }

float saw(float x)
{
    float f = mod(floor(abs(x)), 2.0);
    float m = mod(abs(x), 1.0);
    return f*(1.0-m)+(1.0-f)*m;
}
vec2 saw(vec2 x)
{
    return vec2(saw(x.x), saw(x.y));
}

vec3 saw(vec3 x)
{
    return vec3(saw(x.x), saw(x.y), saw(x.z));
}
vec2 invBilinear( in vec2 p, in vec2 a, in vec2 b, in vec2 c, in vec2 d )
{
    vec2 res = vec2(-1.0);

    vec2 e = b-a;
    vec2 f = d-a;
    vec2 g = a-b+c-d;
    vec2 h = p-a;

    float k2 = cross2d( g, f );
    float k1 = cross2d( e, f ) + cross2d( h, g );
    float k0 = cross2d( h, e );

    // if edges are parallel, this is a linear equation. Do not this test here though, do
    // it in the user code
    if( abs(k2)<0.001 )
    {
        float v = -k0/k1;
        float u  = (h.x*k1+f.x*k0) / (e.x*k1-g.x*k0);
        //if( v>0.0 && v<1.0 && u>0.0 && u<1.0 ) 
            res = vec2( u, v );
    }
	else
    {
        // otherwise, it's a quadratic
        float w = k1*k1 - 4.0*k0*k2;
        //if( w<0.0 ) return vec2(-1.0);
        w = sqrt( w );

        float ik2 = 0.5/k2;
        float v = (-k1 - w)*ik2;// if( v<0.0 || v>1.0 ) v = (-k1 + w)*ik2;
        float u = (h.x - f.x*v)/(e.x + g.x*v);
        //if( u<0.0 || u>1.0 || v<0.0 || v>1.0 ) return vec2(-1.0);
        res = vec2( u, v );
    }
    return (res);
}


mat2 rotate(float x) { return mat2(cos(x), sin(x), sin(x), -cos(x)); }


float smooth_floor(float x)
{
	return floor(x)+smoothstep(.75, 1., fract(x));
}

vec2 tree(vec2 uv)
{

    vec2 p = uv*2.-1.;


        float angle = smooth_floor((time))*PI/12.;

        vec2 a = vec2(1., 1.);
        vec2 b = vec2(0., 1.);
        vec2 c = vec2(0., 0.);
        vec2 d = vec2(1., 1./MAX_DIM);


        vec2 s = vec2(.75);
        vec2 o = vec2(smooth_floor(time/PI)/500., 0.);
        mat2 m = rotate(angle);

		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;

        a = a*m; b = b*m; c = c*m; d = d*m;
        a *= s; b *= s; c *= s; d *= s;
        a += o; b += o; c += o; d += o;


        /*
        //= a*.5+.5; b = b*.5+.5; c = c*.5+.5; d = d*.5+.5;
		a = a*2.-1.; b = b*2.-1.; c = c*2.-1.; d = d*2.-1.;
		*/

        vec2 a2 = a*vec2(-1., 1.);
        vec2 b2 = b*vec2(-1., 1.);
        vec2 c2 = c*vec2(-1., 1.);
        vec2 d2 = d*vec2(-1., 1.);
        if(p.x > 0.)
        	p = (invBilinear( p, a, b, c, d ));
        else
        	p = (invBilinear( p, a2, b2, c2, d2 ));
    return p;
}

vec2 flower(vec2 p)
{
	p *= rotate(time);
	float rots = smooth_floor(3.+6.*saw(time/E))+1./MAX_DIM;
	float angle = atan(-p.y, -p.x);
	float radius = length(p);
	angle = floor(((angle/PI)*.5+.5)*rots);


	vec2 a = vec2(1., 0.);
	vec2 b = vec2(1., 1./MAX_DIM);
	vec2 c = vec2(0., 1./MAX_DIM);
	vec2 d = vec2(0., -1./MAX_DIM);

	b *= rotate(angle/rots*2.*PI);
	angle += 1.;
	a *= rotate(angle/rots*2.*PI);

	return (invBilinear( p, a, b, c, d ));
}


float square(vec2 uv, vec2 uv0)
{
    uv = (uv*2.-1.)*GR;
    return abs(saw(uv.y+uv0.x-uv0.y+time)-uv.x);
}


vec2 spiral(vec2 uv)
{
    float r = log(length(uv)+1.)/2.;
    float theta = atan(uv.y, uv.x)/PI-r*sin(time/E/PI/GR)/PI;
    return vec2(saw(r+time/E/E),
                saw(theta+time/GR/E))*2.-1.;
}

