    uv.xy += vec2(cos(time*.234),
                  sin(time*.345))*scale/2.;
	float m = smoothstep(0.45, .55, saw(time/E/PI));
    uv.xy = spiral(uv.xy*scale)*m+(1.-m)*(uv0);;

	float nature = smoothstep(.45, .55, saw(time/GR/E))*(1.-m);
