// basically a combination of:
// - https://mathematica.stackexchange.com/a/66564 by Nikolai Mikuszeit
// - https://www.shadertoy.com/view/Ws3SDl by Inigo Quilez

#define AA 2

float pi = 3.14159265, tau = 6.2831853;

float box (in float x, in float x1, in float x2, in float a, in float b) {
	return tanh(a * (x - x1)) + tanh(-b * (x - x2));
}

float ex (in float z, in float z0, in float s) {
    return exp(-(z - z0) * (z - z0) / s);
}

float r(in float z, in float x) {
    float s = sin (tau * x), c = cos(tau * x),
		c2 = cos (pi * x), t4z = tanh(4. * z);
    return /* body */.4 * (1.0 - .4 * ex(z, .8, .15) +
		s * s + .6 * ex(z, .8, .25) * c * c + .3 * c) *
		0.5 * (1. + t4z) + /* legs */ (1. - .2 * ex(z, -1.3, .9)) *
		0.5 * (1. - t4z) * (.5 * (1. + s * s + .3 * c) *
		(pow(abs(s), 1.3) + .08 * (1. + t4z) ) ) +
		/* improve butt */ .13 * box(c2, -.45, .45, 5., 5.) *
		box(z, -.5, .2, 4., 2.) - 0.1 * box(c2, -.008, .008, 30., 30.) *
		box(z, -.4, .25, 8., 6.) - .05 * pow(abs(sin(pi * x)), 16.) * box(z, -.55, -.35, 8., 18.);
}

// $1M question: how close are we to ParametricPlot3D[...] surface?
float sd( in vec3 p )
{
	/* shift butt belly */
	float dx = .1 * exp(-pow((p.z-.8),2.)/.6) - .18 * exp(-pow((p.z -.1),2.)/.4);

    // on the surface, we have:
    // p.x = r * cos + dx
	// p.y = r * sin
	float angle = atan(p.y, p.x - dx);
	float r_expected = r(p.z, angle / tau);

    // this does work better now, thanks to (...smoothstep(..)) multiplier ~ @Del's patch
    return (.5 + .5 * smoothstep(.4,1.,p.z)) * (
           length(vec2(p.y, p.x - dx)) - r_expected
    );
}


float map( in vec3 pos )
{
    return sd (pos.zxy);
}

// http://iquilezles.org/www/articles/normalsSDF/normalsSDF.htm
vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0,-1.0)*0.5773;
    const float eps = 0.001;
    return normalize( e.xyy*map( pos + e.xyy*eps ) + 
					  e.yyx*map( pos + e.yyx*eps ) + 
					  e.yxy*map( pos + e.yxy*eps ) + 
					  e.xxx*map( pos + e.xxx*eps ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
     // camera movement	
	float an = 0.5*iTime - 0.8;
	vec3 ro = vec3( 2.5*sin(an), 0.5, -2.5*cos(an) );
    vec3 ta = vec3( 0.0, 0.5, 0.0 );
    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));



    vec3 tot = vec3(0.0);

    #if AA>1
    for( int m=0; m<AA; m++ )
    for( int n=0; n<AA; n++ )
    {
        // pixel coordinates
        vec2 o = vec2(float(m),float(n)) / float(AA) - 0.5;
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+o))/iResolution.y;
        #else    
        vec2 p = (-iResolution.xy + 2.0*fragCoord)/iResolution.y;
        #endif

	    // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 1.8*ww );

        // raymarch
        const float tmax = 5.0;
        float t = 0.0;
        for( int i=0; i<150; i++ )
        {
            vec3 pos = ro + t*rd;
            float h = map(pos);
            if( h<0.0001 || t>tmax ) break;
            t += h;
        }


        // shading/lighting	
        vec3 col = vec3(0.0);
        if( t<tmax )
        {
            vec3 pos = ro + t*rd;
            vec3 nor = calcNormal(pos);
            float dif = clamp( dot(nor,vec3(0.7,0.6,0.4)), 0.0, 1.0 );
            float amb = 0.5 + 0.5*dot(nor,vec3(0.0,0.8,0.6));
            col = vec3(0.3,0.15,0.1)*amb + vec3(0.8,0.5,0.2)*dif;
        }

        // gamma        
        col = sqrt( col );
	    tot += col;
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

	fragColor = vec4( tot, 1.0 );
}

// Improved smoothing using smoothstep
// and mixing trick from 
// https://www.shadertoy.com/view/WdBGzd

#define PI2 6.28318530718

#define COLOR1 vec3(1., 1., 0.6)
#define COLOR2 vec3(0., 0.1, 0.4)
#define LIGHTCOLOR  vec3(1., 1., 1.)

#define ARMS 10.
#define DENSITY 3.
#define ANGLE_SPEED 1.
#define SPEED 2.
#define PULSE_SPEED 4.
#define SMOOTH 0.08

float smoothCheckerboard(in vec2 uv) {    
    // Shift, as fract gives 0..1 and we will smooth
    // around 0.25 and 0.75.
    uv = fract(uv + 0.25);
    float sm2 = SMOOTH * 0.5;
    // p01 oscillates between 0 and 1.
    vec2 p01 =
        smoothstep(0.25 - sm2, 0.25 + sm2, uv) -
        smoothstep(0.75 - sm2, 0.75 + sm2, uv);
    // pn11 oscillates between -1 and 1.
    vec2 pn11 = (p01 - 0.5) * 2.;
    // This results in smoothed 0..1 transitions.
    // We take advantage of the fact that multiplication
    // of -1/1 behaves like XOR. Then we rescale
    // -1..1 result of the multiplication to 0..1.
    return 0.5 - 0.5 * pn11.x * pn11.y;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2
        R = iResolution.xy,
        // Cartesian [-1, 1] along shorter axis.
        uv = (2. * fragCoord - R) / min(R.x, R.y);
    float
        T = iTime,
        // Angle [-PI, PI] and radius.
        a = atan(uv.x, uv.y),
        r = length(uv);

    // Rescale polar coords, as checkerboard generates 1x1 squares.
    vec2 polar = vec2(
        a * ARMS / PI2 + T * ANGLE_SPEED,
        1. / r * DENSITY + T * SPEED 
    );

    vec3 checker = mix(COLOR1, COLOR2, smoothCheckerboard(polar));
    // Light in the center.
    float light = smoothstep(0.7 + 0.3 * sin(T * PULSE_SPEED), 0.2, r);
    vec3 color = mix(checker, LIGHTCOLOR, light);
    fragColor = vec4(color, 1.);
}

#define MAX_STEPS 500
#define MAX_DISTANCE 1000.0
#define MIN_SURF_DIST 0.01
#define SPEC_POW 12.0
#define AMBIENT_LIGHT 0.0
#define USE_SHADOW 0.0

float sphere(vec3 p, vec4 s)
{
    return length(p - s.xyz) - s.w;
}

float sdOctahedron( vec3 p, float s)
{
  p = abs(p);
  float m = p.x+p.y+p.z-s;
  vec3 q;
       if( 3.0*p.x < m ) q = p.xyz;
  else if( 3.0*p.y < m ) q = p.yzx;
  else if( 3.0*p.z < m ) q = p.zxy;
  else return m*0.57735027;

  float k = clamp(0.5*(q.z-q.y+s),0.0,s); 
  return length(vec3(q.x,q.y-s+k,q.z-k)); 
}

float sdTorus( vec3 p, vec2 t )
{
  vec2 q = vec2(length(p.xz)-t.x,p.y);
  return length(q)-t.y;
}

float ground(vec3 p, float h)
{
    return p.y - h;
}


float twistGeo(in vec3 p )
{
    float k = 5.0 * (1.0 + sin(iTime)); // or some other amount
    float c = cos(k*p.y);
    float s = sin(k*p.y);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*p.xz,p.y);
    return sdTorus(q, vec2(0.4 , 0.2));
}

float map(vec3 p)
{
    float nd = 0.0;
    float sd = twistGeo(p);
    float gd = ground(p, -0.7);
    nd = min(sd, gd);
    return nd;
}

vec3 calcNormal(vec3 p)
{
    vec2 e = vec2(0.01, 0.0);
    float d = map(p);
    vec3 n = vec3(
        map(p + e.xyy) - d,
        map(p + e.yxy) - d,
        map(p + e.yyx) - d
        );
    return normalize(n);
}

float tracing(vec3 ro, vec3 rd)
{
    float d = 0.0;       // traced distance from light origin
    vec3 p = ro;         // traced position
    for(int i = 0; i < MAX_STEPS; i++)
    {
       	float nd = map(p);
        d += nd;
        p = ro + rd * d;
        if(d > MAX_DISTANCE || nd <MIN_SURF_DIST)
            break;
    }
    return d;
}

// blin-phong
vec3 lighting(vec3 p, vec3 rd)
{
    float pLightRadius = 10.0;
    vec3 lightPos = vec3(pLightRadius * sin(iTime), 8.0, pLightRadius * cos(iTime));
    vec3 l = normalize(lightPos - p);
    vec3 n = calcNormal(p);

    float diff = max(dot(n, l), 0.0);   //diffuse  
    vec3 refl = normalize(-l + 2.0*n*max(dot(l, n), 0.0));
    float spec = pow(max(dot(rd, refl), 0.0), SPEC_POW); //specular
    //float spec = 0.0;
    //diff = 0.0;
    //spec = 0.0; // debug spec
    float sd = tracing(p + n * MIN_SURF_DIST * 2.0, l);     						// cast a ray from current position to light position
    float shadow = sd < length(lightPos - p) ? 0.1 : 1.0;            // current position is in shadow if hit anything
    shadow = mix(1.0, shadow, USE_SHADOW);
    vec3 col = vec3(diff + spec + AMBIENT_LIGHT);
    return col * shadow;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime;
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * 2.0;
	uv.x *= iResolution.x / iResolution.y;

    float zoom = 0.0;
    vec3 ro = vec3(30.0 * cos(t), 1.0, 30.0 * sin(t));
    //vec3 ro = vec3(0.0, 1.0, -5.0);
    vec3 lookat = vec3(0.0, 0.0, 0.0);

    vec3 f = normalize(lookat - ro);
    vec3 r = cross(vec3(0.0, 1.0, 0.0), f);
    vec3 u = cross(f, r);

	ro += f * zoom;
    vec3 rd = normalize(uv.x * r + uv.y * u - ro);

    float d = tracing(ro, rd);

    vec3 p = ro + rd*d;
    vec3 col = lighting(p, rd);


    fragColor = vec4(col, 0.0);

}

