float sdfSphere(vec3 position) {
    vec3 center = vec3(0.0);
    float radius = 0.25;
    return distance(position, center) - radius;
}

vec3 calcNormal( vec3 p ) 
{
    // We calculate the normal by finding the gradient of the field at the
    // point that we are interested in. We can find the gradient by getting
    // the difference in field at that point and a point slighttly away from it.
    const float h = 0.0001;
    return normalize( vec3(
        			       -sdfSphere(p)+ sdfSphere(p+vec3(h,0.0,0.0)),
                           -sdfSphere(p)+ sdfSphere(p+vec3(0.0,h,0.0)),
                           -sdfSphere(p)+ sdfSphere(p+vec3(0.0,0.0,h)) 
    				 ));
}

float raymarch( vec3 direction, vec3 start) {
    // We need to cast out a ray in the given direction, and see which is
    // the closest object that we hit. We then move forward by that distance,
    // and continue the same process. We terminate when we hit an object
    // (distance is very small) or at some predefined distance.
    float far = 15.0;
    float d = 0.0;
    vec3 pos = start;
    for (int i=0; i<100; i++) {
    	float sphereDistance = sdfSphere(pos);
        pos += sphereDistance*direction;

        d += sphereDistance;
        if (sphereDistance < 0.01) {
        	break;
        }
        if (d > far) {
        	break;
        }
    }
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalise and set center to origin.
    vec2 p = fragCoord/iResolution.xy;
    p -= 0.5;
    p.x *= iResolution.x/iResolution.y;
    vec2 q = p - vec2(0.0, 0.0);

    vec3 planePosition = vec3(0.0, 0.0, -5.0);
    vec3 lookingDirection = vec3(0.0, 0.0, 1.0);

    // Rotate light around origin in xz plane
    float angle = iTime;
    vec2 lightPos2D = mat2(cos(angle),-sin(angle),sin(angle),cos(angle))*vec2(0.0,1.0); 
    vec3 lightPoint = vec3(lightPos2D.x, 0.0, lightPos2D.y);
    vec3 lightFacing = lightPoint - vec3(0.0);

    // raymarch to check for colissions.
    vec3 start = vec3(p, planePosition.z);
    float dist = raymarch(lookingDirection, start);
    float color = 0.0;
    if (dist < 15.0) {
    	vec3 normal = calcNormal(start+ dist*lookingDirection);
        color = -dot(lightFacing, normal);
    }
    fragColor = vec4(vec3(color),1.0);
}

float Circle(vec2 uv, vec2 p, float r, float blur){
	float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);
    return c;
}

float Band(float t, float start, float end, float blur){
	float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end-blur, t);
    float band = step1 * step2;
    return band;
}

float Rect(vec2 uv, float left, float right, float top, float bottom, float blur){
	float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    float rect = band1 * band2;
    return rect;
}

float remap01(float a, float b, float t){
	return (t-a) / (b-a);
}

float remap(float a, float b, float c, float d, float t){
	return remap01(a, b, t) * (d-c) + c;
}

float Smiley(vec2 uv, vec2 p, float size){

    uv -= p;
    uv /= size;

    float mask = Circle(uv, vec2(0), 0.4, 0.05);
    vec3 col = vec3(1,1,0);


    mask -= Circle(uv, vec2(-0.15,0.1), 0.08, 0.015);
    mask -= Circle(uv, vec2(0.15,0.1), 0.08, 0.015);
    //mask -= Circle(uv, vec2(0,-0.2), 0.1, 0.015);

    float mouth = Circle(uv, vec2(0), 0.3, 0.015) * abs(sin(iTime));
    mouth -= Circle(uv, vec2(0,0.05), 0.3, 0.015) * abs(sin(iTime));

    mask-= mouth;
    return mask;
}

