             res=vec2(res.x, 11);

         } 
     } 


    float sdt1= trianguloFalso( p, vec3(5,0.,-3.0), 2.5 );
    float sdt2= trianguloFalso( p, vec3(-5,0.,-5.0), 5. );
    float sdt3= trianguloFalso( p, vec3(15,0.,-3.0), 8. );
