    float sdbv1 =sdBox(p-vec3(-total-0.,0.0,0.0), vec3(0.25,6.5,0.25) ); //inicio
    float sdbv2 =sdBox(p-vec3(total-0.,0.0,0.0), vec3(0.25,6.5,0.25) );  //fin


    res =opU(res, vec2(sdbh1sup,8.0));
    res =opU(res, vec2(sdbh1inf,8.0));


    for(float ii=0.0; ii<numbar; ii++) 
