#define SIDES 7

#define SECONDS 5.0
#define COLORS 24.0

#define DITHER_AMOUNT 90.0
#define DITHER_SIZE 8.0

#define PI 3.14159265359
#define HALF_PI 1.57079632675
#define TWO_PI 6.283185307

mat2 rotate(float angle){   return mat2( cos(angle),-sin(angle),sin(angle),cos(angle) );    }

vec2 ratio(vec2 st)
{
    return  vec2(
            max(st.x/st.y,1.0),
            max(st.y/st.x,1.0)
            );
}

vec2 center(vec2 st)
{
    float offset = iResolution.x/iResolution.y;
    st.x = st.x * offset - offset * 0.5 + 0.5;
    return st;
}

float divide(float x,float a){          return floor(x * a)/a;    }

// cos color palette by iq
vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d ){	return a + b*cos( 6.28318*(c*t+d) );	}

vec3 color_gradient(float x){          return texture(iChannel0, vec2(x,0.5),-10.0).rgb;}
vec3 color_gradient(float x,float a){   return color_gradient(divide(x,a));    }

float random(vec2 st){  return fract( sin( dot(st,vec2(3.678,678.90)) )*678910.0 ); }

float dither(vec2 st, float x, float amount)
{
    float s = 1.0/DITHER_SIZE;

    st *= amount;
    st.y += 0.5*step(mod(st.x,2.0),1.0);

    float c = length(fract(st)-0.5)*x*0.5;
    c = smoothstep(0.75,1.0,1.0-c)*(s)*0.5;   
    return fract(x + c );
    //return x+c;
}


float hex(vec2 pos)
{
    pos = abs(pos);
    float d = dot(pos,normalize(vec2(1.0,1.73)));
        d = max(d,pos.x);
    return d;
}

vec2 hexCoords(vec2 st, out vec2 position)
{
    vec2 aspect = vec2(1.00,1.73);
    vec2 h_aspect = aspect * 0.5;

    vec2 a = mod(st,aspect)-h_aspect;
    vec2 b = mod(st-h_aspect,aspect)-h_aspect;

    vec2 grid;
    if(length(a) <length(b))
        grid = a;
    else
        grid = b;    

    position = st-grid;
    return vec2(grid.x,grid.y);
}

float cutout(float x, float y, float cut)
{
    float z = x;
    z = max(z,-y+cut);
    z = min(z,y);
    return z;
}

float exponentialInOut(float t) {
  return t == 0.0 || t == 1.0
    ? t
    : t < 0.5
      ? +0.5 * pow(2.0, (20.0 * t) - 10.0)
      : -0.5 * pow(2.0, 10.0 - (t * 20.0)) + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = fract(iTime/SECONDS);
    float interval = floor(mod(iTime,SECONDS));

	vec2 st = fragCoord/iResolution.xy;
    st = center(st);
    st = st * 2.0 - 1.0;  

    vec2 uv = st;

    st *= 01.35;
    st *= 1.0 + 0.025*sin(TWO_PI*time);
    st *= rotate(TWO_PI*.25);      

    float pixelSmoothing = 2.0;
    float aa = ratio(iResolution.xy).x/iResolution.x;
    aa *= pixelSmoothing;

    float cut_size = 0.1125;

    const int n = SIDES;
    float a = TWO_PI/float(n);

    vec2 pos;
    vec2 angle_st = hexCoords(st*1.0,pos);
    float angle = atan(angle_st.x,angle_st.y)/TWO_PI+0.5;

    float debug;
    float sdf = 1.0;

    float distance_offset = 0.25;

    for(int i = 0; i <= n*2; i++)
    {
        vec2 pos = (st) * rotate(float(i)*a)-distance_offset;
        float size = .06*(0.5+0.5*sin(TWO_PI*time+HALF_PI*(pos.x-pos.y*.65)));
        float d = abs(hex(pos)-0.5)+size;
        float cut = pos.x-pos.y*.65;
        cut = smoothstep(-aa,aa,sdf);
        cut = (mod(float(i),2.0) >= 1.0) ? 1.0-cut :cut;

        float m = cutout(d/cut,sdf/cut,cut_size);
        sdf = min(sdf,m);
    }

    float texture = dither(st,sdf+a,DITHER_AMOUNT);
    float cutoff = sdf-0.025;
    cutoff = smoothstep(-aa,aa,cutoff);

    st = hexCoords(st*0.44,pos);
    a = atan(st.x,st.y)/TWO_PI+0.5;

    float grad = fract(texture*1.0-time*1.0+a*1.0);
    vec3 tex = color_gradient(grad,COLORS);

    vec3 p = palette(length(tex),vec3(1.25,0.125,0.025),vec3(0.0,0.95,0.125),vec3(1.0, 0.5, 0.0),vec3(0.5));
    tex = p;

    float size = hex(st);
    float outside_cutoff = max(sdf-0.25,-(sdf-0.23));
    outside_cutoff = smoothstep(-aa,aa,outside_cutoff);

    float bg = length(uv)-0.75-0.25*random(uv);
    vec3 background = vec3(0.9333, 0.9333, 0.9333);
    vec3 col = vec3(background);
    	col = mix(vec3(1.0),col,bg);
        col = mix(tex,col,cutoff);

    fragColor = vec4(col,1.0);
}

float DistLine(vec3 ro, vec3 rd, vec3 p) {
	return length(cross(p-ro, rd))/length(rd);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0., .5, -2.);
    vec3 rd = vec3(uv.x, uv.y, 0.)-ro;

    float t = iTime*2.;
    vec3 p =.5*vec3(sin(t), 0., .5+cos(t));
    vec3 q =.5*vec3(sin(t+3.1416), 0., .5+cos(t+3.1416));
    float d = DistLine(ro, rd, p);
    float e = DistLine(ro, rd, q);

    d = pow(.05/(d+0.), 1.);
    e = pow(.05/(e+0.), 1.);

    fragColor = vec4(d*vec3(1.,0.,1.)+e*vec3(0.,1.,1.),1.);
}

