float rand(vec2 n) 
{ 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

float noise(vec2 p)
{
	vec2 ip = floor(p);
	vec2 u = fract(p);
	u = u*u*(3.0-2.0*u);

	float res = mix(
		mix(rand(ip),rand(ip+vec2(1.0,0.0)),u.x),
		mix(rand(ip+vec2(0.0,1.0)),rand(ip+vec2(1.0,1.0)),u.x),u.y);
	return res*res;
}

// power smooth min from iq;
float smin( float a, float b, float k )
{
    a = pow( a, k ); b = pow( b, k );
    return pow( (a*b)/(a+b), 1.0/k );
}

float circle(in vec2 p) 
{
	vec2 q = p;
    float d = 0.0;

    p*=1.2;
    p.y -=0.3;
    d = length(p);

    // drop
    p*=2.5;
    p.x*=5.0;
    p.x -= 3.5; p.y -= 10.5;
    p.y += smoothstep(0.0, 0.99, fract(iTime*0.125)) * 20.0;
    p.x = (mod(p.x, 3.0) / 1.5) * pow(abs(p.x), 5.0);
    d = smin(d, length(p), 1.85);

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;
    vec2 uv = fragCoord/res.xy;

	vec2 p = 2.0*uv-1.0;

    p.x *= res.x/res.y;
    p *= 1.5;

	// noise
    p += noise(p*4.63)*0.075;
    p += noise(0.2*iTime+p*124.0)*0.015;

    // circle
    float c1 = step(circle(p), 0.95);
    vec3 col = vec3(c1*0.675, 0.8, 0.9);

    // organic grain
    float grain = texture(iChannel0, uv+noise(66.0*uv)*0.15).r; 
    grain += 1.5*step(grain*1.75, 0.575)*c1;
    col = mix(col, col*grain, 0.25*noise(p*120.5));

    // bg
    col = mix(1.25*vec3(0.175, 0.2, 0.2), col, c1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define PI 3.14159265358979323846

vec2 rotate2D(vec2 _st, float _angle){
    _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    _st += 0.5;
    return _st;
}

vec2 tile(vec2 _st, float _zoom){
    _st *= _zoom;
    return fract(_st);
}

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = vec2(0.4)-_size*0.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,vec2(1.0)-_st);
    return uv.x*uv.y;
}

float check(vec2 _st){
    float ratio = (sin(iTime * PI * 0.5)+1.0) / 2.0;
    vec2 v_ratio = vec2(cos(iTime* PI)*0.5+0.5, sin(iTime*PI)*0.5 + 0.5);
    v_ratio = vec2(fract(iTime));
    float res = step(v_ratio.x, _st.x);
    float res2 = step(v_ratio.y, _st.y);
    float res3 = step(_st.x, v_ratio.x);
    float res4 = step(_st.y, v_ratio.y);
    return res * res2 + res3 * res4;
}

vec2 scale(vec2 _st, vec2 _scale)
{
	return mat2(_scale.x, 0.0,
               0.0, _scale.y) * _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 color = vec3(0.0);

    uv = tile(uv,3.0);

    vec2 uv2 = fragCoord/iResolution.xy;
    uv2 = tile(uv2,4.0);

	uv = rotate2D(uv, iTime * PI);

    vec3 pink = vec3(1.000,0.604,0.887);
    vec3 white = vec3(1.0);
    vec3 waterblue = vec3(0.545,0.933,1.000);

    vec3 pattern = vec3(box(uv,vec2(0.5),0.01));
    vec3 rev_pattern = vec3(1.0) - pattern;

    color += pattern * pink;
    color += rev_pattern * waterblue;

    vec3 checkpattern = vec3(check(uv2));
    vec3 rev_checkpattern = vec3(1.0) - checkpattern;
    vec3 color2 = checkpattern * pink + rev_checkpattern;

    vec3 result = color2 * color;

    fragColor = vec4(result,1.0);
}

