// vector field variant of "Noise - wave - 2D" by iq. https://shadertoy.com/view/tldSRj

// Fabrice's way to see it: 1 Gabor kernel at each cell center, overlapping neighbor cells
// Here, complex Gabor instead of just the real part.

#define CS(a) cos(a + vec2(0,33))

vec2 g( vec2 n ) { return CS(n.x*n.y); }  // use better hash than n.x*n.y

vec2 noise(vec2 p)
{
    const float kF = 3.1415927;

    vec2 i = floor(p);
	vec2 f = fract(p);
    f = f*f*(3.-2.*f);
    float t = (mod(i.x+i.y,2.)*2.-1.) * iTime;       // checkered rotation ( flownoise )
    return mix(mix(CS( t+ kF*dot(p,g(i+vec2(0,0)))), // Gabor kernel (and overlapping neighbors)
               	   CS(-t+ kF*dot(p,g(i+vec2(1,0)))),f.x),
               mix(CS(-t+ kF*dot(p,g(i+vec2(0,1)))),
               	   CS( t+ kF*dot(p,g(i+vec2(1,1)))),f.x),f.y);
}


float f, scale = 24.,      // scale of noise
      sampling = 7.;       // density of vectors

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy,
	    uv = ( u - R/2. ) / R.y,
	     p = fract(scale*uv)*2.-1., n; // p = local centered coords in cells

    // left: 1-band noise	
	if( uv.x<0. )
	{
        n = noise( floor(scale*uv)/sampling ); // floor: optional (without = curvy)
	}
    // right: fractal noise (4 octaves)
    else	
	{
        mat2 m = mat2(2); // mat2( 1.6,  1.2, -1.2,  1.6 ); // sqrt2 not floor-friendly ;-)
#define N(uv,s) noise( floor(scale*uv/s)*s/sampling ); // floor: optional (without = curvy)
		n  = 0.5000*N( uv, 1.); uv = m*uv;
		n += 0.2500*N( uv, 2.); uv = m*uv;
		n += 0.1250*N( uv, 4.); uv = m*uv;
		n += 0.0625*N( uv, 8.); uv = m*uv;
	}

    // --- display vector field 

  //n = normalize(n);  // optional : for more dramatic vortices
    float l = length(n),
          x = (n.x*p.y-n.y*p.x) / l,
          w = dot(n,p) / l;         // option: arrow points to vector dir
    f = smoothstep(scale*3./R.y, 0., abs(x)+.05*w ); // display vector line
    f *= smoothstep(3./R.y, 0., abs(w)- l ); // optional: show vector length

	O = vec4( f );

    O.x += smoothstep( 3./R.y, 0., abs(uv.x) );	// separator

}

