float dist(vec2 p1, vec2 p2)
{
	float dx = p1.x - p2.x;
    float dy = p1.y - p2.y;
    return sqrt(dx*dx + dy*dy);
}

float circles(vec2 p, float spacing, float radius)
{
	int overlaps = 0;
    float results[4];
    float mx = mod(p.x, spacing);
    float my = mod(p.y, spacing);
    float d = dist(vec2(mx,my), vec2(0.0,0.0));

    if (d < radius)
    {
        results[overlaps++] = smoothstep(0.0,1.0,max(1.0 - d / radius, 0.0));
    }

    mx = mod(p.x, -spacing);
    my = mod(p.y, spacing);
    d = dist(vec2(mx,my), vec2(0.0,0.0));

    if (d < radius)
    {
        results[overlaps++] = smoothstep(0.0,1.0,max(1.0 - d / radius, 0.0));

    }

    mx = mod(p.x, spacing);
    my = mod(p.y, -spacing);
    d = dist(vec2(mx,my), vec2(0.0,0.0));

    if (d < radius)
    {
        results[overlaps++] = smoothstep(0.0,1.0,max(1.0 - d / radius, 0.0));
    }

    mx = mod(p.x, -spacing);
    my = mod(p.y, -spacing);
    d = dist(vec2(mx,my), vec2(0.0,0.0));

    if (d < radius)
    {
        results[overlaps++] = smoothstep(0.0,1.0,max(1.0 - d / radius, 0.0));
    }

    float result = 0.0;

    for (int i=0;i<overlaps;i++)
    {
     	if (results[i] > result)
            result = results[i];
    }

    return min(result, 1.0);
}

