#define R iResolution.xy
#define O 3.
vec2 hash22(vec2 p) // Dave H
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);

}
vec2 cell (vec2 u) {
	vec2 V = floor(u*O+0.5)/O;
    vec2 h = hash22(V);
    return V + h.x*vec2(cos(6.2*h.y+iTime),sin(6.2*h.y+iTime))/O;
}
float pie (vec2 p, vec2 a, vec2 b) {
	vec2 m = 0.5*(a+b); // midpoint
    //if (length(a-b)<1e-3) return 1e3; // ignore self
	return abs(dot(p-m,b-m)/dot(b-m,b-m)); // pojection
}
float line (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return (length(p-a-(b-a)*i));
}
vec2 cc (vec2 a, vec2 b, vec2 c) {
    vec2 ab = 0.5*(a+b), ac = 0.5*(a+c);
	float m1 = (a.x-b.x)/(b.y-a.y), m2 = (a.x-c.x)/(c.y-a.y);
    float b1 = ab.y-m1*ab.x, b2 = ac.y-m2*ac.x;
    float x = (b1-b2)/(m2-m1);
    return vec2(x,m1*x+b1);
}
float sg (vec2 p, vec2 a, vec2 b) {
    float i = clamp(dot(p-a,b-a)/dot(b-a,b-a),0.,1.);
	return length(p-a-(b-a)*i);
}
float tri (vec2 U, vec2 a, vec2 aa, vec2 aaa) {
    vec2 m = cc(a,aa,aaa);
    float v = length(a-m), w = length(U-m);
    return w-v;
}
void mainImage (out vec4 Q, vec2 U) {
	U = 2.*(U-0.5*R)/R.y;
    vec2 V = vec2(1e3);
    for (int x = -3; x <= 3; x++)
    for (int y = -3; y <= 3; y++) {
		vec2 c = cell(U+vec2(x,y)/O);
        if (length(c-U) < length(V-U))
            V=c;
    }
    vec2 W = vec2(1e3);
    for (int x = -3; x <= 3; x++)
    for (int y = -3; y <= 3; y++) {
		vec2 c = cell(U+vec2(x,y)/O);
        if (pie(U,V,c) < pie(U,V,W))
            W=c;
    }
    vec2 Z = vec2(1e3);
    for (int x = -3; x <= 3; x++)
    for (int y = -3; y <= 3; y++) {
		vec2 c = cell(U+vec2(x,y)/O);
        if (tri(U,V,W,c) < tri(U,V,W,Z))
            Z=c;
    }

    Q = 0.5+0.3*sin(30.*(tri(U,V,W,Z))+vec4(1,2,3,4));
	Q *= smoothstep(0.01,0.02,pie(U,W,V)*length(W-V));
    Q += vec4(.5)*smoothstep(0.03,0.00,min(line(U,W,V),min(line(U,W,Z),line(U,V,Z))));
    Q *= smoothstep(0.03,0.04,length(U-V));
}

//--------------------------------------------------------------------------------------------------------------------------------------//  Brick wall with self-shadowing.
//
//  set BRICK_PATTERN   to select the pattern to compile  ( or add more patterns...)
//
//  The surface is seen as an array of tesselating rectangular cells. 
//  Each cell has a series of entries. Each entry represents a single brick (0,0)  to  shiftAndSizes[entry].zw  in the cell corner. 
//  shiftAndSizes[entry].xy  has the offset of the cell, for that entry  (cells effectively overlap per entry).
//
//  This enables the definition of different brick patterns.
//  
//  
//  CELLSIZEU and CELLSIZEV are set for the pattern.  If we add to these, we space the cells out.
//
//  All code, feel free to use, abuse and improve...
//
//  'BRICK_PATTERN'   set this for a few simple patterns I set up - 0,1,2,3.  
//   
//--------------------------------------------------------------------------------------------------------------------------------------

#define  USE_SHADOW 
#define  BRICK_PATTERN 3

// LIGHT_DIRECTION:   y  is  negative as coming from ABOVE.
//                    (  y close to 0  has numerical stability issues for shadow  (ie. light straight down).
//                      producing sparkle on brick edges. )

const vec3   LIGHT_DIRECTION =  normalize(vec3(  0.917,  -0.62,  -0.4 ));  
const float  LIGHT_AMBIENT_INTENSITY = 0.5f;   

 // TODO:   have brick sizes per 'shiftAndSizes' entry.  More than one entry can have the same 'V' offset so should be easy
 //         to have mixed size bricks on one row.  This would make it possible to do herringbone etc.  

#if BRICK_PATTERN == 0		// regular brick

	const float BRICK_ULEN = 0.1; 
	const float BRICK_UGAP = 0.05;

	const float BRICK_VLEN = 0.35; 
	const float BRICK_VGAP = 0.05; 

	const int maxLayers = 2;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0,     BRICK_ULEN, BRICK_VLEN ),  
      vec4( BRICK_ULEN + BRICK_UGAP, 0.5 * (BRICK_VLEN + BRICK_VGAP ),  BRICK_ULEN, BRICK_VLEN ) 
	);

	const float CELLSIZEU  = (BRICK_ULEN + BRICK_UGAP ) * 2.0 + 0.001;
	const float CELLSIZEV  = (BRICK_VLEN + BRICK_VGAP ) + 0.001;
	const float  DepthOfBrick = 0.04439;

#elif BRICK_PATTERN == 1      //  short and long alternate

	const float BRICK_ULEN = 0.1; 
	const float BRICK_UGAP = 0.05;

	const float BRICK_VLEN = 0.35; 
	const float BRICK_VGAP = 0.05; 
	const float BRICK_VLEN_SHORT = 0.11 ;


	const float dispIn = (BRICK_VGAP + BRICK_VLEN_SHORT) * 0.5 ; 
	const int maxLayers = 4;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0,   BRICK_ULEN, BRICK_VLEN_SHORT), 
      vec4( 0, BRICK_VLEN_SHORT + BRICK_VGAP,  BRICK_ULEN, BRICK_VLEN ), 
      vec4( BRICK_ULEN + BRICK_UGAP,  dispIn,  BRICK_ULEN, BRICK_VLEN ) ,
      vec4( BRICK_ULEN + BRICK_UGAP, dispIn + BRICK_VLEN  + BRICK_VGAP,    BRICK_ULEN, BRICK_VLEN_SHORT ) 
	);

	const float CELLSIZEU  = (BRICK_ULEN + BRICK_UGAP ) * 2.0 + 0.001;
	const float CELLSIZEV  = (BRICK_VLEN + BRICK_VGAP ) + (BRICK_VLEN_SHORT + BRICK_VGAP ) + 0.001;
	const float  DepthOfBrick = 0.04439;

#elif BRICK_PATTERN == 2        // square cycle

	const float SCL = 0.8;	// scale to what looks pretty...
	const float BRICK_SA = 0.1 * SCL;
	const float BRICK_SB = 0.05* SCL;
	const float BRICK_SC = 0.25* SCL;
    const float BRICK_SD = 0.05* SCL; 

	const int maxLayers = 4;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0, BRICK_SC, BRICK_SA ), 
      vec4( 0,                    BRICK_SA + BRICK_SB, BRICK_SA, BRICK_SC  ) , 
      vec4( BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA,   BRICK_SC, BRICK_SA ) ,  
      vec4( BRICK_SC + BRICK_SB, 0,  BRICK_SA, BRICK_SC ) 
	);

	const float CELLSIZEU  =  BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD  + 0.001;
	const float CELLSIZEV  =  BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD  + 0.001; 
	const float  DepthOfBrick = 0.04439; 

#elif BRICK_PATTERN == 3        // square cycle staggered

	const float SCL = 0.65;// scale to what looks pretty...
	const float BRICK_SA = 0.1 * SCL;
	const float BRICK_SB = 0.05* SCL;
	const float BRICK_SC = 0.25* SCL;
    const float BRICK_SD = 0.05* SCL; 

	const float staggerU = (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD);
	const float staggerV = (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD) * 0.5;

	const int maxLayers = 8;
	const vec4 shiftAndSizes[maxLayers] = vec4[]
	(
      vec4( 0, 0, BRICK_SC, BRICK_SA ), 
      vec4( 0, BRICK_SA + BRICK_SB, BRICK_SA, BRICK_SC  ) , 
      vec4( BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA,   BRICK_SC, BRICK_SA ) ,  
      vec4( BRICK_SC + BRICK_SB, 0,  BRICK_SA, BRICK_SC ),

      vec4( staggerU, staggerV, BRICK_SC, BRICK_SA   ), 
      vec4( staggerU, BRICK_SA + BRICK_SB + staggerV, BRICK_SA, BRICK_SC  ) , 
      vec4( staggerU + BRICK_SA + BRICK_SB,  BRICK_SA + BRICK_SB +  BRICK_SC - BRICK_SA + staggerV,   BRICK_SC, BRICK_SA  ) ,  
      vec4( staggerU + BRICK_SC + BRICK_SB, staggerV,  BRICK_SA, BRICK_SC) 
	);

	const float CELLSIZEU  =  2.0 * (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD)  + 0.001;
	const float CELLSIZEV  =  (BRICK_SA + BRICK_SB  +  BRICK_SC + BRICK_SD)  + 0.001;
	const float  DepthOfBrick = 0.035; 
 #endif


const float  y0 =  -0.43;  

const float  xLo =  - 1.2; 
const float  xHi =    1.56;
const float  zLo =    0.48;  //-2.0; 
const float  zHi =    2.22;


	// colors per face. For testing

const vec3 colr0[5] = vec3[]
(
    vec3(0,0,0),
    vec3(1,0.5,0),  
	vec3(0.2,0.3,0.4),  
	vec3(1,0,0),
	vec3(0.7,0.5,0.39)  // 4 is mortar 
); 
const vec3 colr1[5] = vec3[]
(
    vec3(0,0,0),
    vec3(0.85,0.5 ,0.44 ), 
    vec3(1,0.5,0),  
	vec3(0.8,0.5,0.2),
	vec3(0.7,0.5,0.39)  // 4 is mortar 
);   

float  RECIP_CELLSIZEU = 1.0 / CELLSIZEU;
float  RECIP_CELLSIZEV = 1.0 / CELLSIZEV;  

const float  y1 =  y0 - DepthOfBrick;

const vec4 boundsA = vec4(zLo, -zHi, xLo, -xHi );
const vec4 boundsB = vec4(-1.0, 1.0, -1.0, 1.0);

	// FPT limits, needed for ensuring numerical stability as ray axes approach alignment with the world space.
    //  https://en.wikipedia.org/wiki/IEEE_754-1985

const float SMALLEST_POSITIVE_IEEE754_FPT =  intBitsToFloat(1); // ±2^−23 × 2^−126  are closest representable numbers to 0
const float BIGGEST_IEEE754_FPT = 	intBitsToFloat(0x7F7FFFFF);  

// Fed up with typing  'clamp(..., 0.0, 1.0)'   when we know it will go to a saturate and
// probably get it for free on the GPU.

float saturate(float x)
{
    return clamp(x, 0.0, 1.0);
}
vec2 saturate(vec2 x)
{
    return clamp(x, 0.0, 1.0);
}
vec3 saturate(vec3 x)
{
    return clamp(x, 0.0, 1.0);
} 
vec4 saturate(vec4 x)
{
    return clamp(x, 0.0, 1.0);
}

float random2(vec2 st)
{
    // Quick random-ish used for determining a random scalar for a brick (for color, etc.)
    //  modded from hash function at:    https://www.shadertoy.com/view/XlXcW4
    //  ( taken one xor line,  good enough for this test! )

    uint  a = floatBitsToUint(st.x);
    uint  b = floatBitsToUint(st.y);

    float h = float(((a>>8U)^b)*1103515245U) / float(0xffffffffU); 
    return h;              
} 


int BrickWall( in vec3 ro, in vec3 rd,  out bool inShadow, out vec3 normal,  out vec2 idx,  inout vec3 posn)  
{  
    inShadow = false;
    normal = vec3(0.0, -1.0, 0.0);
    idx = vec2(0);
    posn = vec3(0); 

    float t0 =  ( -ro.y + y0) *  rd.y; 
    float t1 =  ( -ro.y + y1) *  rd.y;

    float sum = t0 + t1;
    t0 = min(t0,t1);

    if( t0 < 0.0 )     // early out if intersection is behind camera
        return 0;		

    t1 = sum - t0;  		
    vec3  rd2 = rd * rd;  
    vec3  intersect0 = ro * rd2.y + t0 * rd;

 	// NOTE:  if rd.y == 0  (ie. ray is coplanar, then  intersect0 == 0, and the bounds test passes, (if we had used the
    // nieve approach, then  intersect0 would approach infinity as rd.y approaches 0, because we would be using 1/rd.y - 
    // but we multiply up by rd.y^2  to avoid divisions AND maintain the inequality tests.
    //  (In the case of rd2.y very close to 0, then early out). 

    if( rd2.y <  SMALLEST_POSITIVE_IEEE754_FPT )   // consider ray as coplanar,  rd2.y = y^2 so always +ve 
        return 0;


    if( dot(saturate( boundsA * rd2.y + intersect0.zzxx * boundsB ), vec4(1.0)) !=  0.0 )
    {
         return 0;   
    }

    float bestFaceCode = 4.0;		// Default to Mortar

    float  recip_rd2y = 1.0 / rd2.y;    
    intersect0 *= recip_rd2y;    // We have already checked that rd2.y >=  SMALLEST_POSITIVE_IEEE754_FPT,  above

    // Early out on brick face, no need to test anything else if ray intersects the main face of a brick. 

    for( int layer = 0; layer < maxLayers; ++layer)
    {
        vec4 ss = shiftAndSizes[layer]; 

        vec2 fufv   = floor((intersect0.xz - ss.xy ) * vec2(RECIP_CELLSIZEU, RECIP_CELLSIZEV)); 
    	vec2 cellUV = fufv  * vec2(CELLSIZEU, CELLSIZEV) + ss.xy ; 

        vec2 a = saturate( intersect0.xz - cellUV.xy -  ss.zw );
        if ((a.x + a.y) == 0.0)  
    	{
       	 	 // brick face 
            idx =  fufv + float(layer) ; 
            posn = intersect0;
            return 3;
    	}
    }

    // Deal with sides and ends of brick.

    float rdxrdz = rd.x * rd.z;
    float rdxrdz2 = rdxrdz * rdxrdz; 
    vec2 C01 =  rdxrdz * rd.zx; 
    vec2  C4  = vec2(y0,y1) * rdxrdz2 ;  


    // +/- 1 depending on half-space for each axis X, Z

    vec2 hsPlusMinus =  vec2 (  ( rd.x >= 0.0 ) ? 1.0 : -1.0 ,  ( rd.z >= 0.0 ) ? 1.0 : -1.0 );    

    float bestT = BIGGEST_IEEE754_FPT;  

    vec2  F01 = vec2(CELLSIZEU, CELLSIZEV) * C01 ; 
    vec2  D01 = -(vec2(CELLSIZEU,CELLSIZEV) + ro.xz ) * C01; 

    float bestSy = 0.0;
    vec2  fl2 =  vec2(CELLSIZEU, CELLSIZEV); 

    float S1 =  ro.y * rdxrdz2;
    vec4  vecE0E1 = vec4( 0.0, 1.0,  0.0, 1.0); 
    if( C01.x < 0.0 )
        vecE0E1.xy = vec2(1.0, 0.0);
    if( C01.y < 0.0 )
        vecE0E1.zw = vec2(1.0, 0.0); 

    vec4 C5 = vecE0E1 * C01.xxyy;

    for( int layer = 0; layer < maxLayers; ++layer)
    {
        vec4  ss = shiftAndSizes[layer]; 

        vec4 sortOffset = C5 * ss.zzww; 

        vec2 fufv = floor((intersect0.xz - ss.xy ) * vec2(RECIP_CELLSIZEU, RECIP_CELLSIZEV));  

    	vec2 cellUV = fufv * vec2(CELLSIZEU, CELLSIZEV) + ss.xy ;

        vec2 currentIdx =  fufv  +  fl2 ;  
        float dIdxV = currentIdx.y;

        vec2 dudvYstart = cellUV.y * C01.y + D01.y + sortOffset.zw; 

        vec4 t;
        t.xy = cellUV.x * C01.x + D01.x  + sortOffset.xy; 
        for( int u = 0; u < 3; u++ )
        {  
            currentIdx.y = dIdxV ;
            currentIdx.x++; 
            t.zw  = dudvYstart;       
            for(int v = 0; v  < 3; v++ )
            {
                //  s is an associated value for tv,  so that we know after ordering  tv, whether a line segment
                //  crosses / intersects a cell   ( when s.x != s.y ).

                currentIdx.y++;

        		vec4 tv = t;
                vec3 s =  vec3(0.0,0.0,1.0);

                if(tv.x > tv.z) { tv.z = tv.x; s.xz = vec2(1.0, 0.0);  }  
   				if(tv.y > tv.w) { tv.y = tv.w; s.y = 1.0;   } 
   				if(tv.y > tv.z) { tv.y = tv.z; s.y = s.z;  }  

    			float ds = s.x - s.y;
                float p =  S1 + tv.y * rd.y;  

    			if( (  
                     saturate(1.0 - ds*ds) + 
                     saturate( tv.y - bestT) +  
                     saturate( p  - C4.x ) +  
                     saturate( C4.y - p  )) == 0.0)
            		{  
                  		bestT = tv.y; 
                        bestSy =  s.y; 
                        idx =  currentIdx;    
            		}

                 t.zw += F01.y;
            }
            t.xy += F01.x; 
        }
        fl2 += float(maxLayers); 
    } 

    if( bestT != BIGGEST_IEEE754_FPT )
    {
		normal = vec3( hsPlusMinus.x * (1.0 -bestSy), 0.0, hsPlusMinus.y * bestSy  ) ;  
        bestFaceCode = bestSy + 1.0;  
    }

    // Trim edge    

   if (bestFaceCode != 4.0)
   {  
        rd2.y = rdxrdz2;
        t1 = bestT; 
   }

   vec3  coord = ro  * rd2.y + t1 * rd;  // ray intersection point multiplied by rd2.y,  ie. ray-direction 'y' squared

   if( dot(saturate( boundsA * rd2.y + coord.zzxx * boundsB ), vec4(1.0)) !=  0.0 )
   {
        return 0;   
   } 

   #if defined(USE_SHADOW)

    // If light source is from behind wall,  or behind a brick end or side, then always in shadow..

    bool  shadow = ((saturate( LIGHT_DIRECTION.y )  + saturate( -dot( normal.xz, LIGHT_DIRECTION.xz ))  ) != 0.0);

     // Otherwise examine adjacent cells for projecting the eye ray intersection point along the reverse light direction
     // (Since all edges are vertical in the plane, we only need to detect the XZ ray crossing the brick area of a cell.

    if( !shadow)
    { 
         float t0 =  ( y0 * rd2.y -coord.y ) ; 
         intersect0.xz = coord.xz - t0 * LIGHT_DIRECTION.xz; 

         float rdxrdz = LIGHT_DIRECTION.x * LIGHT_DIRECTION.z; 
    	 vec2  C01    =  -rdxrdz * vec2( LIGHT_DIRECTION.z, LIGHT_DIRECTION.x); 
         float rdxrdz2   = rdxrdz * rdxrdz * rd2.y; 
         float G1 = rdxrdz2 * rd2.y;
    	 vec2  C4  = vec2(y0,y1) * G1 ; 

   	     vec2 hsPlusMinus =  vec2 (  ( LIGHT_DIRECTION.x <= 0.0 ) ? 1.0 : -1.0 ,  ( LIGHT_DIRECTION.z <= 0.0 ) ? 1.0 : -1.0 );   

    	 vec2  D01 = -(vec2(CELLSIZEU, CELLSIZEV)  * rd2.y + coord.xz) * C01; 
         vec3  G0 = -LIGHT_DIRECTION * rd2.y;   //-ve to allow + in loop,  easier to mad
         vec4  S0 = boundsA * G1;
         vec3  S1 = coord * rdxrdz2;

        vec4  vecE0E1 = vec4( 0.0, 1.0,  0.0, 1.0); 
    	if( C01.x < 0.0 )
        	vecE0E1.xy = vec2(1.0, 0.0);
    	if( C01.y < 0.0 )
        	vecE0E1.zw = vec2(1.0, 0.0); 

         vec2  C23 = C01  * rd2.y;  
    	 vec2  F10 = vec2(CELLSIZEU, CELLSIZEV) * C23 ;  
         vec4  C5 = C23.xxyy * vecE0E1; 

         intersect0 /= rd2.y;
    	 for( int layer = 0; layer < maxLayers; ++layer)
    	 {
        	vec4 ss = shiftAndSizes[layer];

            vec4 sortOffset = C5 * ss.zzww;
    		float fu = (intersect0.x - ss.x ) * RECIP_CELLSIZEU;
    		float fv = (intersect0.z - ss.y ) * RECIP_CELLSIZEV; 

    		float cellU = floor(fu ) * CELLSIZEU + ss.x ;
    		float cellV = floor(fv ) * CELLSIZEV + ss.y ; 

            vec2 dudvYstart = cellV * C23.y + D01.y + sortOffset.zw;

            vec4 t ; 
            t.xy =  cellU * C23.x + D01.x  + sortOffset.xy;
        	for( int u = 0; u < 3; u++ )
        	{   
                t.zw  = dudvYstart; 
               	for(int v = 0; v  < 3; v++ )
            	{  
        			vec4 tv = t;  //  tv.x <=  tv.y,    tv.z <= tv.w
                	vec3 s =  vec3(0.0,0.0,1.0);

                	if(tv.x > tv.z) { tv.z = tv.x; s.xz = vec2(1.0, 0.0);  }  
   					if(tv.y > tv.w) { tv.y = tv.w; s.y = 1.0;   } 
   					if(tv.y > tv.z) { tv.y = tv.z; s.y = s.z;  }    

                    float ds = s.x - s.y;
                    vec3  p  = S1 + tv.y * G0;   
                    vec4  a  = saturate( S0 + p.zzxx * boundsB ); 

                    // could break out as soon as shadow is true.  probably worth doing (wavefronts ? )

                    shadow = shadow ||
    				   ( 
                         (saturate(1.0 - ds*ds) + 
                          saturate( -tv.y ) +  
       				      saturate(  p.y - C4.x ) +
                          saturate(  C4.y - p.y ) +
                          a.x + a.y + a.z + a.w               // using dot(a,vec4(1.0)) is not good on GCN chips.
                         ) == 0.0
                       ) ;

                	t.zw += F10.y;
            	}   
            	t.xy += F10.x;
        	} 
    	}  
    } 
    inShadow = shadow;

   #endif

   posn = coord / rd2.y;

   return int(bestFaceCode);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (-1.0 + 2.0*fragCoord.xy / iResolution.xy) *  vec2(iResolution.x/iResolution.y, 1.0);

	vec3 ro;
    ro.y = -0.2;

    vec2 maxSpan = vec2( xHi - xLo, zHi - zLo ) * 0.4; 

    float pt = iTime;
    ro.x = xLo + (  xHi - xLo) * 0.5 + sin(pt * 0.2) * maxSpan.x;
    ro.z = zLo + (  zHi - zLo) * 0.5 + cos(pt * 0.2) * maxSpan.y;
    float theta = 1.4 + iMouse.x * 0.01;
    float c = cos(theta);
    float s = sin(theta);
    mat2 rotn;
    rotn[0][0] = c;
    rotn[0][1] = -s;
    rotn[1][0] =  s;
    rotn[1][1] = c;

    vec3 dirn = vec3(uv,1.0) ;
    dirn.xz *= rotn; 

    	// Now do something more interesting....
    bool inShadow = false;
    vec3 normal = vec3(0); 
    vec3 posn = vec3(0); 

    vec2 idx;   

    int faceCode = BrickWall( ro, normalize(dirn), inShadow, normal,  idx, posn);   

    float idCoeff = random2( idx.xy *  1.2341 ); 

    	// Apply the color,  simple dp lighting and darken if the point is in shadow,

    vec4  faceColor  =  vec4(  mix(colr0[faceCode], colr1[faceCode],idCoeff) , 1.0 );  

    if( faceCode != 0)		// Do not light if not on wall surface 
    {
   	 	float intensity =  mix( LIGHT_AMBIENT_INTENSITY, 1.0, clamp(dot(normal, LIGHT_DIRECTION), 0.0, 1.0) ) ;
   	 	faceColor.xyz *= intensity;
    	if(inShadow)
    	{
       	 	faceColor.xyz *= 0.6;   
   		} 
    }  

    fragColor = faceColor; 

}

// Original source:  https://www.shadertoy.com/view/lsfXWH
// 	--Created by inigo quilez - iq/2013
