// Based on Spherical harmonics solver by TimoKinnunen
//  --https://www.shadertoy.com/view/3dKGWG
// 
// Changes: 
//  - Turned back to the simplest naive equation, but surprisingly effective 
//  - Used approximated factorial to evaluate ALP
//  - Support at most band 64 for full band visualisation (l=64, m=[-64, 64])
//	          at most band 128, order 0 for highest record.
//    Seems factorial died after around 128!, probably caused by invalid exp()

precision highp float;

// band
#define L 32

// order
#define M 32

#if HW_PERFORMANCE==0
#define AA 1
#else
#define AA 2   // antialias level (try 1, 2, 3, ...)
#endif

#define AUTOMATIC
//#define SHOW_SPHERES

//---------------------------------------------------------------------------------

// control step length of marching
#define MARCH_STEP				0.05
// control maximum marching depth
#define MARCH_MAX_ITERATION     600

// upper & lower bounds used by factorial approximation
#define a0 0.833333333333333333   //            1 / 12
#define a1 0.033333333333333333   //            1 / 30
#define a2 0.252380952380952381   //           53 / 210
#define a3 0.525606469002695418   //          195 / 371
#define a4 1.011523068126841712   //        22999 / 22737
#define a5 1.517473649153287398   //     29944523 / 19733142
#define a6 2.269488974204959961   // 109535241009 / 48264275462
#define SQRT2 1.4142135623 		  // sqrt(2)
#define INV_4PI 0.0795774715 	  // 1 / (4 * pi)
#define LOG_2PI 1.8378770664      // log(2 * pi)

// look up table for first few factorials due to 
// limited precision of approximated factorial when input x is small
const float factorialLUT[11] = float[11](
	1.0,
    1.0,
    2.0,
    6.0,
    24.0,
    120.0,
    720.0,
    5040.0,
    40320.0,
    362880.0,
    3628800.0
);

// Stieltjes's continued fraction approximation based on: 
// 		http://www.luschny.de/math/factorial/approx/SimpleCases.html
float FactorialStieltjesNoExp(in int x)
{
	if(x < 11) { return log(factorialLUT[x]); }
	float  Z = float(x) + 1.0;
	return 0.5 * LOG_2PI + (Z - 0.5) * log(Z) - Z + 
		a0/(Z + a1/(Z + a2/(Z + a3/(Z + a4/(Z + a5/(Z + a6/Z))))));
}

// K(l, m) * P(l, m)
// Based on Spherical Harmonic Lighting: The Gritty Details by Robin Green, 2003
//    http://silviojemma.com/public/papers/lighting/spherical-harmonic-lighting.pdf
float KP(in int l, in int m, in float x)
{
	float f = 0.0;
	if(m > 0)
	{
        float t1 = FactorialStieltjesNoExp(l - m);
        float t2 = FactorialStieltjesNoExp(2 * m - 1);
        float t3 = FactorialStieltjesNoExp(l + m);
        float t4 = FactorialStieltjesNoExp(m - 1);

        f = pow(2.0, float(2 - 2 * m)) * exp(t1 + 2.0 * t2 - t3 - 2.0 * t4);
	}
	else
	{
		f = 1.0;
	}

	// kp <=> K(l, m) * (2m - 1)!!
	float kp = sqrt((2.0 * float(l) + 1.0) * INV_4PI * f); 
	// (-1)^(m)
	float fact = 1.0 - 2.0 * mod(float(m), 2.0);
	float Pmm = fact * kp * pow(1.0 - x * x, float(m) / 2.0);

	if(l == m){ return Pmm; }

	float Pmmp1 = x * (2.0 * float(m) + 1.0) * Pmm;
	if(l == m+1) { return float(Pmmp1); }

	float Pml = 0.0;
	for (int ll = m+2; ll <= l; ++ll)
	{ 
		Pml = ( (2.0 * float(ll) - 1.0) * x * Pmmp1 - (float(ll) + float(m) - 1.0) * Pmm ) / float(ll - m);
		Pmm = Pmmp1;
		Pmmp1 = Pml;
	}
	return Pml;
}

// Y_l_m(s), where l is the band and m the range in [-l..l] 
float SHNaive(in int l, in int m, in vec3 s)
{
	float phi = atan(s.z, s.x);

	if(m == 0)
	{
		return KP(l, 0, s.y);
	}
	else if(m > 0)
	{
        //s.y <=> cos(theta)
		return SQRT2 * cos(float(m) * phi)  * KP(l, m, s.y);
	}
	else
	{
		return SQRT2 * sin(-float(m) * phi) * KP(l, -m, s.y);
	}
}

vec3 map( in vec3 p )
{
    vec3 p00 = p;

	float r, d; vec3 n, s, res;

    #ifdef SHOW_SPHERES
	#define SHAPE (vec3(d-0.35, -1.0+2.0*clamp(0.5 + 16.0*r,0.0,1.0),d))
	#else
	#define SHAPE (vec3(d-abs(r), sign(r),d))
	#endif

    int l = L;
    int m = M;
    #ifdef AUTOMATIC
    int x = ((iFrame>>1)&0x7FF)/15;
    int Y = int(floor(sqrt(0.25+float(2*x))-0.5));
    int X = int(floor(float(x)-0.5*float(Y+Y*Y)));
    l = Y+1;
    m = X+1;
    #endif
	d=length(p00); n=p00/d; r = SHNaive(l, m, n ); s = SHAPE; res = s;

	return vec3( res.x, 0.5+0.5*res.y, res.z );
}

vec3 intersect( in vec3 ro, in vec3 rd )
{
	vec3 res = vec3(1e10,-1.0, 1.0);

	float maxd = 10.0;
    float h = 1.0;
    float t = 1.0;
    vec2  m = vec2(-1.0);
    for( int i=0; i<MARCH_MAX_ITERATION; i++ )
    {
        if( h<0.001||t>maxd ) break;
        vec3 p = ro+rd*t ;
	    vec3 res = map(p);
        h = res.x;
		m = res.yz;
        t += h*MARCH_STEP;
    }
	if( t<maxd && t<res.x ) res=vec3(t,m);


	return res;
}

vec3 calcNormal( in vec3 pos )
{
    vec3 eps = vec3(0.001,0.0,0.0);

	return normalize( vec3(
           map(pos+eps.xyy).x - map(pos-eps.xyy).x,
           map(pos+eps.yxy).x - map(pos-eps.yxy).x,
           map(pos+eps.yyx).x - map(pos-eps.yyx).x ) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // camera
    float an = 10.0*iMouse.x/iResolution.x;
    float am = 2.*iMouse.y/iResolution.y - 1.;
    float turn = radians(360./256.)*float(iFrame&0xFF);
    vec3  ro = vec3(2.0*sin(an+turn),2.0*sin(am),2.0*cos(an+turn)) * 1.0;
    vec3  ta = vec3(0.0,0.0,0.0);

    // camera matrix
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,1.0,0.0) ) );
    vec3 vv = normalize( cross(uu,ww));

    vec3 tot = vec3(0.0);

    #define ZERO min(iFrame,0)
    for( int m=ZERO; m<AA; m++ )
    for( int n=ZERO; n<AA; n++ )
    {        
        vec2 p = (-iResolution.xy + 2.0*(fragCoord+vec2(float(m),float(n))/float(AA))) / iResolution.y;

        // create view ray
        vec3 rd = normalize( p.x*uu + p.y*vv + 2.0*ww );

        // background 
        vec3 col = vec3(0.3) * clamp(1.0-length(p)*0.5,0.0,1.0);

        // raymarch
        vec3 tmat = intersect(ro,rd);
        if( tmat.y>-0.5 )
        {
            // geometry
            vec3 pos = ro + tmat.x*rd;
            vec3 nor = calcNormal(pos);
            vec3 ref = reflect( rd, nor );

            // material		
            vec3 mate = 0.5*mix( vec3(1.0,0.6,0.15), vec3(0.2,0.4,0.5), tmat.y );

            float occ = clamp( 2.0*tmat.z, 0.0, 1.0 );
            float sss = pow( clamp( 1.0 + dot(nor,rd), 0.0, 1.0 ), 1.0 );

            // lights
            vec3 lin  = 2.5*occ*vec3(1.0,1.00,1.00)*(0.6+0.4*nor.y);
                 lin += 1.0*sss*vec3(1.0,0.95,0.70)*occ;		

            // surface-light interacion
            col = mate.xyz * lin;
        }

        // gamma
        col = pow( clamp(col,0.0,1.0), vec3(0.4545) );
        tot += col;
    }
    tot /= float(AA*AA);
    fragColor = vec4( tot, 1.0 );
}

