float gamma = 2.2;

vec3 getBaseColor(int i)
{
	if (i == 0) return vec3(1.0, 0.4, 0.0);
	if (i == 1) return vec3(0.4, 1.0, 0.0);
	if (i == 2) return vec3(0.0, 1.0, 0.4);
	if (i == 3) return vec3(0.0, 0.4, 1.0);
	if (i == 4) return vec3(0.4, 0.0, 1.0);
	if (i == 5) return vec3(1.0, 0.0, 0.4);

	return vec3(1.);
}

vec3 getBaseColor()
{
	float colorPerSecond = 0.5;
	int i = int(mod(colorPerSecond * iTime, 7.));
	int j = int(mod(float(i) + 1., 7.));

	return mix(getBaseColor(i), getBaseColor(j), fract(colorPerSecond * iTime));
}

vec3 linearToneMapping(vec3 color)
{
	float exposure = 1.;
	color = clamp(exposure * color, 0., 1.);
	color = pow(color, vec3(1. / gamma));
	return color;
}

vec3 simpleReinhardToneMapping(vec3 color)
{
	float exposure = 1.5;
	color *= exposure/(1. + color / exposure);
	color = pow(color, vec3(1. / gamma));
	return color;
}

vec3 lumaBasedReinhardToneMapping(vec3 color)
{
	float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
	float toneMappedLuma = luma / (1. + luma);
	color *= toneMappedLuma / luma;
	color = pow(color, vec3(1. / gamma));
	return color;
}

vec3 whitePreservingLumaBasedReinhardToneMapping(vec3 color)
{
	float white = 2.;
	float luma = dot(color, vec3(0.2126, 0.7152, 0.0722));
	float toneMappedLuma = luma * (1. + luma / (white*white)) / (1. + luma);
	color *= toneMappedLuma / luma;
	color = pow(color, vec3(1. / gamma));
	return color;
}

vec3 RomBinDaHouseToneMapping(vec3 color)
{
    color = exp( -1.0 / ( 2.72*color + 0.15 ) );
	color = pow(color, vec3(1. / gamma));
	return color;
}

vec3 filmicToneMapping(vec3 color)
{
	color = max(vec3(0.), color - vec3(0.004));
	color = (color * (6.2 * color + .5)) / (color * (6.2 * color + 1.7) + 0.06);
	return color;
}

const mat3x3 ACESInputMat = mat3x3
(
    0.59719, 0.35458, 0.04823,
    0.07600, 0.90834, 0.01566,
    0.02840, 0.13383, 0.83777
);

// ODT_SAT => XYZ => D60_2_D65 => sRGB
const mat3x3 ACESOutputMat = mat3x3
(
     1.60475, -0.53108, -0.07367,
    -0.10208,  1.10813, -0.00605,
    -0.00327, -0.07276,  1.07602
);

vec3 RRTAndODTFit(vec3 v)
{
    vec3 a = v * (v + 0.0245786f) - 0.000090537f;
    vec3 b = v * (0.983729f * v + 0.4329510f) + 0.238081f;
    return a / b;
}

vec3 ACESFitted(vec3 color)
{
    color = color * ACESInputMat;

    // Apply RRT and ODT
    color = RRTAndODTFit(color);

    color = color * ACESOutputMat;

    // Clamp to [0, 1]
  	color = clamp(color, 0.0, 1.0);

    color = pow(color, vec3(1. / gamma));

    return color;
}

vec3 Uncharted2ToneMapping(vec3 color)
{
	float A = 0.15;
	float B = 0.50;
	float C = 0.10;
	float D = 0.20;
	float E = 0.02;
	float F = 0.30;
	float W = 11.2;
	float exposure = 2.;
	color *= exposure;
	color = ((color * (A * color + C * B) + D * E) / (color * (A * color + B) + D * F)) - E / F;
	float white = ((W * (A * W + C * B) + D * E) / (W * (A * W + B) + D * F)) - E / F;
	color /= white;
	color = pow(color, vec3(1. / gamma));
	return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec3 color = getBaseColor();

	float n = 8.;
	if (uv.x > 0.2)
	{
		if (uv.x < 0.6)
			// blacks
			color *= (2.5 * uv.x - 0.5);
		else
			// whites
			color *= 15.*(2.5 * uv.x - 1.5) + 1.;

		int i = int(n * (1. - uv.y));
		if (i == 1) color = linearToneMapping(color);
		if (i == 2) color = simpleReinhardToneMapping(color);
		if (i == 3) color = lumaBasedReinhardToneMapping(color);
		if (i == 4) color = whitePreservingLumaBasedReinhardToneMapping(color);
		if (i == 5) color = RomBinDaHouseToneMapping(color);		
		if (i == 6) color = ACESFitted(color);
		if (i == 7) color = Uncharted2ToneMapping(color);
	}

	if (abs(fract(n * uv.y + 0.5) - 0.5) < 0.02)
		color = vec3(0.);

	fragColor = vec4(color, 1.);
}

#define S(a, b, t) smoothstep(a,b,t)

float TaperBox(vec2 p, float wb, float wt, float yb, float yt, float blur) {
    float m = S(-blur, blur, p.y - yb);
    m *= S(blur, -blur, p.y - yt);

    p.x = abs(p.x);

    float w = mix(wb, wt, (p.y - yb) / (yt - yb));
    m *= S(blur, -blur, p.x - w);
    return m;
}

vec4 Tree(vec2 uv, vec3 col, float blur) {
    float m = TaperBox(uv, .03, .03, -.05, .25, blur);
    m += TaperBox(uv, .2, .1, .25, .5, blur);
    m += TaperBox(uv, .15, .05, .5, .75, blur);
    m += TaperBox(uv, .1, .0, .75, 1., blur);

    float shadow = TaperBox(uv - vec2(.2, 0), .1, .5, .15, .25, blur);
    shadow += TaperBox(uv + vec2(.25, 0), .1, .5, .45, .5, blur);
    shadow += TaperBox(uv - vec2(.25, 0), .1, .5, .7, .75, blur);

    col -= shadow * .8;
    return vec4(col, m);
}

float GetHeight(float x) {
    return sin(x * .423) + sin(x) * .3;
}

vec4 Layer(vec2 uv, float blur) {
    vec4 col = vec4(0);
    float id = floor(uv.x);
    float n = fract(sin(id * 234.12) * 5463.3) * 2. - 1.;
    float x = n*.3;
    float y = GetHeight(uv.x);

    float ground = S(blur, -blur, uv.y + y);
    col += ground;

    y = GetHeight(id + .5 + x);

    uv.x = fract(uv.x) - .5;
    vec4 tree = Tree((uv - vec2(x, -y)) * vec2(1., 1. + n * .2), vec3(1), blur);

    // col.rg = uv;

    col = mix(col, tree, tree.a);
    col.a = max(ground, tree.a);

    return col;
}

float Hash21(vec2 p) {
    p = fract(p * vec2(234.45, 765.34));
    p += dot(p, p + 547.123);
    return fract(p.x * p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 M = (iMouse.xy / iResolution.xy) * 2. - 1.;

	float t = iTime * .3;

    float blur = .005;

    float twinkle = dot(length(sin(uv + t)), length(cos(uv * vec2(22, 6.7) - t * 3.)));
    twinkle = sin(twinkle * 10.) * .5 + .5;
    float stars = pow(Hash21(uv), 100.) * twinkle;
    vec4 col = vec4(stars);

    float moon = S(.01, -.01, length(uv-vec2(.4, .2)) - .15);
    col *= 1. - moon;
    moon *= S(-.01, .1, length(uv-vec2(.5, .25)) - .15);
    col += moon;

    vec4 layer;

    for(float i = 0.; i < 1.; i += 1./10.) {
        float scale = mix(30., 1., i);
        //blur = mix(.1, .005, i);
        layer = Layer(uv * scale + vec2(t + i * 100., i) - M, blur);
        layer.rgb *= (1. - i) * vec3(.9, .9, 1.);

        col = mix(col, layer, layer.a);
    }

    layer = Layer(uv + vec2(t, 1.) - M, .07);
    col = mix(col, layer * .1, layer.a);

    float thickness = 1./iResolution.y;
    // if (abs(uv.x)<thickness) col.g = 1.;
    // if (abs(uv.y)<thickness) col.r = 1.;

    fragColor = col;
}

