 float jsoftshadow( in vec3 light, in vec3 pos, float w )
 {
     float dist = distance(light, pos);
     vec3 dir = normalize(pos - light);

     float s = 1.0;
     for( float t=.1; t<dist-.1; )
     {
         float h = scene(light + dir*t);
         s = min( s, 0.5+0.5*h/(w*t) );
         if( s<0.0 ) break;
         t += clamp(h, 0.01, 0.50);
     }
     s = max(s,0.0);

     return s*s*(3.0-2.0*s); // smoothstep
 }



vec3 lightOrigin = vec3(-.3, .6, -.3);



vec3 trace(vec3 camOrigin, vec3 dir, out float totalDist) {
 	const int maxSteps = 1024;
    vec3 ray = camOrigin;
    totalDist = 0.0;

    // hacky near plane clipping
    totalDist += .1;
    ray += totalDist * dir;

    for(int i = 0; i < maxSteps; i++) {
     	float dist = scene(ray);
        if (abs(dist) < .001) {
            vec3 lightDir = normalize(ray - lightOrigin);
            // this is not how falloff works!
            float lightFalloff = 2.0 - pow(length(ray - lightOrigin) * .5, 2.);
            float diffuse = clamp(dot(lightDir, estimateNormal(ray))* lightFalloff, 0., 1.) ;

            float s = jsoftshadow(lightOrigin, ray, .02);
            diffuse *= s;

            vec3 ambient = vec3(.1, .1, .2);
            return vec3(1.0, .8, .6)*diffuse + ambient;

        }
        totalDist += dist;
        ray += dist * dir;
    }

    return vec3(0.0);
}

