float map(vec3 p) 
{


  float d;

   	d=999.9;


   //p.x =mod(p.x,20.0)-15.0;

   float sdt1= sdTorus(p.yxz- vec3(0.0,3.0,4.5), vec2(4.5,1) );
   float sdt2= sdTorus(p.xzy- vec3(-5.5,3.0,3.5), vec2(8.5,1) );

   float sdb1= sdBox(  p-vec3(2.5,0.0,3.0), vec3(2.0,7.0,1.0) );
   float sdb2= sdBox(  p-vec3(-13.5,0.0,3.0), vec3(1.5,7.0,1.0) );

   float sdb3= sdBox(  p-vec3(-5.0,-5.0,3.0), vec3(10.0,12.0,2.5) ); 
   float diff1= differenceSDF(sdt2,sdb3);


   d=unionSDF(d, diff1);

   d=unionSDF(d, sdb1 );
   d=unionSDF(d, sdb2 );


  float s = 1.0;
  for (int m = 0; m < 2; m++) {
    vec3 a = mod(p * s, 2.0) - 1.0;
    s *= 3.0;
    vec3 r = 1.0 - 3.0 * abs(a);
    float c = sdCross(r) / s;
    d = max(d, c);
  }

  return d;

}
