vec2 RayMarch (vec3 ro, vec3 rd) 
 {
 vec2 h, t=vec2( 0.);
 for (int i=0; i<MAX_STEPS; i++) 
  {
 h = GetDist(ro + t.x * rd);
 if(h.x<SURF_DIST||t.x>MAX_DIST) break;
  t.x+=h.x*1.;
  t.y=h.y;
 }
 if(t.x>100.) t.x=0.;
 return t;
}

vec3 GetNormal(vec3 p){
vec2 e = vec2(.00035, -.00035); 
return normalize(
 e.xyy * GetDist(p + e.xyy).x + 
 e.yyx * GetDist(p + e.yyx).x + 
 e.yxy * GetDist(p + e.yxy).x + 
 e.xxx * GetDist(p + e.xxx).x);
}


float GetLight(vec3 p) {
// vec3 lightPos = vec3(sin(iTime)*2., cos(iTime)*2., 3);
     vec3 lightPos = vec3(0.,0.,5);
 vec3 l = normalize(p-lightPos);
 vec3 n = GetNormal(p);
 float dif = clamp(dot(n, l)*.5+.5, 0., 1.);
 vec2 d = RayMarch(p+n*SURF_DIST*1., l);
 return dif;
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
 vec3 f = normalize(l-p),
 r = normalize(cross(vec3(0,1,0), f)),
 u = cross(f,r),
 c = p+f*z,
 i = c + uv.x*r + uv.y*u,
 d = normalize(i-p);
 return d;
}



