float Circle(vec2 uv, vec2 p, float r, float blur)
{
    float d = length(uv-p);
    float c = smoothstep(r, r-blur, d);

    return c;
}

float BandEqual(float b, float w, float v, float blur)
{
    float band = smoothstep((b-w)+blur, b-blur, v);
    band *= 1.-smoothstep(b+blur, (b+w)-blur, v);
    return band;
}

float Band(float b, float e, float v, float blur)
{
    float band = smoothstep(b-blur, b, v);
    band *= smoothstep(e+blur, e, v);
    return band;
}

float SmileyFace(vec2 uv, vec2 p, float size)
{
    uv -= p; //  remaps the point you want it to be at to 0.
    		 //  translation of the coordinate system
    uv /= size; //  scaling coordinate system

    float mask = Circle(uv, vec2(0.), 0.4, 0.05);

    mask -= Circle(uv, vec2(-.15,0.15), 0.07, 0.01);
    mask -= Circle(uv, vec2(.15,0.15), 0.07, 0.01);

    float mouth = Circle(uv, vec2(0.), 0.3, 0.02);
    mouth *= 1.- Circle(uv, vec2(0., 0.05), 0.3, 0.02);

    mask *= 1. - clamp(mouth, 0., 1.);
    return mask;
}

float Square(vec2 uv, float size, vec2 p, float blur)
{
    float mask = BandEqual(p.x, size, uv.x, blur);
    mask *= BandEqual(p.y, size, uv.y, blur); 

    return mask;
}

float Rect(float b1, float e1, float b2, float e2, float blur, vec2 uv)
{
    float mask = Band(b1, e1, uv.x, blur);
    mask *= Band(b2, e2, uv.y, blur);
    return mask;
}

