vec2 Rotate2D(vec2 UV, float Angle) {

	return vec2((UV.x * cos(Angle)) - (UV.y * sin(Angle)), (UV.x * sin(Angle)) + (UV.y * cos(Angle)));    

}

vec3 HLine(vec2 UV, vec2 Position, float Width) {

	float LeftRight = step((0.5 - (Width * 0.1)) + (-0.5 + Position.x), UV.x) * step((0.5 - (Width * 0.1)) - (-0.5 + Position.x), 1.0 - UV.x);
    float TopBottom = step(Position.y, UV.y) * step(Position.y, 1.0 - UV.y);

    return vec3(LeftRight * TopBottom);

}

vec3 VLine(vec2 UV, vec2 Position, float Width) {

	float LeftRight = step(Position.x, UV.x)* step(Position.x, 1.0 - UV.x);
    float TopBottom = step((0.5 - (Width * 0.1)) + (-0.5 + Position.y), UV.y) * step((0.5 - (Width * 0.1)) - (-0.5 + Position.y), 1.0 - UV.y);

    return vec3(LeftRight * TopBottom);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 UV = fragCoord.xy / iResolution.xy;

    UV.x -= cos(iTime) * 0.05;
    UV.y += sin(iTime) * 0.05;

    UV.x -= 0.5;
    UV.y -= 0.5;
    UV /= 1.25;
    UV.x += 0.5;
    UV.y += 0.5;

    UV.x += sin((UV.y * 3.141592) + iTime) * 0.025;
    UV.y += sin((UV.x * 3.141592) + iTime) * 0.025;

    vec3 LineA = HLine(UV, vec2(0.1, 0.0), 0.25);
    vec3 LineB = VLine(UV, vec2(0.0, 0.1), 0.25);
    vec3 LineC = VLine(UV, vec2(0.0, 0.3), 0.25);
    vec3 LineD = VLine(UV, vec2(0.0, 0.8), 0.25);
    vec3 LineE = HLine(UV, vec2(0.4, 0.0), 0.25);
    vec3 LineF = HLine(UV, vec2(0.9, 0.0), 0.25);

    vec3 Lines = 1.0 - (LineA + LineB + LineC + LineD + LineE + LineF);

    Lines *= (step(0.5, UV.x) * vec3(0.25, 0.5, 1.0)) + (step(0.5, 1.0 - UV.x) * vec3(0.0, 0.0, 1.0));

    fragColor = vec4(Lines, 1.0);

}

