    float dif2;
    float dif3;
    vec3 p;
    p=point; 

    d=999.9;
    float planeDist = p.y;


    vec2 res;
    res = vec2(9999, 0);

    res = opU(res, vec2(planeDist, 6));


    float bxInf = sdBox (p-vec3(0.0,1,4.5),vec3(1.65,0.1,0.5));
    float bxDer = sdBox (p-vec3(-1.6,1.9,4.5),vec3(0.1,1.0,0.5));
    float bxIzq = sdBox (p-vec3(1.6,1.9,4.5),vec3(0.1,1.0,0.5));
    float bxSup = sdBox (p-vec3(-0.0,3.0,4.5),vec3(2.5,0.1,0.5));
    float bxAtras = sdBox(p-vec3(-0.1,2.1,5.0),vec3(1.65,1.0,0.1));

    res = opU(res, vec2(bxInf, CInf));


    float bxAtrasVentana = sdBox (p-vec3(-0.5,2.1,5.0),vec3(0.5,0.5,0.4));
    float bxAtrasPuerta = sdBox (p-vec3(0.8,1.8,5.0),vec3(0.45,0.8,0.4));
    float bxSupVentana = sdBox (p-vec3(0.0,3.0,4.5),vec3(0.5,0.21,0.4));
    float bxIzqVentana = sdBox (p-vec3(1.6,1.9,4.5),vec3(0.11,0.30,0.25));
    float bxDerVentana = sdBox (p-vec3(-1.6,1.9,4.5),vec3(0.11,0.55,0.25));


    dif2=differenceSDF(bxIzq,bxIzqVentana);     
    res = opU(res, vec2(dif2, CIzq));


    dif1 = differenceSDF(bxAtras,bxAtrasVentana);
    dif1= differenceSDF(dif1,bxAtrasPuerta);
    res = opU(res, vec2(dif1, CAtras));   

    dif2=differenceSDF(bxSup,bxSupVentana);     
    res = opU(res, vec2(dif2, CSup));

    dif3=differenceSDF(bxDer,bxDerVentana);     
    res = opU(res, vec2(dif3, CDer));


    d = res.x;
    mObj.dist = res.x;
    mObj.tipo = res.y;

    return d;
}

vec4 castRay( vec3 ro,  vec3 rd) {
  float t = 1.0;
  float tmax = 50.0;

  float precis = 0.002;
  for(int i = 0; i < 50; i++) {
    float dist = mapDistance(ro + rd * t);
    if(dist < precis || t > tmax)
      break;
    t += dist;
  }

    vec3 m =vec3(1.0);
  //vec3 m = t > tmax ? vec3(-1.0) : mapDistance(ro + rd * t).yzw;
  return vec4(t, m);
}

float softshadow( vec3 ro,  vec3 rd) {
  float t = 0.02;
  float tmax = 2.5;
  float precis = 0.001;
  float res = 1.0;
  for(int i = 0; i < 32; i++) {
    float h = mapDistance(ro + rd * t);
    res = min(res, 20.0 * h / t);
    t += clamp( h, 0.02, 0.10 );
    if(h < 0.001 || t > tmax) break;
  }
  return clamp(res, 0.0, 1.0);
}

vec3 calcNormal( vec3 pos) {
  vec3 eps = vec3(0.001, 0.0, 0.0);
  vec3 nor = vec3(
      mapDistance(pos + eps.xyy) - mapDistance(pos - eps.xyy),
      mapDistance(pos + eps.yxy) - mapDistance(pos - eps.yxy),
      mapDistance(pos + eps.yyx) - mapDistance(pos - eps.yyx));
  return normalize(nor);
}

float calcAO( vec3 pos,  vec3 nor) {
  float occ = 0.0;
  float sca = 1.0;
  for(int i = 0; i < 5; i++) {
      float hr = 0.01 + 0.03 * float(i);
      vec3 aopos = nor * hr + pos;
      occ += (hr - mapDistance(aopos)) * sca;
      sca *= 0.95;
  }
  return clamp( 1.0 - 3.0 * occ, 0.0, 1.0 );    
}

vec3 render( vec3 ro,  vec3 rd) {
  vec3 col = vec3(0.5, 0.5, 0.5);
  vec4 res = castRay(ro, rd);
  TObj Obj;  
  Obj=mObj;

  if(res.y >= 0.0) {
    vec3 pos = ro + res.x * rd;
    vec3 nor = calcNormal(pos);

    vec3 ref = reflect( rd, nor );
    float occ = calcAO( pos, nor );
    vec3  lig = normalize(vec3(-0.6, 0.7, -0.5));
    float amb = clamp(0.5 + 0.5 * nor.y, 0.0, 1.0);
    float dif = clamp(dot(nor, lig), 0.0, 1.0);
    float bac = clamp(dot(nor, normalize(vec3(-lig.x, 0.0, -lig.z))), 0.0, 1.0 ) * clamp( 1.0 - pos.y, 0.0, 1.0);
    float dom = smoothstep(-0.1, 0.1, ref.y);
    float fre = pow(clamp(1.0 + dot(nor, rd), 0.0, 1.0), 2.0);
    float spe = pow(clamp(dot(ref, lig), 0.0, 1.0),16.0);

    dif *= softshadow(pos, lig);
    dom *= softshadow(pos, ref);

    vec3 colorobj= getSphereColor(int( Obj.tipo));  

    vec3 lin = vec3(0.0);
    lin += 1.20 * dif * colorobj;
    lin += 1.20 * spe * colorobj * dif;
    lin += 0.20 * amb * colorobj* occ;
    lin += 0.30 * dom * colorobj* occ;
    lin += 0.30 * bac * colorobj* occ;
    lin += 0.40 * fre * colorobj* occ;


      col = (res.yzw * lin)/1.5;
  }

  return clamp(col, 0.0, 1.0);
}


mat3 setCamera(  vec3 ro,  vec3 ta, const float cr ){
  vec3 cw = normalize(ta-ro);
  vec3 cp = vec3(sin(cr), cos(cr),0.0);
  vec3 cu = normalize( cross(cw,cp) );
  vec3 cv = normalize( cross(cu,cw) );
  return mat3( cu, cv, cw );
}

