float shape(vec3 p)
{

    vec3 res = vec3(1.0,-1., -1.);
	vec3 q = p;
   	q.y -=5.5;
    q.xz = rotate(q.xz, iTime *.5);
    q.yx = rotate(q.yx, iTime *1.25);
    float rad = 5.0 + .5 * sin(q.z - iTime * 5.)
        * cos(q.x + 2.0 * 1.5) 
        * (sin(q.z * 3.5) + cos(q.y * 1.5) ); // base

     // q.x = mod(q.x + .5, 1.0) - .5;
    float d = length(q) - rad;


    d *= .25;

    res = vec3(d, 1.0, res.z);
