#define maxSteps 2000
#define maxDist 2000.
#define surfDist .00001

//Distance Estimator
float GetDist (vec3 p) {
    vec4 s = vec4(sin(iTime),1,cos(iTime) + 6.,1);
    float sphereDist = length(p-s.xyz) - s.w;
    float planeDist = p.y + sin(iTime*2.) + .3;

    return min(sphereDist, planeDist);
}

//Ray Marcher
float RayMarch (vec3 ro, vec3 rd) {
    float dO = 0.;

    for (int i = 0; i < maxSteps; i++) {
        vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        dO += dS;
        if (dO > maxDist || dS < surfDist) break;
    }

    return dO;
}

