  vec3 col, clCol, mDir, vn;
  vec2 q;
  float a, f, fd, ff, smd, mRad, ts;
  a = atan (rd.z, rd.x);
  if (rd.y < 0.02 * Fbm1 (32. * a) - 0.02) {
    col = vec3 (0.22, 0.3, 0.33) * (0.8 + 0.2 * Fbm2 (64. * vec2 (a, rd.y)));
    if (isDark) col *= 0.5;
  } else {
    if (isDark) {
      mDir = normalize (vec3 (0.6, 0.05, 1.));
      mRad = 0.015;
      col = vec3 (0.06, 0.06, 0.03) * pow (clamp (dot (rd, mDir), 0., 1.), 16.);
      smd = dot (rd, mDir);
      ts = smd * smd - 1. + mRad * mRad;
      if (ts > 0.) {
        ts = smd - sqrt (ts);
        if (ts > 0.) {
          vn = normalize ((ts * rd - mDir) / mRad);
          col += vec3 (1., 0.9, 0.5) * clamp (dot (vec3 (-0.77, -0.4, 0.5), vn) *
             (1. - 0.3 * Noisefv3 (8. * vn)), 0., 1.);
        }
      } else col += StarPat (rd, 6.);
    } else {
      smd = max (dot (rd, sunDir), 0.);
      q = vec2 (0.05 * tCur + 2. * rd.xz / rd.y);
      ff = Fbm2 (q);
      f = smoothstep (0.2, 0.8, ff);
      fd = smoothstep (0.2, 0.8, Fbm2 (q + 0.01 * sunDir.xz)) - f;
      clCol = (0.7 + 0.5 * ff) * (vec3 (0.7) - 0.7 * vec3 (0.3, 0.3, 0.2) * sign (fd) *
         smoothstep (0., 0.05, abs (fd)));
      col = mix (vec3 (0.4, 0.5, 0.8), clCol, 0.1 + 0.9 * f * smoothstep (0.01, 0.1, rd.y));
      col += 0.1 * pow (smd, 16.) + 0.2 * pow (smd, 256.);
    }
  }
  return col;
}

