float WakeFac (vec3 p)
{
  vec2 tw;
  tw = Rot2D (p.xz - sbPos.xz, sbRot);
  tw.y += 6.5;
  return (1. - smoothstep (0.3, 0.6, abs (tw.x) + 0.1 * tw.y)) * clamp (1. - 2. * tw.y, 0., 0.2) *
     smoothstep (-5., -2., tw.y);
}

