float NautDf (vec3 p)
{
  vec3 q, qe;
  float dMin, d, rad, s, dph, suLen;
  dMin = dstFar;
  q = p;
  s = q.z / sbLen;
  rad = 1.2 * (1. - 0.9 * smoothstep (0.4, 1.1, s)) * (1. - 0.85 * smoothstep (0.6, 1.1, - s));
  q.x *= 1.1  - 0.1 * smoothstep (0.7, 0.9, abs (s));
  d = max (mix (max (abs (q.y), dot (abs (q.xy), vec2 (0.866, 0.5))), length (q.xy),
     clamp (s * s, 0., 1.)) - rad, abs (q.z) - sbLen);
  dph = 0.54 - length (q.yz);
  DMIN (idBod);
  q = p;
  d = max (PrRoundBoxDf (q, vec3 (1.2 * rad - 0.05, 0.02, sbLen - 0.02), 0.02), dph);
  DMIN (idPlat);
  s = (q.y > 0.) ? 2. * rad - 0.24 : 1.5 * rad - 0.15;
  d = max (PrRoundBoxDf (q, vec3 (0.03, s, sbLen - 0.02), 0.02),
     - max (PrBox2Df (q.yz, vec2 (s - 0.2, sbLen - 0.1)), - q.y));
  d = max (d, ((q.y > 0.) ? 2. - q.z : 0.4 - abs (q.z - 0.3 * q.y + 1.)));
  DMIN (idKl);
  s += 0.05;
  d = max (PrRoundBoxDf (q, vec3 (0.06, s, sbLen - 0.02), 0.01),
     - PrBox2Df (q.yz, vec2 (s - 0.1, sbLen - 0.1 + 0.1)));
  d = max (max (max (d, abs (mod (q.y + 0.05, 0.1) - 0.05) - 0.03),
     2. - q.z), max (q.y - 2.1, - q.y - 1.65));
  DMIN (idSth);
  q = p;
  q.yz -= vec2 (1.8, -2.8 - 0.5 * (q.y - 1.8));
  d = PrRoundBoxDf (q, vec3 (0.03, 0.35, 0.1), 0.02);
  DMIN (idKl);
  q.z -= 0.1;
  d = max (PrRoundBoxDf (q, vec3 (0.06, 0.3, 0.05), 0.02),
     abs (mod (q.y + 0.05, 0.1) - 0.05) - 0.03);
  DMIN (idSth);
  q = p;
  q.z -= - sbLen + 0.2;
  d = PrRoundBoxDf (q, vec3 (0.03, 1.1 + 0.5 * smoothstep (-1., 0.5, - q.z),
     1.8 - step (q.z, 0.)), 0.02);
  q.z -= -0.35;
  d = max (d, - PrBox2Df (q.yz, vec2 (0.7, 0.15)));
  DMIN (idTl);
  q = p;
  q.z -= sbLen + 1.;
  d = PrCylDf (q, 0.18 * (0.6 - 0.4 * q.z), 1.);
  DMIN (idSpk);
  q = p;
  q.z -= -0.13 - sbLen;
  d = PrCylDf (q, 0.25 * (1. + q.z), 0.13);
  q.xy = Rot2D (q.xy, prpRot);
  s = sign (q.x) * sign (q.y);
  q.xy = Rot2D (abs (q.xy) - 0.25, 0.25 * pi);
  q.xz = Rot2D (q.xz, -0.2 * s * pi);
  d = min (d, PrRoundBoxDf (q, vec3 (0.11, 0.28 * (1. - 4. * q.x * q.x), 0.002), 0.01));
  DMIN (idProp);
  qe = vec3 (abs (p.x), p.yz) - vec3 (0.25, 1.7, 1.95);
  q = p;
  q.yz -= vec2 (1.44, -0.4);
  suLen = 2.7;
  s = q.z / suLen;
  rad = 0.7 * (1. - 0.9 * smoothstep (0., 2., s)) * (1. - 0.5 * smoothstep (0., 2., - s));
  q.x += 0.2 * q.y * sign (q.x);
  d = - PrBox2Df (q.yz - vec2 (1., -2. + 0.2 * q.y), vec2 (1., 3.));
  q.z += 0.2 * q.y * sign (q.z);
  d = max (max (d, PrFlatCylDf (q.zxy, suLen, rad, 0.76)), - PrSphDf (qe, 0.32));
  DMIN (idSup);
  q = p;
  d = PrCylAnDf (q.yzx, 0.53, 0.02, 1.45);
  DMIN (idWfrm);
  q = p;
  q.x = abs (q.x) - 0.85;
  d = max (PrSphDf (q, 0.75), - dph);
  DMIN (idWinS);
  d = PrSphDf (qe, 0.3);
  DMIN (idWinF);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 2.1, suLen - 0.35);
  d = PrSphDf (q, 0.08);
  DMIN (idLmpF);
  q = p;
  q.yz -= vec2 (1.42, suLen - 6.27);
  d = PrSphDf (q, 0.05);
  DMIN (idLmpB);
  return 0.7 * dMin;
}

float ObjDf (vec3 p)
{
  float dMin, d;
  p -= sbPos;
  p.xz = Rot2D (p.xz, sbRot);
  d = PrCylDf (p, 2.3, sbLen + 2.);
  dMin = d;
  if (d < 0.1) dMin = NautDf (p);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 160; j ++) {
    d = ObjDf (ro + dHit * rd);
    if (d < 0.002 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = ObjDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx)));
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

float ObjSShadow (vec3 ro, vec3 rd)
{
  float sh, d, h;
  sh = 1.;
  d = 0.02;
  for (int j = VAR_ZERO; j < 30; j ++) {
    h = ObjDf (ro + rd * d);
    sh = min (sh, smoothstep (0., 0.05 * d, h));
    d += max (0.02, h);
    if (sh < 0.05) break;
  }
  return 0.6 + 0.4 * sh;
}

vec2 LBeamDf (vec3 p)
{
  vec3 q;
  float d, bz;
  p -= sbPos;
  p.xz = Rot2D (p.xz, sbRot);
  q = p;
  q.x = abs (q.x);
  q -= vec3 (0.15, 2.1, 2.35);
  q.yz = Rot2D (q.yz, -0.04 * pi);
  q.xz = Rot2D (q.xz, 0.01 * pi);
  bz = q.z / 20.;
  d = length (q.xy) - 0.08 * (1. + 3. * bz);
  d = 0.9 * max (d, - min (20. * (1. - bz), q.z));
  return vec2 (d, bz);
}

vec2 LBeamRay (vec3 ro, vec3 rd)
{
  vec3 p;
  vec2 d2;
  float dHit;
  dHit = 0.;
  for (int j = VAR_ZERO; j < 80; j ++) {
    d2 = LBeamDf (ro + dHit * rd);
    dHit += d2.x;
    if (d2.x < 0.001 || dHit > dstFar) break;
  }
  return vec2 (dHit, d2.y);
}

vec3 LBeamNf (vec3 p)
{
  vec4 v;
  vec2 e;
  e = vec2 (0.001, -0.001);
  for (int j = VAR_ZERO; j < 4; j ++) {
    v[j] = LBeamDf (p + ((j < 2) ? ((j == 0) ? e.xxx : e.xyy) : ((j == 2) ? e.yxy : e.yyx))).x;
  }
  v.x = - v.x;
  return normalize (2. * v.yzw - dot (v, vec4 (1.)));
}

vec4 NautColN (vec3 p, inout vec3 vn)
{
  vec4 col4, cc, cu, cd, cb;
  float t;
  p -= sbPos;
  p.xz = Rot2D (p.xz, sbRot);
  vn.xz = Rot2D (vn.xz, sbRot);
  cd = vec4 (0.2, 0.2, 0.2, 0.);
  cu = vec4 (0.4, 0.3, 0.2, 0.1);
  cb = vec4 (0.1, 0.4, 0.4, 0.2);
  cc = (p.y > 0.) ? mix (cu, cb, smoothstep (0.45, 0.55, Fbm3 (2. * p))) : cu;
  if (idObj == idBod) {
    col4 = cc * (0.7 + 0.3 * smoothstep (0.05, 0.07, 
       length (mod (vec2 (6. * atan (p.y, - p.x) / pi, 2. * p.z + 0.5), 1.) - 0.5))) *
       (0.7 + 0.3 * max (step (3., abs (p.z + 1.)), smoothstep (0.01, 0.012, abs (p.y + 0.55))));
  } else if (idObj == idPlat) {
    col4 = (abs (vn.y) > 0.2) ? cc : cd;
  } else if (idObj == idWfrm) {
    col4 =  (abs (vn.x) < 0.2) ? cc : cd;
  } else if (idObj == idKl) {
    col4 = (abs (vn.x) > 0.2) ? cu : cd;
  } else if (idObj == idSth) {
    col4 = vec4 (0.4, 0.6, 0.2, 0.2); 
  } else if (idObj == idSup) {
    t = smoothstep (0.05, 0.07, length (mod (vec2 (((abs (p.x) > 0.5) ?
       2. * p.z : 2. * p.x + 0.5), 2. * p.y + 0.5) + 0.5, 1.) - 0.5));
    t = min (t, smoothstep (0.01, 0.012, abs (abs (length (vec2 (p.x, abs (p.z + 1.) - 0.5)) -
       0.18) - 0.12)));
    t = min (t, 1. - step (length (vec2 (p.y - 1.1, abs (abs (p.z + 0.5) - 0.5) - 0.25)), 0.15) *
       smoothstep (0.18, 0.2, abs (mod (24. * p.y + 0.5, 1.) - 0.5)));
    t = min (t, 1. - step (length (vec2 (p.x, p.z - 1.3)), 0.3) *
       smoothstep (0.25, 0.3, abs (mod (12. * p.x + 0.5, 1.) - 0.5)));
    col4 = cc * (0.7 + 0.3 * t);
  } else if (idObj == idTl) {
    col4 = (abs (vn.x) > 0.2) ? cc * (1. - 0.5 * SmoothBump (-0.01, 0.01, 0.003, p.z + 6.28)) : cd;
  } else if (idObj == idProp) {
    col4 = vec4 (0.5, 0.5, 0.3, 0.2) * (0.7 + 0.3 * smoothstep (0.01, 0.012,
       abs (length (p.xy) - 0.6)));
  } else if (idObj == idSpk) {
    col4 = vec4 (0.5, 0.5, 0.5, 0.2) * (0.7 + 0.3 * smoothstep (0.1, 0.15,
       abs (mod (12. * p.z + 0.5, 1.) - 0.5)));
  } else if (idObj == idLmpF) {
    col4 = vec4 (0.8, 0.8, 0.2, -1.);
  } else if (idObj == idLmpB) {
    col4 = vec4 (0.8, 0.2, 0.2, -1.);
  } else if (idObj == idWinF || idObj == idWinS) {
    if (idObj == idWinF) {
      p = vec3 (abs (p.x), p.yz) - vec3 (0.25, 1.7, 1.95);
      p.xy = Rot2D (p.xy, -0.05 * pi);
      p.xz = Rot2D (p.xz, -0.05 * pi);
      t = length (p.yz) - 0.17;
    } else {
      t = length (p.yz) - 0.3;
    }
    if (min (step (0.02, abs (t)), max (step (t, 0.), step (0.02,
       min (abs (p.y + p.z), abs (p.y - p.z))))) == 0.) col4 = cb;
    else col4 = (isDark || unWat || inCave) ? vec4 (0.5, 0.4, 0.3, -1.) : vec4 (0.4, 0.4, 0.5, 0.1);
  }
  if (col4.a >= 0.) {
    vn = VaryNf (32. * p, vn, 0.5);
    if (isDark) col4.rgb *= 0.5;
  }
  vn.xz = Rot2D (vn.xz, - sbRot);
  return col4;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec4 col4, vn4;
  vec3 col, vn, vno, vnb, rob[2], rdb[2], bgCol, ltVec, roo, row, vnw;
  vec2 lbDist, ws;
  float dstObj, dstObjO, dstCave, dstGrnd, dstWat, sh, atten, wkFac, s;
  bool waterRefl;
  dstObj = ObjRay (ro, rd);
#if ALL_PG || INCAVE
  if (inEnv && inCave) {
    dstCave = CaveRay (ro, rd);
    if (dstCave < min (dstObj, dstFar)) {
      dstObj = dstCave;
      idObj = idCav;
    }
  }
#endif
#if ALL_PG || UNWAT  
  if (inEnv && unWat) {
    dstGrnd = (rd.y < 0.) ? GrndRay (ro, rd) : dstFar;
    if (dstGrnd < min (dstObj, dstFar)) {
      dstObj = dstGrnd;
      idObj = idGrnd;
    }
  }
#endif
  sh = 1.;
  atten = 1.;
  if (bmOn) {
    rob[0] = ro;
    rdb[0] = rd;
  }
  if (inEnv && unWat) {
#if ALL_PG || UNWAT  
    if (dstObj < dstFar) {
      if (idObj == idGrnd) {
        ro += dstObj * rd;
        col4 = mix (vec4 (0.4, 0.3, 0.3, 0.), vec4 (0.3, 0.3, 0.2, 0.),
           0.3 + 0.4 * smoothstep (0.4, 0.6, Fbm2 (2. * ro.xz))) * (1. +
           0.2 * smoothstep (0.3, 0.7, Fbm2 (0.5 * ro.xz)));
        vn = GrndNf (ro);
        vno = vec3 (0., 1., 0.);
        if (vn.y > 0.85) {
          vn4 = RippleNorm (0.25 * ro.xz, vn, 8. * (1. - smoothstep (-0.4, -0.2, dot (rd, vn))));
          vn = vn4.xyz;
          col4 *= 0.9 + 0.1 * smoothstep (0.1, 0.3, vn4.w);
        }
        vn = VaryNf (4. * ro, vn, 4. - 3.5 * smoothstep (0.2, 0.5, dstObj / dstFar));
        ws = ro.xz - sbPos.xz;
        ws = Rot2D (ws, sbRot);
        sh = 0.6 + 0.4 * smoothstep (-0.2, 0.8, length (ws -
           vec2 (0., clamp (ws.y, - sbLen, sbLen))) -
           1.2 * (1. - 0.9 * smoothstep (0.4, 1.1, ws.y / sbLen)) *
           (1. - 0.85 * smoothstep (0.6, 1.1, - ws.y / sbLen)));
#if ALL_PG || INCAVE
        if (inCave) sh = min (sh, min (CaveSShadow (ro, vec3 (0., 1., 0.)), 
           0.5 + 0.5 * CaveAO (ro, vec3 (0., 1., 0.))));
#endif
        col = col4.rgb * (0.3 + 0.7 * sh * max (dot (vn, sunDir), 0.));
        col *= mix (vec3 (0.5, 0.5, 1.), vec3 (1.), exp (- dstObj / dstFar));
        col = mix (col, UnwCol (rd) + WatShd (rd), 0.1 + 0.9 * smoothstep (0.3, 0.8, dstObj / dstFar));
      } else if (idObj == idCav) {
#if ALL_PG || INCAVE
        ro += dstObj * rd;
        vn = CaveNf (ro);
        vno = vn;
        col = vec3 (0.4, 0.4, 0.4);
        col *= mix (vec3 (0.5, 0.5, 1.), vec3 (1.), exp (-4. * dstObj / dstFar));
        vn = VaryNf (ro, vn, 12.);
        col *= (0.2 + 0.8 * max (dot (vn, sunDir), 0.) +
           0.5 * pow (max (dot (reflect (sunDir, vn), rd), 0.0), 128.));
        col = mix (col, UnwCol (rd) + WatShd (rd), 0.1 + 0.9 * smoothstep (0.3, 0.8, dstObj / dstFar));
#endif
      } else {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        vno = vn;
        col4 = NautColN (ro, vn);
        sh = ObjSShadow (ro, vec3 (0., 1., 0.));
        if (col4.a >= 0.) {
          col = col4.rgb * (0.5 + 0.5 * sh * max (vn.y, 0.) + 0.2 * UnwCol (rd));
          col *= vec3 (0.7, 0.9, 1.);
        } else {
          col = col4.rgb * (0.5 - 0.5 * dot (vn, rd));
        }
      }
      col += 0.2 * step (0.95, sh) * TurbLt (0.02 * ro, abs (vno), 0.2 * tCur) *
         (1. - smoothstep (0.5, 0.8, dstObj / dstFar)) * smoothstep (-0.2, -0.1, vno.y);
    } else {
      col = UnwCol (rd) + WatShd (rd);
    }
#endif
  } else if (! inEnv || inEnv && ! unWat) {
#if ALL_PG || ! UNWAT
    roo = ro;
    dstObjO = dstObj;
    bgCol = (abs (rd.y) < 0.5) ? vec3 (0.05, 0.05, 0.1) : vec3 (0.01);
    waterRefl = false;
    if (inEnv && inCave) {
#if ALL_PG || INCAVE
      if (rd.y < 0. && ro.y + rd.y * dstObj < 0.) {
        dstWat = - ro.y / rd.y;
        waterRefl = (dstWat < min (dstObj, dstFar) && ro.y + rd.y * dstWat < 0.001);
      } else {
        dstWat = dstFar;
      }
#endif
    } else if (inEnv && ! inCave) {
#if ALL_PG || ! INCAVE
      dstWat = WaveRay (ro, rd);
      waterRefl = (dstWat < min (dstFar, dstObj));
#endif
    }
    if (waterRefl) {
      ro += dstWat * rd;
      row = ro;
#if ALL_PG || INCAVE
      if (inCave) vnw = VaryNf (0.2 * ro, vec3 (0., 1., 0.), 0.1);
#endif
#if ALL_PG || ! INCAVE
      if (! inCave) vnw = WaveNf (ro, dstWat);
#endif
      wkFac = WakeFac (row);
      if (wkFac > 0.) vnw = VaryNf (8. * row, vnw, 16. * wkFac);
      rd = reflect (rd, vnw);
      ro += 0.01 * rd;
      dstObj = ObjRay (ro, rd);
#if ALL_PG || INCAVE
      if (inCave) {
        dstCave = CaveRay (ro, rd);
        if (dstCave < min (dstObj, dstFar)) {
          dstObj = dstCave;
          idObj = idCav;
        }
      }
#endif
      if (bmOn) {
        rob[1] = ro;
        rdb[1] = rd;
      }
    }
    if (inEnv && inCave) {
#if ALL_PG || INCAVE
      if (dstObj < dstFar) {
        ro += dstObj * rd;
        ltVec = roo + ltPos - ro;
        s = length (ltVec);
        atten = (1. - smoothstep (0.1, 0.6, s / dstFar)) / (1. + 0.002 * pow (s, 1.5));
        ltVec /= s;
        if (idObj == idCav) {
          vn = CaveNf (ro);
          col4 = vec4 (mix (vec3 (0.55, 0.5, 0.5), vec3 (0.3, 0.4, 0.2),
             smoothstep (0.5, 0.8, vn.y)), 0.5);
          sh = min (CaveSShadow (ro, ltVec), CaveAO (ro, vn));
          vn = VaryNf (ro, vn, 12.);
        } else {
          vn = ObjNf (ro);
          col4 = NautColN (ro, vn);
          sh = ObjSShadow (ro, ltVec);
        }
        if (col4.a >= 0.) col = col4.rgb * sh * (0.2 + 0.8 * max (dot (vn, ltVec), 0.)) +
           col4.a * step (0.95, sh) * pow (max (dot (reflect (ltVec, vn), rd), 0.), 32.);
        else col = col4.rgb * (0.5 - 0.5 * dot (vn, rd));
        col = mix (col, bgCol, smoothstep (0.45, 0.99, dstObj / dstFar)) * atten;
      } else col = bgCol;
      if (waterRefl) col = mix (vec3 (0.1, 0.2, 0.3) * (0.2 + 0.8 * atten), col, 0.5);
#endif
    } else if (inEnv && ! inCave) {
#if ALL_PG || ! INCAVE
      if (dstObj < min (dstWat, dstFar)) {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        col4 = NautColN (ro, vn);
        sh = isDark ? 1. : ObjSShadow (ro, sunDir);
        if (col4.a >= 0.) {
          col = col4.rgb * (0.3 + 0.2 * max (- dot (vn, sunDir), 0.) +
             0.7 * sh * max (dot (vn, sunDir), 0.)) +
             col4.a * step (0.95, sh) * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
        } else {
          col = col4.rgb * (0.5 - 0.5 * dot (vn, rd));
        }
      } else {
        col = SkyHrzCol (rd);
      }
      if (waterRefl) col *= 0.6;
      if (isDark) col *= 0.8;
#endif
    } else if (! inEnv) {
      if (dstObj < dstFar) {
        ro += dstObj * rd;
        vn = ObjNf (ro);
        col4 = NautColN (ro, vn);
        col4.rgb = vec3 (1.) * pow (Maxv3 (col4.rgb), 0.7);
#if ISBOOK
        col4.rgb *= vec3 (0.8, 0.8, 0.85);
#else
        col4.rgb *= mix (vec3 (0.8, 1., 1.), vec3 (1., 1., 0.8), - dot (vn, rd));
#endif
      if (col4.a < 0.) col4.a = 0.2;
        s = max (dot (vn, sunDir), 0.);
        col = col4.rgb * (0.2 + 0.8 * s * s) +
           2. * col4.a * pow (max (dot (reflect (sunDir, vn), rd), 0.), 32.);
      } else {
        col = vec3 (0., 0., 0.5);
      }
    }
#if ALL_PG || ! UNWAT
    if (inEnv && waterRefl && wkFac > 0.) {
      s = Fbm3 (16. * row);
      if (inCave) col = mix (col, vec3 (0.7), 4. * wkFac * clamp (0.1 + 0.5 * s, 0., 1.));
      else col = mix (col, vec3 (0.9), 8. * wkFac * clamp (0.1 + 0.5 * s, 0., 1.));
    }
#endif
#if ALL_PG || ! UNWAT && ! INCAVE
    if (inEnv && waterRefl && ! inCave) {
      col = mix (col, vec3 (0.7), pow (clamp (2.8 * WaveHt (row.xz) +
         0.5 * Fbm3 (32. * row), 0., 1.), 8.));
    }
#endif
#endif
  }
  if (inEnv && bmOn) {
    lbDist = LBeamRay (rob[0], rdb[0]);
    if (unWat) {
#if ALL_PG || UNWAT
      vnb = LBeamNf (rob[0] + lbDist.x * rdb[0]);
      if (lbDist.x < min (dstObj, dstFar))
         col = mix (col, vec3 (1., 1., 1.), 0.5 * (1. - lbDist.y) * (0.7 - 0.3 * dot (vnb, rd)));
#endif
    }
    if (! unWat) {
#if ALL_PG || ! UNWAT
      if (lbDist.x < min (min (dstObjO, dstWat), dstFar))
         col = mix (col, vec3 (1., 1., 0.7), 0.7 * (1. - lbDist.y));
      if (waterRefl) {
        lbDist = LBeamRay (rob[1], rdb[1]);
        if (lbDist.x < min (min (dstObj, dstWat), dstFar))
           col = mix (col, vec3 (0.8, 1., 0.7), 0.4 * (1. - lbDist.y));
      }
#endif
    }
  }
  return pow (clamp (col, 0., 1.), vec3 (0.9));
}

void ExMain (out vec3 fCol, in vec2 fCoord)
{
  mat3 vuMat;
  vec3 col, ro, rd, fpF, fpB, vd;
  vec2 canvas, uv;
  float el, az, zmFac, t, s, dVu, tVuCyc;
  uv = fCoord;
#if ISBOOK
  tCur += 30. * idPage;
#endif
#if ALL_PG
#if ! ISBOOK
  tVuCyc = 20.;
  idPage = floor (mod (tCur / tVuCyc, 6.)) + 1.;
#endif
  unWat = (idPage == 4. || idPage == 5.);
  inCave = (idPage == 5. || idPage == 6.);
  isDark = (idPage == 3.);
  inEnv = (idPage >= 2.);
#elif ISBOOK && ! ALL_PG
  unWat = false;
  inCave = false;
  isDark = (idPage == 3. || idPage == 5.);
  inEnv = (idPage >= 2.);
#else
  unWat = (UNWAT == 1);
  inCave = (INCAVE == 1);
  isDark = (ISDARK == 1);
  inEnv = (INENV == 1);
#endif
  cvSize = 7.;
  if (inEnv) {
    bmOn = isDark || unWat || inCave;
  } else {
    bmOn = false;
    isDark = false;
  }
  prpRot = 0.5 * 2. * pi * tCur;
  sbLen = 6.;
  t = 3. * tCur;
  az = 0.;
  el = 0.;
  if (inEnv && inCave) {
    fpF = TrackPathCv (t + 0.1);
    fpB = TrackPathCv (t - 0.1);
    sbPos = 0.5 * (fpF + fpB);
    sbPos.y = 0.09;
    vd = fpF - fpB;
    sbRot = atan (vd.x, vd.z);
  } else if (inEnv && ! inCave) {
    sbPos = vec3 (0., 0.09, t);
    if (! unWat) sbPos.y = 0.1 - 0.5 * (1. + sin (0.1 * 2. * pi * tCur));
    sbRot = 0.;
  } else if (! inEnv) {
    sbPos = vec3 (0.);
    sbRot = -0.25 * pi;
  }
  az += (msAz > -999.) ? msAz : 0.;
  el += (msEl > -999.) ? msEl : 0.;
  s = unWat ? 1. : -1.;
  if (inEnv && inCave) {
    dVu = 2. * SmoothBump (0.25, 0.75, 0.15, mod (tCur / 40., 1.)) - 1.;
    t += 20. * dVu;
    fpF = TrackPathCv (t + 0.1 * sign (dVu));
    fpB = TrackPathCv (t - 0.1 * sign (dVu));
    ro = 0.5 * (fpF + fpB);
    ro.x += 2. * s * (1. - abs (dVu));
    ro.y = 2. + 2. * (1. - abs (dVu));
    vd = sbPos - ro;
    vuMat = StdVuMat (el + atan (vd.y, length (vd.xz)), az + atan (vd.x, vd.z));
    dstFar = 50. * cvSize;
    zmFac = 1.4;
  } else if (inEnv && ! inCave) {
    ro.xz = sbPos.xz + 24. * sin (0.01 * s * 2. * pi * tCur + vec2 (0., 0.5 * pi));
    ro.y = unWat ? -2. : 2.;
    vd = sbPos - ro;
    vuMat = StdVuMat (el + atan (vd.y, length (vd.xz)), az + atan (vd.x, vd.z));
    dstFar = 100.;
    zmFac = unWat ? 2. : 4.;
  } else if (! inEnv) {
#if ISBOOK
    ro = vec3 (0., 8., 24.);
    vd = sbPos + vec3 (-1., 0., 0.) - ro;
    vuMat = StdVuMat (atan (vd.y, length (vd.xz)), atan (vd.x, vd.z));
    dstFar = 100.;
    zmFac = 6.;
#else
    az += 0.05 * pi * tCur;
    el -= 0.1 * pi * sin (0.04 * pi * tCur);
    vuMat = StdVuMat (el, az);
    dstFar = 60.;
    zmFac = 5.;
    ro = vuMat * vec3 (0., 0., -24.);
#endif
  }
  ltPos = vuMat * vec3 (0., 1., 1.);
  if (inEnv) {
    sunDir = normalize (vec3 (1., 3., -1.));
  } else {
#if ISBOOK
    sunDir = normalize (vec3 (-0.1, 0.4, 1.));
#else
    sunDir = vuMat * normalize (vec3 (-0.5, 1., -1.));
#endif
  }
  rd = vuMat * normalize (vec3 (uv, zmFac));
  col = ShowScene (ro, rd);
  if (inEnv && unWat) {
#if ISBOOK
    uv *= vec2 (pgSize.z  / pgSize.x, 1.);
#else
    uv *= vec2 (9./16., 1./0.85);
#endif
    col *= 1. - 0.8 * smoothstep (0.9, 1., length (uv));
  }
  fCol = col;
}

