#define bpm 120.
#define speed .25
#define cross_height .1
#define cross_width .4
#define circle_radius .25
#define circle_stroke .2

#define AA 5.

#define pi 3.141592
#define time (speed*(bpm/60.)*iTime)

// https://lospec.com/palette-list/1bit-monitor-glow
vec3 col1 = vec3(.133, .137, .137);
vec3 col2 = vec3(.941, .965, .941);

mat2 r2d(float a) {
    float c = cos(a), s = sin(a);
    return mat2(c, s, -s, c);
}

float fill(float d) {
    return 1. - smoothstep(0., AA / iResolution.x, d);
}

// inspired by Pixel Spirit Deck: https://patriciogonzalezvivo.github.io/PixelSpiritDeck/
// + https://www.shadertoy.com/view/tsSXRz
float stroke(float d, float width) {
	return 1. - smoothstep(0., AA / iResolution.x, abs(d) - width * .5);
}

float bridge(float mask, float sdf, float w) {
    mask *= 1. - stroke(sdf, w * 2.);
    return mask + stroke(sdf, w);
}

float circle(vec2 p, float radius) {
  return length(p) - radius;
}

float rect(vec2 p, vec2 size) {
  vec2 d = abs(p) - size;
  return min(max(d.x, d.y), 0.0) + length(max(d,0.0));
}

float easeInOutQuad(float t) {
    if ((t *= 2.) < 1.) {
        return .5 * t * t;
    } else {
        return -.5 * ((t - 1.) * (t - 3.) - 1.);
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    float t1 = fract(time * .25);// sliding
    float t2 = fract(time);// rotation
    t2 = easeInOutQuad(t2);

    uv *= r2d(pi * .25);
    vec2 uv1 = fract((uv + t1) * 4.) - .5;
    vec2 uv2 = fract(((uv-vec2(t1, 0)) * 4.)+.5) - .5;

    // layer1 - cross
    float mask = fill(rect(uv1 * r2d(t2 * pi), vec2(cross_height, cross_width)));
    mask += fill(rect(uv1 * r2d(t2 * pi), vec2(cross_width, cross_height)));

    // layer2 - circle
    mask = bridge(mask, circle(uv2, circle_radius), circle_stroke) ;

    mask = clamp(mask, 0., 1.);
    vec3 col = mix(col1, col2, mask);

    fragColor = vec4(col, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;

    float w1 = 41.0;
    float w2 = 23.0;
    float speedMultiplier = 18.0f;

    vec3 col = vec3(0.0f,0.0f,0.0f);
	float modular = 10.0f;
    float moveOffset = iTime/speedMultiplier;  

    float w = 100./modular/iResolution.y;
    vec2 aStep =fract(((uv + moveOffset) * 100.) / modular),
         dStep = smoothstep(-w,w,abs(aStep-.5)-.25);

    float dStepXY = dStep.x * dStep.y;

    col = vec3(dStepXY,dStepXY,dStepXY);

    float blockHeight = dStepXY/2.0;

    fragColor = vec4(col, blockHeight);
}

