const float pi = radians(180.);
float smin(float a, float b, float k)
{
    return -log(exp(-k*a)+exp(-k*b))/k;
}
mat3 Tx(float a)
{
    float c=cos(a), s=sin(a);
    return mat3(1,0,0, 0,c,-s, 0,s,c);
}
mat3 Ty(float a)
{
    float c=cos(a), s=sin(a);
    return mat3(c,0,s, 0,1,0, -s,0,c);
}
float sphere(vec3 x, float r)
{
    return length(x) - r;
}
float square(vec3 x, float r)
{
    return length(vec3(max(vec2(0.), abs(x.xz)-r), x.y));
}
float cube(vec3 x, float r)
{
    return length(max(vec3(0.), abs(x)-r));
}
float capsule(vec3 x, vec3 b, float r)
{
    vec3 m = b / 2.;
    vec3 n = normalize(b);
    float l = length(b - m);
    x -= m;
    return length(vec4(cross(x, n), max(0., abs(dot(x, n))-l))) - r;
}
struct Arm
{
    float a, b;
};
struct Body
{
    vec3 x;
    vec4 rippleLeft, rippleRight;
    Arm legLeft, legRight;
    Arm armLeft, armRight;
    float y;
    float headY;
};
float arm(vec3 x, Arm joints)
{
    float r = 1e8;
    x -= vec3(0.1,0,0);
    x = x * Tx(-joints.a);
    r = min(r, capsule(x, vec3(.20, -.20, 0), .1));
    x -= vec3(.20, -.20, 0);
    x = x * Tx(-joints.b);
    r = min(r, capsule(x, vec3(.05, -.35, 0), .1));
    x -= vec3(.05, -.35, 0);
    return r;
}
float leg(vec3 x, Arm joints)
{
    float r = 1e8;
    x = x * Tx(-joints.a);
    r = min(r, capsule(x, vec3(.15, -.45, 0), .1));
    x -= vec3(.15, -.45, 0);
    x = x * Tx(-joints.b);
    r = min(r, capsule(x, vec3(.05, -.45, 0), .1));
    x -= vec3(.05, -.45, 0);
    r = min(r, sphere(x, .1));
    return r;
}
vec3 mirror(vec3 x)
{
	return vec3(-x.x, x.yz);
}
void figure(inout float r, vec3 x, Body joints)
{
    x.y -= joints.y;
    vec3 hip = vec3(0, 1., 0);
    vec3 neck = vec3(0, 1.45, 0);
    vec3 head = vec3(0, 1.80 + joints.headY, 0);
    vec2 arms = vec2(
    	arm(mirror(x - neck), joints.armLeft),
    	arm(x - neck, joints.armRight)
    );
    vec2 legs = vec2(
        leg(mirror(x - hip), joints.legLeft),
    	leg(x - hip, joints.legRight)
	);
    float c = capsule(x - hip, neck-hip, .1);
    float b = smin(min(arms.x, arms.y), c, 15.);
    r = min(r, smin(min(legs.x, legs.y), b, 25.));
    r = min(r, sphere(x-head, .15));
}
float fade(float p)
{
    float c = cos(p * pi);
    return c*c;
}
void walkHeight(out float y, float t0)
{
    	float t = fract(t0);
        float f, w = 1e-10;
        f = fade(t - 0.00); w += f; y += f * +0.2f;
        f = fade(t - 0.20); w += f; y += f * +0.0f;
        f = fade(t - 0.40); w += f; y += f * +0.0f;
        f = fade(t - 0.60); w += f; y += f * +0.0f;
        f = fade(t - 0.80); w += f; y += f * +0.0f;
        y /= w;
}
void walkCycle(out Arm arm, out Arm leg, float phase, float t0)
{
    {
    	float t = fract(t0 + phase);
        float f, w = 1e-10;
        f = fade(t - 0.00); w += f; leg.a += f * +1.5f; leg.b += f * -1.7f;
        f = fade(t - 0.20); w += f; leg.a += f * +0.0f; leg.b += f * -0.0f;
        f = fade(t - 0.40); w += f; leg.a += f * -1.5f; leg.b += f * +0.5f;
        f = fade(t - 0.60); w += f; leg.a += f * +0.0f; leg.b += f * -0.9f;
        f = fade(t - 0.80); w += f; leg.a += f * +0.0f; leg.b += f * -0.0f;
        leg.a /= w;
        leg.b /= w;
    }
    {
    	float t = fract(t0 + phase + .5);
        float f, w = 1e-10;
        f = fade(t - 0.00); w += f; arm.a += f * +1.0f; arm.b += f * +0.5f;
        f = fade(t - 0.20); w += f; arm.a += f * -0.0f; arm.b += f * -0.0f;
        f = fade(t - 0.40); w += f; arm.a += f * -1.0f; arm.b += f * -0.5f;
        f = fade(t - 0.60); w += f; arm.a += f * +0.0f; arm.b += f * -0.0f;
        f = fade(t - 0.80); w += f; arm.a += f * +0.0f; arm.b += f * -0.0f;
        arm.a /= w;
        arm.b /= w;
    }
}
float rounded_square(vec3 x, float r)
{
    vec3 y = vec3(x.x, 0, x.z);
    return length(vec2(x.y, max(0., square(y, r) - .1)));
}
float tile(vec3 x)
{
    float r = 1e8;
    r = min(r, rounded_square(x, .7));
    r = min(r, rounded_square(x - vec3(0,-.05,0), .75));
    return r;
}
float rippleHeight(vec3 x, vec4 ripple)
{
    float l = length(x.xz-ripple.xz);
    float t = ripple.w;
    float source = 1. - exp(-pow(l*10.,2.));
    float front = exp(-pow(l*3.-t*5.,2.));
    float falloff = 1./(1.+l*l) * (1. - smoothstep(.5, 1., t));
	return .02*sin(l*40.) * source * front * falloff;
}
float scene(vec3 x, Body joints)
{
    float r = 1e8;
    float scale = 1.;
    vec3 gx = vec3(x - -joints.x);
    gx.z = mod(gx.z, 2.2);
    for (float i=-1. ; i<=1. ; i+=1.)
    {
        float ripple = 0.;
        ripple += rippleHeight(x, joints.rippleLeft);
    	ripple += rippleHeight(x, joints.rippleRight);
    	r = min(r, tile(gx - vec3(0,ripple,2.2*i)));
    }
    figure(r, x, joints);
    return r / scale;
}
#define saw(x) abs(fract((x)/2.)*2.-1.)
