vec3 getNormal(vec3 p, Body joints){
	vec2 t = vec2(0.0002, 0);
	return normalize(
    	scene(p, joints) - vec3(
        	scene(p - t.xyy, joints),
        	scene(p - t.yxy, joints),
        	scene(p - t.yyx, joints)
        )
    );
}
vec4 crotate(vec4 c, float t)
{
    vec4 c2 = c;
    c2.xyz *= Ty(t);
    return mix(c, c2, .5);
}
void mainImage( out vec4 C, in vec2 o )
{
    vec2 uv = (o-R.xy/2.)/R.y;
    float t = iTime * .1;
    t = -2.0;
    vec3 dx = normalize(vec3(uv,-1)) * Tx(-.35) * Ty(t);
    vec3 x = vec3(-2,3,5) * Ty(t);
    float z = 0.;
    Body joints;
    int i=0;
    {
        float t = iTime;
        float v = 2.2;
        joints.x = vec3(0, 0, -.3-v * t);
        {
            float rt = t + .0;
            joints.rippleLeft = vec4(-0.20, 0, -.3 + v * fract(rt), rt-floor(rt));
        }
        {
            float rt = t + .5;
            joints.rippleRight = vec4(0.20, 0, -.3 + v * fract(rt), rt-floor(rt));
        }
        walkHeight(joints.y, t * 2.);
        walkCycle(joints.armLeft, joints.legLeft, .0f, t);
        walkCycle(joints.armRight, joints.legRight, .5f, t);
        float y1;
        walkHeight(y1, t * 2. - .1);
        joints.headY = (y1 - joints.y) * .5;
    }
    bool found = true;
    for ( ; i<100 ; ++i)
    {
        float r = scene(x+dx*z, joints);
        if (z > 100.)
        {
            found = false;
            break;
        }
        if (r<z*1e-3 || z > 100.)
            break;
        z += r * .5;
    }
    C = pow(texture(iChannel0, (dx * Ty(-.5)).xy).zyxw, vec4(1.5))*.3;
    if (found)
    {
        vec3 p = x+dx*z;
        vec3 normal = getNormal(p, joints);
        //C = vec4(.5+.5*normal, 1.);
        vec4 M = vec4(1);
        if (p.y <= .1)
        {
            vec3 pg = p + joints.x;
            M = crotate(texture(iChannel1, pg.xz), dot(pg.xz, vec2(.5, -.3)));
        }
        if (p.y <= -.025)
            M = vec4(1,1,1, .2);
        M.xyz *= vec3(.05 + .9 * max(0.,normal.y));
        M.xyz *= exp(-.2*(z-6.));
        C = mix(C, M, M.w);
    }
    C = sqrt(C);
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

