#define time .5 * iTime
#define ZPOS -50. + 300. * (floor(time * .5) + smoothstep(.0, .1, fract(time * .5)))

float PI = acos(-1.);

mat2 rot2d(float a) {
  float c = cos(a), s = sin(a);

  return mat2(c, s, -s, c);
}

float sph(vec3 p, float r) {
  return length(p) - r;
}

float tube(vec3 p, vec3 a, vec3 b, float r) {
  vec3 ab = b - a;
  vec3 ap = p - a;
  float t = dot(ab, ap) / dot(ab, ab);
  t = clamp(t, 0., 1.);
  vec3 c = a + ab * t;

  return length(p - c) - r;
}

vec3 rep(vec3 p, vec3 r) {
  vec3 q = mod(p, r) - .5 * r;

  return q;
}

vec3 kifs(vec3 p, float s, float r, float it, float tmin, float tmax, float shift) {
    float t = shift + floor(time * .5) + smoothstep(tmin, tmax, fract(time * .5));

    for (float i = 0.; i < it; i++) {
        p.xy *= rot2d(t * .7);
        p.yz *= rot2d(t * .8 - i);
        p = abs(p);
        p -= s;
        s *= r;
    }

	return p;
}

float at = 0.;
float map(vec3 p) {
    float basez = p.z / 50.;
    p.xy *= rot2d(p.z / 30.);
    p = rep(p, vec3(40.));
	p = kifs(p, 3., .7, 3., .8, 1., basez);
	p = kifs(p, 3., .7, 2., .6, .8, basez);
    float d = 1000.;
  	float obj = 3000.;
    vec3 base = vec3(1, 0, 0);
    float ttube = floor(time * .5) + smoothstep(.2, .4, fract(time * .5));
    float tshift = floor(time * .5) + smoothstep(.4, .6, fract(time * .5));
    float ntube = 6. + 3. * sin(ttube);
    for (float i = 0.; i < ntube; i++) {
        float angle = i * 2. * PI / ntube;
        vec3 shift = vec3(1., 0, 0);
        shift.xy *= rot2d(tshift * PI);
        vec3 a = vec3(0) + shift;
        vec3 b = vec3(2. * cos(angle), 2. * sin(angle), 0);
        obj = min(obj, tube(p, a, b, .3));
    }
    d = min(d, obj);


    at += .1 / (.1 + d);

    return d;
}


vec3 glow = vec3(0, 0, 0);
float rm(vec3 ro, vec3 rd) {
  float d = 0.;

  for (int i = 0; i < 100; i++) {
    vec3 p = ro + d * rd;
    float ds = map(p);

    if (ds < 0.01 || ds > 100.) {
      break;
    }

    d += ds * 1.;
    glow += .02 * at * vec3(0, 1., 0.);
  }

  return d;
}

vec3 normal(vec3 p) {
  vec2 e = vec2(0.01, 0);

  vec3 n = map(p) - vec3(
    map(p - e.xyy),
    map(p - e.yxy),
    map(p - e.yyx)
  );

  return normalize(n);
}

float light(vec3 p) {
  vec3 lp = vec3(0, 0, ZPOS);
  vec3 tl = lp - p;
  vec3 tln = normalize(tl);
  vec3 n = normal(p);
  float dif = dot(n, tln);
  float d = rm(p + .01 * n, tln);

  if (d < length(tl)) {
    dif *= .1;
  }

  return dif;
}


