float rand_func(float x, float seed){ // from 0 to 1
    const float freq = 20.;
    float block = floor(x * freq);
    float A = rand(block + seed);
    float B = rand(block + 1. + seed);
    return mix(A, B, x * freq - block);
}

vec2 rand(vec2 uv, float seed){
    seed += 0.01313;
    uv.x += 0.0004;
    uv.y += 0.0007;
    uv.x = floor(uv.x / 0.001) * 0.001;
    uv.y = floor(uv.y / 0.001) * 0.001;
    uv += vec2(13., 63.);
    uv.x = fract(sin(uv.x * 10. * uv.y * uv.y * seed)  * 438.5453);
    uv.y = fract(cos(uv.x * uv.x * 10. * uv.y * seed)  * 561.5453);
    return uv;
}

vec2 cont_rand(vec2 uv, float seed){ // continueous
    uv += vec2(0.141, 0.123);
    return vec2(rand_func(seed, rand(uv.x * uv.y * uv.x)), rand_func(seed, rand(uv.x * uv.y * uv.y)));
}

