    vec2 A = nwuv + vec2(0., 0.) * clsz;
    A = cont_rand(A, seed);
    vec2 B = nwuv + vec2(1., 0.) * clsz;
    B = cont_rand(B, seed);
    vec2 C = nwuv + vec2(0., 1.) * clsz;
    C = cont_rand(C, seed);
    vec2 D = nwuv + vec2(1., 1.) * clsz;
    D = cont_rand(D, seed);
    vec2 AB = mix(A, B, (uv - nwuv).x / clsz);
    vec2 CD = mix(C, D, (uv - nwuv).x / clsz);
    /*vec2 A = mix(
        rand(vec2(float(X), float(Y))), 
        rand(vec2(float(X + 1), float(Y))), 
        (uv - nwuv).x / clsz
    );*/
    vec2 ans = mix(AB, CD, (uv - nwuv).y / clsz);
    return uv + (ans - vec2(0.5)) * 2. * strength;
}

vec2 distortion(vec2 uv, float strength, int power, float seed){
    for (int i = 0; i < power; i++){
        uv = uvtransform(uv, strength, seed);
    }
    return uv;
}

