    float WIDTH = iResolution.x / 4.;
    for (float i = 0.; i < WIDTH; i++) {
        // radius of a ball
        float r = 10.;
        float hspace = iResolution.x / WIDTH;
        // some dancing, some horizontal space
        float x = iResolution.x / 60.0 * sin(PI / 180. * fFrame) + hspace * i - hspace  * rand1(i);
        float movement = fFrame * iResolution.y / 360.;
        // movement, some vertical space
        float y = movement + iResolution.y * rand1(i);
        	  y = mod(-abs(y), iResolution.y + r);

        vec2 ball = vec2(x, y);
        vec2 ballDist = fragCoord - ball;

        fragColor = mix(fragColor, vec4(hsb2rgb(vec3(0.20, .9, .9)), 1.0),
                        1.-smoothstep(
                            ball.y / 10., ball.y / 2.,
                            dot(ballDist, ballDist)));
    }
}

