// 2D matrix Rotation

mat2 r(float a){return mat2(cos(a),sin(a),
                           -sin(a),cos(a));}
// Using a shadertoy 
// https://www.shadertoy.com/view/XsdyWr
///
mat3 rotationXY( vec2 angle ) {
    vec2 c = cos( angle );
    vec2 s = sin( angle );

    return mat3(
        c.y      ,  0.0, -s.y,
        s.y * s.x,  c.x,  c.y * s.x,
        s.y * c.x, -s.x,  c.y * c.x
    );
} 
float opI(float d1, float d2) {
    return max(d1, d2);
}

float opU(float d1, float d2) {
    return min(d1, d2);
}

float opS(float d1, float d2) {
    return max(-d1, d2);
}

float sdPetal(vec3 p, float s) {
float ratio = 7.;
    p*=ratio+cos(fract(iTime))* sin(fract(iTime)*2.) ;
    s/=1.5;
    p = p * vec3(0.8, 1.5, 0.6) + vec3(0.1, 0.0, 0.0)*ratio;
    vec2 q = vec2(length(p.xz), p.y);

    float lower = length(q) - 1.0;
    lower = opS(length(q) - 0.97, lower);
    lower = opI(lower, q.y);

    float upper = length((q - vec2(s, 0)) * vec2(1, 1)) + 1.0 - s;
    upper = opS(upper, length((q - vec2(s, 0)) * vec2(1, 1)) + 0.97 - s);
    upper = opI(upper, -q.y);
    upper = opI(upper, q.x - 2.0);

    float region = length(p - vec3(1.0, 0.0, 0.0)) - 1.0;

    return opI(opU(upper, lower), region)/16.;
}
