 float ratio = .8;
 p*=ratio;

 float d = 1000., s = 2.0;
    mat3 r = rotationXY(vec2(0.01, PI2 * 0.618034));
    r = r * mat3(1.08,0.,max(0.0,sin(dtime)*.2) ,0.0,0.995,max(0.0,cos(dtime)*.1), 0.0,max(0.0,cos(dtime)*.1),1.08);
    for (int i = 0; i < 24; i++) {
        d = opU(d, sdPetal(p, s));
        p = r * p;
        p += vec3(0.0, -0.001, 0.0);
        s *= 1.05;
    }
    return d/ratio;
}

