// Scene Shape
float moon(vec3 pos) {
pos .xz*=r(-iTime+PI/2.);
float visible = sdCappedCylinder(pos.xyz,vec3(0.,.0,-.1),vec3(.0,.0,.1),.4);
float remove = sdCappedCylinder(pos.xyz-vec3(0.,.2,.0),vec3(0.,.0,-.2),vec3(.0,.0,.2),.5);
    return max(visible,-remove);
    return mix(remove,visible,sin(iTime));
}






float yin(vec3 pos, float p){
       pos.xy *= r(p);


   float q = pos.y >0. ? sdCappedCylinder(pos.xyz-vec3(0.,0.0,.0),vec3(0.,0.0,-.1),vec3(0.0,0.0,.1), 0.6 ) : 1. ;
   float r = sdCappedCylinder(pos.xyz-vec3(0.30,0.0,.0),vec3(0.,0.0,-.1),vec3(0.0,0.0,.1), 0.3 );
   float t = sdCappedCylinder(pos.xyz-vec3(-0.30,0.0,.0),vec3(0.,0.0,-.2),vec3(0.0,0.0,.2), 0.3 );
   float v = sdCappedCylinder(pos.xyz-vec3(0.30,0.0,.0),vec3(0.,0.0,-.2),vec3(0.0,0.0,.2), 0.1 );

   return max(-v,max(-t,min(r,q)));
}
float yinyang(vec3 pos) {
        float ratio = 2.7;
        pos *=ratio;
      pos.xy *= r(PI/2.);
        pos .zy*=r(tan(sin(iTime))*.25);
       pos .xz*=r(tan(cos(iTime*.45))*.25);
     return min(
       yin(pos+vec3(.0,-.05,.0),0.),
       yin(pos+vec3(.0,+.05,.0),PI)
    )/ratio;
}
float flame(vec3 pos) {

     float ratio = 2.5;
    pos.y -=1.6;
    pos*=2.5;
    float q = pos.y <0. ? sdCappedCylinder(pos.xyz-vec3(0.,0.0,.0),vec3(0.,0.0,-.1),vec3(0.0,0.0,.1), 0.6 ) : ratio;
    float f = pos.y >0. ? sdRoundCone(abs(pos)-vec3(0.4+(sin(pos.y*12.+iTime)+1.)*.04,0.,0.),.1,.01,.5)  : ratio ;
    float central_flame = pos.y > 0. ? sdRoundCone(pos-vec3(-0.025+(sin(PI/2.+pos.y*12.+iTime*.7)+1.)*.04,0.,0.),.1,.01,.8) : ratio ;
    return opSmoothUnion(q,opSmoothUnion(central_flame,f,0.5),0.25)/ratio ;
    return min(q,min(central_flame,f))/ratio;

}

float sideBox(vec3 pos) {
  pos.x = abs(pos.x);
  pos = pos-vec3(.6,.0,.0);
  pos.xz *= r(pow(sin(pos.y*2.*PI),2.)+iTime*.1);
 return sdBox(pos,vec3(0.16,0.6,0.1));
}

float backflower( in vec3 p, in float c, in vec3 l )
{
    p*=.5;
      vec3 id = round(p/c);
     p.x += .25+sign(sin(4.*id.z))*sin(iTime*.16);

    p.y -= .25+sign(cos(4.*id.z))*cos(iTime*.23);


    vec3 q = p-c*clamp(round(p/c),-l,l);

    q.zx*=r(iTime*.2+length(p.xz));

    return flower(q);
}

vec2 SDF(vec3 pos) {
     vec3 ppos = pos;
         pos.xz*=r(cos(iTime*.9)*.2);
      pos.yz*=r(cos(iTime*.78)*.1);

    float sideBox =sideBox(pos);

    vec3 pos_tri = pos;
    pos_tri.xy *= r(3.141592);
    pos_tri.xy *= vec2(.5,1.3);

    float triangle = sdTriPrism(pos_tri+vec3(0.,.67,.0),vec2(0.2,0.1));
    float box = sdBox(pos-vec3(.0,.29,.0),vec3(0.35,0.05,0.1));
    float box2 = sdBox(pos-vec3(.0,-.29,.0),vec3(0.35,0.05,0.1));

    float triange2 = sdTriPrism(pos_tri+vec3(0.,-.6,.0),vec2(0.2,0.1));
    float moon = moon(pos-vec3(.0,1.05,.0));

    float yy= yinyang(pos);
    float fl = flame(pos);
    float flow   = flower( (pos.xzy)+vec3(.0,-.01,-1.08));

    float flowrz = backflower(pos.xzy+vec3(0.,-10.,0.),1.,vec3(4.,.1,4.));

   float dist = min(fl,min(yy,min(moon,min(min(box,box2),min(min(triange2,triangle),min(sideBox,flowrz))))));
    dist = min(dist,flow);
   if(dist == flow && pos.y > .75) { // A DIRTY TRICK TO REMOVE BUG
    	return vec2(dist,1.0);
   } else if (dist == flowrz){
   		return vec2(dist,2.0);
   } else {
       	return vec2(dist,0.0);
   }
}

// Main function
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

  // Initialization of the Ray Marching algorithm
  vec3 ray_origin = vec3(.01,.61,-3.); // Were we start 
  vec3 ray_direction = normalize(vec3(uv,1.)); // going forward
  vec3 position = ray_origin ; 

  vec3 color = vec3(.0,.3,.6); // By default, everything will be black

  bool hit = false; 

  float shad = 0.;
  float part = 0.;
  for(float i=0.; i<ITER; ++i) { // We iterate the ray

    vec2 _distance = SDF(position); // Find the closest distance from the current position
    if(_distance.x <0.001) { // If we are close enough, it's a hit, we put the shad and break the loop
        shad = i / ITER;
        hit = true;
        break;
     }
     part = _distance.y;

      float f = position.z > 0.1 ? 1.9:1.; // Speedup for background

     position += _distance.x*f * ray_direction; // Otherwise we conditnue the marching, updating the ray position
  }

  if(hit) { 
     color = vec3(1.-shad*.766); // Yeah no real lighting here, I like the render as it is
    if(part == 0.){
     color = mix(vec3(1.0,0.1,.0), vec3(.9,.9,0.1)+vec3(0.,0.,length(uv)), color)*(1.-shad); 
     } else if(part== 1.) {
        color = mix(vec3(0.0,0.0,0.0), vec3(1.0,0.0,0.4)+vec3(0.,0.,length(uv)), color)*(1.-shad);
        color-=max(0.,sin(iTime*1.3))*vec3(.0,.3,.6);
    } else if(part ==2.) {
    	color = mix(vec3(.0,.3,.6), vec3(.0,.3,.6), color)*(1.75-shad);   

    }

  } 

  fragColor = vec4(color,1.);
}

/*
The idea here is to demonstrate that like in SQL tables are designed in order to
plays nicely with specific queries in GLSL geometry is also designed specifically
to further graphics procedure to use in.

I mean that circle graphics is based on ring geometry not circle geometry.

So, in real life geometry and graphics are tight coupled unfortunately
(https://en.wikipedia.org/wiki/Loose_coupling)
*/

/*support*/

// #define time 0.
#define time iTime
#define RED vec3(1, 0, 0)
#define BLUE vec3(0, 0, 1)
#define BOUNCE1 abs(sin(time * 3.))
#define BOUNCE2 abs(sin(time * 10.))
vec2 normalization(vec2 p) { return (2. * p - iResolution.xy)/ iResolution.y; }

/*objects*/

// below is Circle constructor that defines new circle instance in following steps:
//   origin in Cartesian
//   converts Cartesian to polar 
//   geometry in polar 
//   graphics in polar 
//   coloring 
//   one to one mapping to instance without post-production effects
vec3 Circle(vec2 p, vec3 color) 
{
	vec2 origin = vec2(0);
    float radius = .3;
    float antialiasing = .05 + BOUNCE1 * .4;
    p -= origin;				       // define origin in Cartesian cs	
    float polarCoordinate = length(p); // transform Cartesian cs to polar cs

    // define ring geometry in polar cs by mapping [0..Inf] to [radius..radius * 1.025]
    float ringGeometry = smoothstep(radius, radius * (1. + antialiasing), polarCoordinate); 

    // "graphics query" as I call it that defines circle graphics based on ring geometry
    vec3 smoothCircleGraphics = vec3(1. - ringGeometry);

    vec3 coloring = smoothCircleGraphics * color; 

    vec3 instance = coloring; // no post-production effects
    return instance; // the nice little thing here is that I semantically return new instance of object like it's not glsl function but real constructor in real programming language
}

vec3 Ring(vec2 p, vec3 color) 
{
	vec2 origin = vec2(0);
    float radius = .4 + BOUNCE2 * .02;
    p -= origin;				       
    float polarCoordinate = length(p); 
    float ringGeometryOutside = smoothstep(radius, radius * 1.05, polarCoordinate); 
    float ringGeometryInside = 1. - smoothstep(radius * 0.95, radius, polarCoordinate); 
    float ringGeometry = ringGeometryInside + ringGeometryOutside;
    vec3 smoothCircleGraphics = vec3(1. - ringGeometry);
    vec3 coloring = smoothCircleGraphics * color; 
    vec3 instance = coloring;
    return instance;
}

/*canvas*/

void mainImage(out vec4 canvas, in vec2 pixel)
{

    vec2 p = normalization(pixel); // normalization
    canvas = vec4(0); // init	
    canvas += vec4(Circle(p, RED), 1); // append Circle
    canvas += vec4(Ring(p, BLUE), 1); // append Ring
}

