    float f = 1.0 - min(w, 1.0);

    fragColor = vec4(mix(sky_color, clouds_color, f), 1.0);
}

#define PI  3.1415926538
#define TAU 6.2831853076
#define AA_THRESHOLD 0.1

float sdf_circle(vec2 pos, vec2 circle_pos, float circle_r) {
	return length(pos - circle_pos) - circle_r;
}

float sdf_donut(vec2 pos, vec2 donut_pos, float donut_r, float donut_width) {
	return max(sdf_circle(pos, donut_pos, donut_r + donut_width),
              -sdf_circle(pos, donut_pos, donut_r - donut_width));    
}

float sdf_plane(vec2 pos, vec2 plane_pos, float angle) {
    pos -= plane_pos;
    return (sin(angle) * pos.y - cos(angle) * pos.x);
}

float sdf_arc(vec2 pos, vec2 arc_pos, float r, float width, float angle_a, float angle_b)
{
    float donut1 = sdf_donut(pos, arc_pos, r, 0.14);
    float donut2 = sdf_donut(pos, arc_pos, r * 1.5, 0.15);
    float donut3 = sdf_donut(pos, arc_pos, r * 2.0, 0.16);
    float donut = min(min(donut1, donut2),donut3);
	float corner;

    if (mod(angle_b - angle_a, TAU) < PI) {
        corner = min(sdf_plane(pos, arc_pos, angle_a),
                    -sdf_plane(pos, arc_pos, angle_b));
    } else {
        corner = max(sdf_plane(pos, arc_pos, angle_a),
                    -sdf_plane(pos, arc_pos, angle_b));
    }

    corner += AA_THRESHOLD * (mod(angle_b - angle_a, TAU) / TAU);

    return max(donut, -corner);
}

float map_color(float in_value, float threshold) {
	return clamp (in_value / threshold, 0.0, 1.0);
}

float get_depth(vec2 pos)
{
    pos *= 0.5;
   	vec2 delta = vec2(sin(iTime * 0.4), cos(-iTime*0.4) * 0.4) - pos;
   	vec2 delta2 = vec2(sin(-iTime * 1.5)*0.5, cos(-iTime*0.3)) - pos;
    return 1.0-length(delta);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.yy;
    vec2 orig_uv = uv;

    uv = mod(uv * 10.0, 2.0) - vec2(1.0,1.0);

    vec2 pos1 = vec2(0.0,0.0);


    float _time = mod(iTime, TAU * 2.0);

    float weight = get_depth(orig_uv);

    vec3 xy_0 = vec3(orig_uv, get_depth(orig_uv));
    vec3 xy_1 = vec3(orig_uv + vec2(0.1,0.0), get_depth(orig_uv + vec2(0.1,0.0)));
    vec3 xy_2 = vec3(orig_uv + vec2(0.0,0.1), get_depth(orig_uv + vec2(0.0,0.1)));

    vec3 normal = cross(xy_1 - xy_0, xy_2 - xy_0);

    float angle = atan(-normal.x, -normal.y);
    float weight_normal = weight * 0.5 + 0.5;
    pos1 += vec2(normal.x, normal.y)*5.0;
    float distance = sdf_arc(uv, pos1, 0.15 + 0.35 * weight, 0.6 - weight * 0.3, angle - PI * weight_normal, angle + PI * weight_normal);    

	float shade = map_color(-distance, AA_THRESHOLD);

    fragColor = vec4(shade, shade, 0.0,1.0);
}

